css:
@-moz-document domain("en.wikipedia.org") {
@keyframes open {
	from {
		opacity: 0.0;
	}
	to {
		opacity: 1.0;
	}
}
body {
	animation-name: open;
	animation-duration: 1s;
}
#content {
	width: 42%;
	opacity: 0.95;
	box-shadow: 0px 0px 35px 3px #A3B9CF;
	margin: 0 19%;
	padding: 10px 50px;
	font-size: 20px;
}
/*Hover Effect*/
a:hover,
.mw-body-content:hover a.external:hover {
	text-decoration: none;
	text-shadow: 0 -2px 4px gray, 2px 0 5px red,-2px 0 5px red;
	color:white;
}
a,
.mw-body-content a.external{
	transition:color 0.3s, text-shadow 0.5s;
}
#toc > ul {
	width: 380px;
	background: transparent;
	height: 99%;
	float: right;
	top: 0px;
	left: 75%;
	opacity: 0.5;
	position: fixed;
	transition: opacity 1s;
	overflow: scroll;
}
#toc > ul:hover {
	opacity: 1.0;
}
#toc {
	border-style: none;
	float: right;
	position: relative;
}
.toc a,
li,
.tocnumber {
	font-size: 16px;
}
/*Image Transform*/
/*infobox*/

h1,
h2,
h3,
h4 {
	font-family: "Copperplate Gothic";
	color: rgb(255, 145, 145);
	text-shadow: rgb(3, 3, 3) 2px 2px 3px;
}
.mw-body h1,
.mw-body-content h1,
.mw-body-content h2,
#mw-content-text h3,
h4 {
	font-family: "Estrangelo Edessa";
	transition: color 500ms;
}
.mw-body h1:hover,
.mw-body-content h1:hover,
.mw-body-content h2:hover,
#mw-content-text h3:hover,
h4:hover {
	color: red;
}
#mw-panel,
#mw-navigation {
	display: none;
}
#footer,
#mw-page-base {
	display: none;
}

}
