css:
@-moz-document domain("0chan.hk") {
.block {
    display: inline-block;
    margin: 2px;
    padding: 10px;
    border: solid 1px #CCCCCC;
    border-radius: 2px;
    background: #DDDDDD;
    box-shadow: 0 0px 0px #cccccc;
    white-space: normal;
}

a, span.a {
    color: #FF6600;
}

a:hover, span.a:hover {
    color: #3F51B5;
}

.post .post-header .post-date {
    font-style: italic;
    margin-left: 10px;
    color: #333333;
}

.fa-mail-reply:before, .fa-reply:before {
    content: "\F112";
    color: #9E9E9E;
}

.btn-primary {
    color: #f60;
    background-color: #efefef;
    border-color: #efefef;
}

.sidebar ul.list li a:hover i, .sidebar ul.list li a:hover::before {
    color: #fc6600;
}

.post-button:hover, .post-button.active {
    color: #FF6600;
}

.fa-heart:before {
    content: "\F004";
    color: #ff6600;
}

::-webkit-scrollbar {
    height: 10px;
    width: 10px;
    background: #f5f5f5;
}
 
::-webkit-scrollbar-thumb {
    background: #666666;
    -webkit-border-radius: 5px;
}

.sidebar {
    -webkit-transform: translateZ(0);
    -webkit-backface-visibility: hidden;
    padding: 20px;
    background: #eeeeee;
}

.sidebar .sidemenu-boards-list-box .sidemenu-boards-list .sidemenu-board-item a:hover, .sidebar .sidemenu-boards-list-box .sidemenu-boards-list .sidemenu-board-item a.active {
    background: #efefef;
    border-color: #eeeeee;
}

.sidebar .sidemenu-boards-search input {
    background: #ffffff;
    border-color: #919191;
    border-style: solid;
    color: #333;
    outline: none;
    border-radius: 20px;
    padding: 4px 10px;
}

.sidebar .sidemenu-boards-search input:focus {
    border-color: #ff6600;
    box-shadow: 0 0 3px #ff6600;
}

.sidebar .sidemenu-boards-list-box .sidemenu-boards-list .sidemenu-board-item .sidemenu-board-title {
    color: #ff6600;
}

.sidebar .sidemenu-boards-list-box .sidemenu-boards-list .sidemenu-board-item a:hover .sidemenu-board-title, .sidebar .sidemenu-boards-list-box .sidemenu-boards-list .sidemenu-board-item a.active .sidemenu-board-title {
    color: #3F51B5;
}


a, span.a, a > *, span.a > * {
    text-decoration: none !important;
    cursor: pointer;
    transition: color 0.0s linear !important;
}

.btn-default {
    color: #f60;
    background-color: #eee;
    border-color: #eee;
}

.headmenu {
    background: #efefef;
    border-bottom: #cccccc 1px solid;
    height: 39px;
    padding: 2px 10px;
    overflow: hidden;
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
}

.sidebar .sidemenu-boards-list-box .sidemenu-boards-list .sidemenu-board-item a:hover .sidemenu-board-dir, .sidebar .sidemenu-boards-list-box .sidemenu-boards-list .sidemenu-board-item a.active .sidemenu-board-dir {
          color: #919191;
}

.sidebar ul.list li a:hover span, .sidebar ul.list li a:active span {
    color: #3F51B5;
}

.sidebar a {
    text-decoration: none;
    color: #ff6600;
}

::selection {
    background: #2196F3;
    color: #fefefe;
}

.btn-info {
    color: #ff6600;
    background-color: #efefef;
    border-color: #eeeeee;
}

.reply-form .reply-form-limit-counter {
    color: #919191;
    font-size: 80%;
}

.btn-info:focus, .btn-info.focus {
    color: #ff6600;
    background-color: #efefef;
    border-color: #eeeeee;
}

.btn-primary:focus, .btn-primary.focus {
    color: #ff6600;
    background-color: #efefef;
    border-color: #eeeeee;
}
.btn-primary:hover {
    color: #ff6600;
    background-color: #efefef;
    border-color: #eeeeee;
}
.btn-primary:active, .btn-primary.active,
  .open > .btn-primary.dropdown-toggle {
    color: #fff;
    background-color: #ff6600;
    border-color: #ff6600;
}
.btn-primary:active:hover, .btn-primary:active:focus, .btn-primary:active.focus, .btn-primary.active:hover, .btn-primary.active:focus, .btn-primary.active.focus,
    .open > .btn-primary.dropdown-toggle:hover,
    .open > .btn-primary.dropdown-toggle:focus,
    .open > .btn-primary.dropdown-toggle.focus {
          color: #ff6600;
    background-color: #efefef;
    border-color: #eeeeee;
}

.btn-primary.disabled:hover, .btn-primary.disabled:focus, .btn-primary.disabled.focus, .btn-primary[disabled]:hover, .btn-primary[disabled]:focus, .btn-primary[disabled].focus,
  fieldset[disabled] .btn-primary:hover,
  fieldset[disabled] .btn-primary:focus,
  fieldset[disabled] .btn-primary.focus {
    background-color: #f3904e;
    border-color: #f3904e;
}

.post-highlighted {
  border-color: #ff6600 !important;
  box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075), 0 0 8px rgba(255, 87, 34, 0.49) !important;
}

.btn-info:active:hover, .btn-info:active:focus, .btn-info:active.focus, .btn-info.active:hover, .btn-info.active:focus, .btn-info.active.focus,
    .open > .btn-info.dropdown-toggle:hover,
    .open > .btn-info.dropdown-toggle:focus,
    .open > .btn-info.dropdown-toggle.focus {
    color: #ff6600;
    background-color: #efefef;
    border-color: #eeeeee;
}

.btn-info:hover {
    color: #ff6600;
    background-color: #efefef;
    border-color: #eeeeee;
}

.post .post-footer {
    border-top: 1px solid #eeeeee;
    margin-top: 2px;
    padding: 2px 0;
}


mark, .spoiler {
  color: #bbbbbb;
  background: #bbbbbb;
  padding: 2px;
  transition: none;
}
mark:hover, .spoiler:hover {
    color: inherit;
    background: #bbb;
    transition: none;
}

.fa-star:before {
  content: "\F005";
  color: #FF6600;
}

.btn-default:hover {
    color: #ff6600;
    background-color: #efefef;
    border-color: #eeeeee;
}

.btn-default:active:hover, .btn-default:active:focus, .btn-default:active.focus, .btn-default.active:hover, .btn-default.active:focus, .btn-default.active.focus,
    .open > .btn-default.dropdown-toggle:hover,
    .open > .btn-default.dropdown-toggle:focus,
    .open > .btn-default.dropdown-toggle.focus {
    color: #ff6600;
    background-color: #efefef;
    border-color: #eeeeee;
}

.btn-default:focus, .btn-default.focus {
    color: #ff6600;
    background-color: #efefef;
    border-color: #eeeeee;
}

.btn:focus, .btn:active, .btn:focus:active {
    outline: none;
    color: #ff6600;
}


.btn-primary:active:hover, .btn-primary:active:focus, .btn-primary:active.focus, .btn-primary.active:hover, .btn-primary.active:focus, .btn-primary.active.focus,
    .open > .btn-primary.dropdown-toggle:hover,
    .open > .btn-primary.dropdown-toggle:focus,
    .open > .btn-primary.dropdown-toggle.focus {
    color: #ff6600;
    background-color: #efefef;
    border-color: #eeeeee;
}

.btn-info:active:hover, .btn-info:active:focus, .btn-info:active.focus, .btn-info.active:hover, .btn-info.active:focus, .btn-info.active.focus,
    .open > .btn-info.dropdown-toggle:hover,
    .open > .btn-info.dropdown-toggle:focus,
    .open > .btn-info.dropdown-toggle.focus {
    color: #ff6600;
    background-color: #efefef;
    border-color: #eeeeee;
}

.btn-info:hover {
    color: #ff6600;
    background-color: #efefef;
    border-color: #eeeeee;
}

a:hover, a:focus {
    color: #FF6600;
    text-decoration: underline;
}

.btn-primary.disabled:hover, .btn-primary.disabled:focus, .btn-primary.disabled.focus, .btn-primary[disabled]:hover, .btn-primary[disabled]:focus, .btn-primary[disabled].focus, fieldset[disabled] .btn-primary:hover, fieldset[disabled] .btn-primary:focus, fieldset[disabled] .btn-primary.focus {
    color: #ff6600;
    background-color: #efefef;
    border-color: #eeeeee;
}

.post-img .post-img-labels .post-img-gif-label {
    color: #ff6600;
    border: 1px solid #ff6600;
}


address, .im-address {
    color: #FF6600 !important;
    border: 1px solid #FF6600;
}

.btn-link {
    color: #ff6600;
}

.dialog .message-incoming .message-name {
    color: #ff6600;
}

.im-box .dialog-list .dialog-list-items a.router-link-active {
    border-right: 2px #ff6600 solid;
}




.nav-pills > li.active > a, .nav-pills > li.active > a:hover, .nav-pills > li.active > a:focus {
    color: #ffffff;
    background-color: #ff6600;
}

.form-control:focus {
    border-color: #ff6600;
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075), 0 0 8px rgba(255, 87, 34, 0.49);
}




.im-button:hover {
    background: none !important;
    color: #ff6600 !important;
}

.btn-link:hover, .btn-link:focus {
    color: #ff6600;
}

.__cov-progress {
    background-color: #ff6600 !important;
}




img.post-img-full {
    width: 100% !important;
    height: 100% !important;
}

.btn-group > .btn, .btn-group-vertical > .btn {
    position: static;
    float: left;
}


button.btn.btn-info {
    box-shadow: inset -1px 0px 0px 0px #dddddd;
}

button.btn.btn-default {
    box-shadow: -1px 0px 0 0px #dddddd;
}


button.btn.btn-primary {
    box-shadow: none;
}

.post-img-full {
    position: inherit;
}

.post-img .post-embed .post-embed-play-btn {
    z-index: 0;
}
}
