css:
@-moz-document domain("youtube.com") {

  
#body-container{
  background: transparent url(https://s20.postimg.cc/526qcl3u5/8340842e8113591eeec1aa61ffaebf40.jpg) center top fixed !important;
  background-repeat:no-repeat !important;
  background-size:cover !important;
}
  
  
#body{
  background: transparent url(https://s20.postimg.cc/526qcl3u5/8340842e8113591eeec1aa61ffaebf40.jpg) center top fixed !important;
  backround-repeat:no-repeat !important;
  background-size:cover !important;
}
    

#guide-container {
    background: none !important;
    border:0px !important;
}


#guide.yt-scrollbar{
  border:0px !important;  
  }

#guide{
  border:0px !important;  
  }

.guide-pinned.show-guide .guide-pinning-enabled #page, .guide-pinned.show-guide .guide-pinning-enabled #appbar-content {
    padding-left: 200px !important;

}  
  
#yt-masthead-container{

  background-size: 122px !important;
  background-repeat: repeat-x !important;    
  border:0px !important;
}  
  
#masthead-appbar {
    border: 0px !important;
}  
  
  #appbar-guide-menu{
    border: 0px !important;    
    background: rgba(255,255,255,0.9) !important;
  }
  
  #footer-container{
    background: rgba(255,255,255,0.8) !important;    
  }
  
  
#appbar-nav{

   background-size: 200px !important;
   background-repeat: repeat-x !important;    
   border:0px !important;
  }
  
#appbar-nav.appbar-content-hidable > ul.appbar-nav-menu{
  padding-left: 15px !important;
  padding-right: 15px !important;
  background: rgba(255,255,255,0.9) !important;
  
  }  
  
.guide-pinned.show-guide .guide-pinning-enabled #page{

  background-size: cover !important;
  background-repeat: no-repeat !important;
  border:0px !important;  
  }
  
#browse-items-primary .item-section > li > .yt-lockup-tile, .branded-page-v2-primary-col .branded-page-box, .section-list li .item-section .branded-page-box {
  background: transparent !important;

  }

.individual-feed{
    position: relative;
    background: transparent !important;
}  

  
.yt-card {
    margin: 0 0 10px;
    border: 0;
    background: rgba(255,255,255,0.8);
  }
  
#body-container > div#masthead-positioner > div{
  background: rgba(0,0,0,0.3) !important;
  
  }  
  
  #page-container{
    background:transparent !important;
  }
  
#page{
    background:transparent !important;
  }  

  .yt-ui-ellipsis{
    background:transparent !important;
  }
  
  
.yt-uix-button-icon-material-upload {
    filter: invert(100) !important;
    opacity:1 !important;
}  
  
#yt-masthead-user #yt-masthead-notifications-button {
    filter: invert(100) !important;
}  
  
yt-uix-button-default::before, .yt-uix-button-default .yt-uix-button-icon {
    opacity: 1 !important;
    filter: alpha(opacity=100) !important;
}
  
.yt-uix-button {
    opacity: 1 !important;
}
  
  
#masthead-positioner > div#masthead-appbar-container.clearfix > div#masthead-appbar{
    background: transparent !important;  
  }  
  
#body-container > div#page-container > div#page.home.clearfix > div#content{
  float:left !important;
  margin-left:3.5% !important;
  width:75% !important;
  }  
  

.home > div#content{
  float:left !important;
  margin-left:3.5% !important;
  width:75% !important;  
  }  
  

  #footer-container{
    display:none !important;
  }

  #appbar-guide-menu{
    margin-top:100px !important;
  }
  

#page.search > div#guide.yt-scrollbar > div#appbar-guide-menu{
    margin-top:60px !important;
  }
  
  a:link{
    color:#005d66 !important;
  }  
  
#appbar-nav.appbar-content-hidable > ul.appbar-nav-menu{
    float:left !important; 
    margin-left:30px !important;
  }  
  
.guide-item.guide-item-selected, .guide-item.guide-item-selected:hover, .guide-item.guide-item-selected .yt-deemphasized-text, .guide-item.guide-item-selected:hover .yt-deemphasized-text {
    background: #017783;
    color: #fff !important;
}  
  
  
#yt-masthead #logo-container .logo {
    filter: invert(1) !important;
}
  
  
  
  
  
  
  
}
