}



/*** 包含这首歌的歌单 End*********************************/


  /*<li>NEW 网易云音乐 - 显示更多的歌单文字 2k170404</li>*/
  .f-thide {
    overflow: hidden;
    text-overflow: ellipsis;
    word-wrap: normal;
    white-space: normal;
    font-size: 12px;
}

  /*<li><blockquote>网易云音乐 - 加长播放列表</blockquote></li>*/
  .m-playbar .list {
    position: absolute;
    left: 50%;
    bottom: 47px;
    _bottom: 46px;
    width: 986px;
    _width: 982px;
    margin-left: -493px;
    _marign-left: -491px;
    height: 80vh;  /*this*/
}

/* mp3 list title*/
.m-playbar .listhd {
    background-position: 0 0;
    height: 41px;
    margin-top: -41px;
}


/*mp3 list cont*/
.m-playbar .listbd {
    position: absolute;
    left: 0;
    top: 41px;
    width: 976px;
    _width: 972px;
    overflow: hidden;
    background-position: -1014px 0;
    _background-position: -1018px 0;
    background-repeat: repeat-y;
    height: 100%;
    margin-top: -41px;
}
.m-playbar .listbdc {
    position: absolute;
    left: 2px;
    _left: 0;
    top: 0;
    z-index: 4;
    height: 260px;
    width: 553px;
    overflow: hidden;
    height: 100%;
}


/*select bar */
.m-playbar .listbd .bline {
    position: absolute;
    left: 555px;
    _left: 553px;
    top: -1px;
    z-index: 2;
    width: 6px;
    height: 260px;
    background: #000;
    opacity: .5;
    filter: Alpha(opacity=50);
    height: 100%;
}


/* lrc*/
.m-playbar .listlyric {
    position: absolute;
    right: 40px;
    top: 0;
    z-index: 4;
    margin: 21px 0 20px 0;
    height: 219px;
    width: 354px;
    overflow: hidden;
    height: 100%;
}


  /*<li><blockquote>网易云音乐 - 去TMD封面圆圈</blockquote></li>*/
  .u-cover-6 .msk {
    width: 206px;
    height: 205px;
    top: -1000px;
    left: 0;
    background-position: 0 0;
    overflow: hidden;
}
.msk{
  background:url(http://none.jpg) !important;   /*this*/
}

.u-cover-6 img {
    width: 100%;
    height: 100%;
    margin: 0;
}

  
  /*<li><blockquote>网易云音乐 - 更多的搜藏空间</blockquote></li>*/
  /* bg */
div.m-layer-w2 {
    width: 800px;
    height: 100%;
    margin: 115px -37px;
}

/* cont*/
div.m-layer div.zcnt {
    padding: 0;
    border: 1px solid #878787;
    border-width: 0 1px 1px;
    border-radius: 0 0 4px 4px;
    background: #fff;
    height: 100%;
}

/* cont size*/
div.m-layer .lyct-1 {
    padding: 0;
    height: 100%;
}

/* cont cont*/
.m-favgd ul li {
    padding: 6px 0 6px 35px;
    border-top: 1px solid #e0e0e0;
    float: left;
    width: 28%;
    height: 55px;
}

/* 搜藏 x */
div.m-layer .zcls {
    position: absolute;
    z-index: 20;
    top: 13px;
    right: 13px;
    width: 19px;
    height: 16px;
    overflow: hidden;
    text-indent: -9999px;
    cursor: pointer;
    background-position: 0 -95px;
    background-color: #FFF;
}  
  
  
  
  
  
  
}
