css:
@-moz-document domain("waffle.io") {
/*!
 * Waffle Darkness v0.1.0 (https://github.com/AmirSavand/waffle-darkness)
 * Amir Mehdi Savand
 * Licensed under MIT (https://github.com/AmirSavand/waffle-darkness/blob/master/LICENSE)
 */
.column-ct,
.sidebar.navbar .header-nav,
.column-card-count-total,
.sidebar.navbar .nav-list-item .nav-link.active,
.sidebar-toggle-icon,
.sidebar.navbar .nav-list-item .navbar-brand {
    background: #333 !important;
    color: #aaa !important;
}

.column,
.sidebar.navbar .header-nav,
.sidebar.navbar .nav-list-item .navbar-brand,
.search-query,
.header-action-toggle {
    border-color: #292929 !important;
    border-width: 2px !important;
}

.sidebar.navbar .sidebar-bottom-nav img,
.header-action-toggle .button {
    border-color: #222 !important;
}

.display-name,
.column-card-count-total {
    text-transform: uppercase;
    font-weight: bold;
    font-size: smaller;
}

.column-description,
.header-action-toggle .responsive-text {
    display: none !important;
}

.column-toggle {
    opacity: 0.5;
}

.sidebar.navbar,
.sidebar-toggle,
.header-action-toggle .button,
.as-sortable-placeholder,
.card-pr-list,
.search-query,
.header-action-toggle{
    background: #292929 !important;
}

.card-body,
.card-header,
.card .title {
    background: #222 !important;
}

.sidebar.navbar .nav-list-item .nav-link,
.card-number {
    color: #666 !important;
}

.card .title,
.search-query,
.header-action-toggle {
    color: #aaa !important;
}

.header-action-toggle .fa {
    padding-right: 1px !important;
}

.project-dropdown,
.sidebar.navbar .header-nav .account-type-icon,
.search-query,
.header-action-toggle{
    border: none !important;
}

.card-header,
.card-pr-list,
.card-body {
    border-radius: 2px !important;
}

.search-query,
.header-action-toggle {
    border-radius: 0 !important;
}

.card-pr-list {
    margin-top: -2px;
}

.label-pill {
    color: #000;
}
}
