css:
/* Spiegel-Online Headlines according ressort for firefox sidebar */
	@-moz-document url-prefix(http://www.spiegel.de/schlagzeilen/ressort/index.html){
		/* preparing style sheet for sidebar */
			*{
				max-width: 50ex !important;
                                line-height: 1.3em !important;
				background: rgb(255, 255, 244) !important;
				}
			body{
				font-size: 0.8em !important;
                                }
                        a{
				border-bottom-style: none !important;
				text-decoration: none !important;
                                font-size:1.2em !important;
                                margin-bottom: 10px !important;                                
                                }
			.spDottedLine{
                                margin-bottom: 5px !important;
                                font-size: 1.4em !important;
                                text-align:left !important;
                                }
                        .blk{   
                                display:block !important;
                                font-size: 0.9em !important;
                                }
		/* removing unneccessary stuff */
			*{
				border: none !important; 
				position: static !important;
				}
			#spBreadcrumbLine,
                        #spFooter,
                        #spHeader,
			#spSchlagzNavi,
			img[src=""]{
				display: none !important;
				}
           }
