css:
@-moz-document regexp("https?://(www\\.)?speedrun\\.com/.*") {
/* better category tabs */
    .category.category-tab {
        display: inline !important;
        flex: auto;
        text-align: center;
        margin: 1px;
    }
    .pending-tab-container { display: none !important; }
/* hides comments and ads sections */  
    [component-name="CommentsWidget"], .malediction { display: none !important; }
/* make text more readable */  
    main { min-width: 75% !important; }
    * { font-size: 11pt }
}
