css:
@media only screen and (min-width: 1200px) {
    #main, #topbar, .fullw {
        width: 1168px;
        max-width: 1168px;
    }
}
@media only screen and (min-width: 768px) {
    .splash>a {
        transition: background-color ease-in-out 0.25s;
    }
    .splash {
        transition: background-size ease-in-out 2s;
    }
    .splash:hover {}
    .wpx {
        box-shadow: 0 0 6px #111315;
    }
    #splash-3 {
        height: 455px;
        margin-bottom: 1px;
    }
    .featured-entry a.title {
        color: #c5c5c5;
        font-size: 14px;
    }
    .featured-entry a.title:hover {
        color: #eee;
    }
    .splash-1 {
        height: 275px;
    }
    .splash-2 {
        height: 180px;
        border-color: #1c1f22;
    }
    .splash-2:first-child {
        border-left-width: 0;
    }
    .b-side {
        padding: 10px 20px;
        margin-bottom: 25px;
        background: #3a4952;
        box-shadow: 0 0 6px #111315;
    }
    .c-main {
        margin: 0 -350px 0 0;
        padding: 0 370px 0 10px;
    }
    .c-side {
        padding: 0 10px 0 0;
        margin-top: 0;
        width: 340px;
    }
    ._p-mp {
        margin-bottom: 0;
    }
    .b-side h3 {
        padding: 8px 0;
        font-size: 18px;
    }
    .news-item {
        background: #39444b;
    }
    .news-item:hover {
        background: #3d4950;
    }
    .b-side h3 {
        border-top: none;
    }
    .event-desc a {
        color: #e2e2e2;
    }
    .event-desc span {
        color: #bac1c5;
    }
    .news-media img {
        border: solid 1px #1c1f22;
    }
    .news-media div {
        padding: 2px 6px;
        bottom: 10px;
        right: 10px;
        border-radius: 3px;
        font-size: 20px;
    }
    .news-media div:before {
        position: absolute;
        bottom: -6px;
        left: 3px;
        border-width: 6px 6px 0 0;
        border-style: solid;
        border-color: #DEDEDE transparent;
        display: block;
        width: 0;
    }
    /* Hilos */
    
    .post .buttons {
        opacity: 0.4;
    }
    a.qn,
    span.rd {
        float: left;
        margin-right: 2px;
    }
    span.rd {
        margin-right: 6px;
        color: #708da4;
    }
    #thread-companion .btn {
        display: block;
    }
    #side-nav a,
    #side-nav span.current {
        width: 34px;
        height: 34px;
        line-height: 34px;
        display: block;
        text-align: center;
        margin: 0px 0px 2px 2px;
        font-size: 14px;
        font-weight: 400;
        color: #858e93;
        border-radius: 2px 0 0px 2px;
        background: #272b2f;
    }
    #side-nav .fid {
        opacity: 1;
    }
    #side-nav span.current {
        margin: 0 0 1px 1px;
        border-top: none;
        background: #39444b;
    }
    #side-nav a:hover {
        background-color: #393d40;
    }
    .scrollPane {
        opacity: 1 !important;
        background: #39444b;
        margin: 0 4px;
    }
    .forums .icon-col {
        background-color: #58646d;
        padding: 25px;
    }
    .forums .forum {
        border-radius: 4px;
        min-height: 80px;
    }
    .forums .info-col {
        margin-left: 80px;
    }
    .forums .forum:hover .icon-col {
        background-color: #3e474e;
    }
    .forums .forum p {
        color: #a2b2bb;
    }
    #temas .thread .fa.fa-thumb-tack {
        margin: 0 5px;
        font-size: 20px;
        float: right;
    }
    .fa-thumb-tack:before {
        content: "ï‚";
        transform: rotateZ(45deg);
        display: block;
    }
    .cat-badge i {
        opacity: 1;
    }
    .cat-badge:hover span {
        color: #aabfce;
    }
    .cat-badge {
        color: #9ea9af;
    }
    .btn-circle > i {
        min-width: 24px;
        margin: 0px 0 20px 0;
        font-size: 36px;
    }
    .post-meta a.qn:hover,
    .post-meta-reply a.qn:hover {
        color: #1c1f22;
    }
    .replies .rep {
        border-left: 8px solid #1a1f22;
        margin-left: -8px;
    }
    .post blockquote.quote {
        border-left: 8px solid #1a1f22 !important;
        border-top: solid 1px #1a1f22;
        border-right: solid 1px #1a1f22;
        border-bottom: solid 1px #1a1f22;
    }
    #post-editor {
        background-color: rgba(97, 106, 112, 0.85);
    }
    .post-body {
        margin-left: 90px;
    }
}
