css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.sjgames.com") {
	
	/*Navigation*/
	[name=top] + table > tbody > tr > td{
  	background: url(http://i67.tinypic.com/8vq2v4.png) no-repeat !important;
    background-position: center !important; 		
	}	
	[title~=Navigation] {
		opacity: 0;
  }
	
	/*Page Background*/
	body {
		background: #000 !important;
	}
	.page {
		background-image: none;
		background-color: transparent;
	}
  
	/*Dark*/
	.alt1, .alt1Active, .tborder {
    background: #1a1812 !important;
  }
	
	/*Medium*/
	.alt2, .alt2Active, .panelsurround, .vbmenu_option_alink {
    background: #332f24 !important;
  }
	
	/*Light*/
	.thead, .tfoot, .fjsel, legend, .vbmenu_control, .vbmenu_hilite, .tcat, .panel {
	  background: #4d4736 !important;
  }
	
	/*Borders*/
	.tborder {
		border: 1px solid #737067 !important;
	}


	/*Regular text*/
	body, .page, .alt1, .alt1Active, .alt2, .alt2Active, .thead, .tfoot, .fjsel, legend, .vbmenu_control, .vbmenu_hilite, .tcat, .panel, .panelsurround {
		color: #737067 !important;
	}
	
	/*Timestamps*/
	.time {
		color: #a6a295 !important;
	}
	
	/*Url Text*/
	a:link, body_alink {
    color: #fff !important;
  }
	a:visited, body_avisited
  {
	  color: #fff !important;
  }
  a:hover, a:active, body_ahover
  {
	  color: #d1a52a !important;
  }
}
