css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.sjgames.com") {
	
	/*Navigation*/
	[name=top] + table > tbody > tr > td{
  	background: url(http://i64.tinypic.com/33c2qa8.png) no-repeat !important;
    background-position: center !important; 		
	}	
	[title~=Navigation] {
		opacity: 0;
  }
	
	/*buttons*/
	[title='Reply'] {
		opacity: 0.5 !important;
	}
	[title='Reply With Quote'] {
		opacity: 0.5 !important;
	}
	[title='Edit/Delete Message'] {
		opacity: 0.5 !important;
	}
	
	/*Page Background*/
	body {
		background-color: 000 !important;
		background-image: url('http://i65.tinypic.com/iqh7rm.jpg') !important;
		background-attachment: fixed !important;
	}
	.page {
		background-image: none;
		background-color: transparent;
	}
  
	/*Dark*/
	.alt1, .alt1Active, .tborder {
    background-color: rgba(0,0,0,0.30) !important;
		background-image: none !important;
	}
	
	/*Dark Solid*/
	.vbmenu_popup {
		background-color: 000 !important;
		background-image: url('http://i65.tinypic.com/iqh7rm.jpg') !important;
		background-attachment: fixed !important;
	}
	
	/*Medium*/
	.alt2, .alt2Active, .panelsurround, .vbmenu_option_alink {
    background: rgba(18,24,26,0.30) !important;
  }
	
	/*Light*/
	.thead, .tfoot, .fjsel, legend, .vbmenu_control, .vbmenu_hilite, .tcat, .panel, .vbmenu_hilight_alink {
	  background: rgba(54,72,77,0.30) !important;
  }
	
	/*.vbmenu_popup vbmenu_option vbmenu_option_alink vbmenu_hilight_alink*/
	
	/*Borders*/
	.tborder {
		border: 1px solid #000 !important;
	}


	/*Regular text*/
	body, .page, .alt1, .alt1Active, .alt2, .alt2Active, .thead, .tfoot, .fjsel, legend, .vbmenu_control, .vbmenu_hilite, .tcat, .panel, .panelsurround {
		color: #737067 !important;
	}
	
	/*Timestamps*/
	.time {
		color: #a6a295 !important;
	}
	
	/*Url Text*/
	a:link, body_alink {
    color: #fff !important;
  }
	a:visited, body_avisited
  {
	  color: #fff !important;
  }
  a:hover, a:active, body_ahover
  {
	  color: #4e89e8 !important;
  }
}
