css:
@-moz-document domain("stocktwits.com") {
body:not(.settings) {
	background: #2f2f40!important;
}
#top-gray {
	background: none;
	box-shadow: none;
	border: none;
}
.title-link,
#home-tab {
	color: #54c7b0!important;
	text-shadow: none;
}
.mb3-avatar {
	height: 50px;
}
.mb3-avatar img,
.avatar img {
	border-radius: 50%;
}
.mb3-carat {
	display: none;
}
.mb3-container {
	background: #292938;
	box-shadow: none;
	border: 1px solid #292938;
	border-radius: 10px;
	padding: 5px;
}
.mb3-container:hover,
.mb3-container:focus,
.mb3-container:active {
	border: 1px solid #54c7b0;
}
textarea.message-text {
	color: #80849e!important;
}
.mb3-container:hover textarea.message-text,
.mb3-container:focus textarea.message-text,
.mb3-container:active textarea.message-text,
textarea.message-text:active,
textarea.message-text:focus,
textarea.message-text:hover {
	color: #fff!important;
}
.mb3-bottom {
	background: none;
}
.mb3-tw,
.mb3-fb,
.mb3-li {
	display: none;
}
.sentiment-selector {
	background-color: transparent;
	left: 8px;
	bottom: 0px;
	box-shadow: none;
}
.sentiment-selector div {
	box-shadow: none;
	border-radius: 25px;
	background: none;
}
.sentiment-selector span {
	border-radius: 50%;
	height: 15px;
	width: 15px;
	top: 3px;
	left: 2px;
	background: #80849e;
	border: none;
	box-shadow: none;
}
.sentiment-selector span:before {
	background: none!important;
}
.sentiment-selector label {
	text-shadow: none!important;
}
.sentiment-display-2 label {
	color: #80849e;
}
.sentiment-display-3 label {
	color: #d5464a;
}
.sentiment-display-1 label {
	color: #c7546b;
}
.sentiment-selector:hover .sentiment-display-1 label,
.sentiment-selector:hover .sentiment-display-2 label,
.sentiment-selector:hover .sentiment-display-3 label {
	color: white;
}
.mb3-counter {
	text-shadow: none;
	color: #80849e;
}
.mb3-counter-neg {
	color: #96171d;
}
.chart-container .mb3-btn {
	opacity: 0.5;
}
.mb3-button {
	box-shadow: none;
	text-shadow: none;
	background: #292938;
	border: 1px solid #292938;
	color: #54c7b0;
	border-radius: 10px;
	text-transform: uppercase;
	font-size: 1.1em!important;
}
.mb3-button:hover {
	background: #292938;
	border: 1px solid #54c7b0;
	color: #54c7b0;
}
.mb3-button:active,
.mb3-button:focus {
	background: #54c7b0;
	border: 1px solid #54c7b0;
	color: #292938;
}
.ticker-header.negative {
	border-left-color: #d5464a!important;
	border-top-left-radius: 7px;
	border-bottom-left-radius: 7px;
}
.ticker-header.positive {
	border-left-color: #68b04d!important;
	border-top-left-radius: 7px;
	border-bottom-left-radius: 7px;
}
#top-content h1,
.ticker-header h1,
.ticker-header .exchange,
.ticker-header .last-updated {
	color: #80849e!important;
	text-shadow: none!important;
}
.ticker-header .symbol-box,
.ticker-header .price {
	color: white!important;
}
.change.negative,
.change.negative * {
	color: #d5464a!important;
}
.ticker-header .change-image.negative {
	border-top-color: #d5464a;
}
.change.positive,
.change.positive * {
	color: #68b04d!important;
}
.ticker-header .change-image.positive {
	border-top-color: #68b04d;
}
.gray-bar {
	background: none;
	border: none;
	box-shadow: none;
}
.toggle {
	color: #80849e!important;
	background: none!important;
	box-shadow: none!important;
	border: none!important;
}
.toggle.active {
	color: #54c7b0!important;
	background: none!important;
	box-shadow: none!important;
	border: none!important;
}
.toggle:hover {
	color: white!important;
	background: none!important;
	box-shadow: none!important;
	border: none!important;
}
.puaseUpdates,
.pauseUpdates * {
	border: none!important;
	box-shadow: none!important;
	color: #80849e!important;
}
a:hover {
	text-decoration: none!important;
	color: white!important;
}
.pauseMsg {
	background: none;
	text-shadow: none;
	color: #80849e;
	border: none;
	box-shadow: none;
}
.pauseMsg a {
	color: #54c7b0;
}
.stream-list {
	background: transparent;
}
.messageli {
	padding: 10px!important;
	border-radius: 15px;
}
.messageli .message-header,
.username,
.message-date a {
	color: #80849e!important;
}
.messageli .message-body {
	color: white;
}
.messageli .message-body a {
	color: #54c7b0;
}
.new-icon-comments {
	display: none;
}
.conversation-bubble {
	width: 20px;
	height: 20px;
	display: flex;
	align-items: center;
	justify-content: center;
	background: #54c7b0;
	border-radius: 50%;
}
.conversation-bubble:hover {
	background: white;
	cursor: pointer;
}
.convo-contain {
	font-weight: bold;
	font-size: .8em;
	color: #292938!important;
}
.messageli .chart-icon {
	box-sizing: border-box;
	border: 3px solid transparent;
	border-radius: 15px;
}
.messageli .chart-icon:hover {
	border-color: #54c7b0;
}
.messageli .chart-icon:hover img {
	opacity: 0.5;
}
.messageli .avatar {
	border-radius: 50%;
	border: 3px solid transparent;
}
.messageli .avatar:hover {
	border-color: #54c7b0;
}
.stream-list > li:hover {
	background: #292938;
	cursor: pointer;
}
.stream-list > li {
	border: none!important;
}
a.ticker {
	color: #54c7b0;
}
.message-tool {
	opacity: 0.2;
}
.message-tool:hover {
	opacity: 1;
}
.message-source {
	display: none;
}
.sentiment {
	box-shadow: none;
	background: none!important;
	text-transform: uppercase;
	font-size: 10px;
	margin-top: 1px;
	position: relative;
}
.sentiment.bullish,
.sentiment-display-3 label {
	color: #68b04d;
	border-color: #68b04d;
}
.sentiment.bearish,
.sentiment-display-1 label {
	color: #d5464a;
	border-color: #d5464a;
}
.stream-tabs li {
	border: none!important;
}
.more-streams {
	color: #80849e;
}
.single-msg {
	background: #2f2f40!important;
	border: none!important;
	border-radius: 15px;
}
.single-msg .stream-content {
	border-radius: 15px;
}
.single-msg .single-message {
	background: #292938!important;
}
.single-msg .context,
.single-msg .companies-mentioned,
.single-msg .companies-mentioned th {
	color: #80849e;
	border-color: #80849e;
}
.show-likes li a img {
	border-radius: 50%;
	border: 3px solid transparent;
}
.show-likes li a img:hover {
	border-color: #54c7b0;
}
.show-likes-total span {
	color: #54c7b0;
	border: none;
}
.show-likes-total span small {
	color: #80849e;
}
.share-options {
	display: none;
}
#single-page-modal #msg-contain {
	margin: 0 auto;
	margin-top: 3%;
}
.reshare-message {
	border-color: #54c7b0;
}
#back-to-top {
	border: 1px solid #54c7b0;
	background: #2f2f40;
	border-top-left-radius: 5px;
	border-top-right-radius: 5px;
	border-bottom: none;
	padding: 0 5px;
}
#back-to-top * {
	background: none!important;
	border: none!important;
	padding: 5px!important;
	color: #54c7b0;
	text-transform: uppercase;
}
#back-to-top *:hover {
	background: none;
}
.loading-box,
.loading-box * {
	background: none!important;
	border: none!important;
}
#global-header,
#global-header.logged-in,
#global-header.logged-out {
	background: #1e1e2a!important;
}
#logo,
#logo.logged-in,
#logo.logged-out {
	background: url(https://static.wixstatic.com/media/3af762_e1800d803e424828a17ff1c1cf6d45a3~mv2.png/v1/fill/w_160,h_96,al_c,usm_0.66_1.00_0.01/3af762_e1800d803e424828a17ff1c1cf6d45a3~mv2.png)no-repeat scroll transparent;
	background-size: 50px 30px;
	margin-top: 9px;
	width: 50px;
	/*[[logo]]*/
}
#stocktwits-nav,
#stocktwits-nav.logged-in,
#stocktwits-nav.logged-out,
#global-search-form,
#global-search-form.logged-in,
#global-search-form.logged-out,
#user-info,
#user-info.logged-in,
#user-info.logged-out,
#user-info li {
	background: none!important;
	border: none!important;
}
#stocktwits-nav {
	margin-top: 8px!important;
}
#stocktwits-nav li a {
	color: #54c7b0!important;
}
#stocktwits-nav li:hover a {
	color: white!important;
}
#global-search-form .searchField {
	background: #292938;
	border: 1px solid transparent;
	color: #80849e!important;
	font-size: 1em;
	text-transform: uppercase;
}
#global-search-form .searchField::-webkit-input-placeholder {
	/* Chrome/Opera/Safari */
	
	color: #80849e!important;
}
#global-search-form .searchField::-moz-placeholder {
	/* Firefox 19+ */
	
	color: #80849e!important;
}
#global-search-form .searchField:-ms-input-placeholder {
	/* IE 10+ */
	
	color: #80849e!important;
}
#global-search-form .searchField:-moz-placeholder {
	/* Firefox 18- */
	
	color: #80849e!important;
}
#global-search-form .searchField:hover,
#global-search-form .searchField:active,
#global-search-form .searchField:focus {
	color: #54c7b0!important;
	border-color: #54c7b0;
}
#user-info .user-login-text,
#user-info #down-arrow {
	color: #54c7b0;
	border-top-color: #54c7b0;
}
#user-info li:hover li,
#user-info li:hover #user-login-name {
	background: #292938!important;
}
#trader-drop-down {
	width: 159px;
	background: #292938;
}
#trader-drop-down ul {
	width: 100%!important;
	margin: 0;
}
#trader-drop-down a {
	color: #80849e!important;
	background: none!important;
}
#trader-drop-down a:hover {
	color: white!important;
}
#trending-ad {
	display: none;
}
#indices-container {
	display: none;
}
#single-msg-col-right {
	background: none;
}
.conversation_chart img {
	border-radius: 15px;
}
#trending-container {
	background: #292938;
	border: none;
}
#trending-headline {
	color: #54c7b0!important;
}
#scrollingText a {
	color: #80849e!important;
	border: none!important;
	margin-top: 2px;
}
#scrollingText a:hover {
	color: white!important;
	background: #54c7b0!important;
}
#global-footer {
	opacity: 0.2;
	transition: opacity .15s;
}
#global-footer:hover {
	opacity: 1;
}
#global-footer * {
	color: #80849e!important;
}
#global-footer a:hover {
	color: white!important;
}
#global-footer .logomark {
	display: none;
}
.footer-line-2 {
	margin: 0!important;
}
.sticky-header {
	background: #1e1e2a;
	top: 80px;
	border: none!important;
}
.price-color-change.negative {
	background: #d5464a;
}
.price-color-change.positive {
	background: #68b04d;
}
.symbol-box,
.pricing .price {
	color: white!important;
}
.btn-price-change {
	padding: 0px 15px;
	padding-bottom: 1px;
	margin: 0;
	width: auto;
	text-transform: uppercase;
	border-radius: 10px;
	background: #292938!important;
	border: 1px solid transparent;
}
.btn-price-change,
.btn-price-change h2 {
	color: #80849e;
}
.btn-price-change:hover,
.btn-price-change:hover h2 {
	color: #54c7b0;
	border-color: #54c7b0;
}
.btn-price-change.watching:hover,
.btn-price-change.watching:hover h2 {
	color: #d5464a;
	border-color: #d5464a;
}
.stupid-check {
	display: none;
}
#two-col-side .btn-price-change {
	width: 300px!important;
	padding: 0;
}
.watchers-top {
	color: #80849e!important;
}
.chart-tabs > li > a {
	color: #80849e!important;
	background: none!important;
	border: none!important;
	box-shadow: none;
}
.chart-tabs > li > a.active {
	color: #54c7b0!important;
	font-weight: bold;
}
.chart-tabs > li > a:hover {
	color: white!important;
}
#chart-finviz,
#chart-finviz .chart,
#chart-finviz .chart-zoom {
	background: none;
	border: none;
	color: #80849e;
	text-transform: uppercase;
}
#chart-finviz .zoom.selected {
	color: #54c7b0;
	background: none;
}
#chart-finviz .zoom:hover {
	color: white;
	background: none;
}
.highcharts-background {
	fill: none;
}
.highcharts-root image {
	display: none;
}
.highcharts-yaxis-grid .highcharts-grid-line {
	stroke: #80849e;
	opacity: 0.3;
}
.highcharts-axis-line {
	stroke: transparent;
}
.highcharts-tick {
	stroke: #80849e;
}
.highcharts-xaxis-labels text,
.highcharts-yaxis-labels text {
	fill: #80849e!important;
}
.highcharts-point {
	fill: #80849e;
	stroke: #80849e;
	opacity: 0.6;
}
.highcharts-point-up {
	fill: #68b04d;
	stroke: #68b04d;
	opacity: 1;
}
.highcharts-point-down {
	fill: #d5464a;
	stroke: #d5464a;
	opacity: 1;
}
.highcharts-crosshair {
	stroke: #54c7b0;
}
.highcharts-tooltip > path {
	stroke: #54c7b0;
	stroke-opacity: 1;
	stroke-width: 1;
	fill: #292938;
	fill-opacity: 0.4;
}
.highcharts-tooltip > text {
	fill: #80849e!important;
}
.highcharts-tooltip > text > tspan:first-of-type {
	fill: #54c7b0!important;
}
.chart-bottom {
	background: none;
	border: none;
}
.chart-bottom .bar-options a {
	color: #80849e;
	background: none!important;
	border: none!important;
	box-shadow: none;
}
.chart-bottom .bar-options a:hover {
	color: white;
}
.chart-bottom .bar-options a.active {
	color: #54c7b0;
	font-weight: bold;
}
.qtip {
	border: none;
	background: #1e1e2a!important;
	border-radius: 15px;
}
.symbol-card h1,
.symbol-card .symbol-card-footer {
	background: none;
	border: none;
}
.symbol-card .sidebar-tabs-container {
	background: #292938;
	color: white;
}
.symbol-card .price-container {
	background: none;
}
.symbol-card h1 a {
	color: #54c7b0;
}
.symbol-card h1 a span {
	color: #80849e;
}
.symbol-card h1 a:hover span {
	color: white;
}
.symbol-card #chart-finviz:after {
	background: none;
}
.symbol-card .chart-tabs {
	background: none!important;
}
.symbol-card .chart-tabs li {
	background: none!important;
}
.symbol-card .chart-tabs li > a.active {
	background: #80849e!important;
}
.symbol-card .chart-tabs li > a.active .label,
.symbol-card .chart-tabs li > a.active * {
	color: white!important;
}
.symbol-card .chart-tabs li > a.active .change-image.negative {
	border-top-color: white!important;
}
.symbol-card .chart-tabs li > a.active .change-image.positive {
	border-bottom-color: white!important;
}
#watch-list-widget,
#recent-activity {
	background: none;
	border: none;
	margin-bottom: 35px;
}
.wl-title,
.recent-title {
	border-top-left-radius: 10px!important;
	border-top-right-radius: 10px!important;
	background: #1e1e2a!important;
	border-bottom: 10px solid #2f2f40;
}
.wl-title h2,
.recent-title h2 {
	color: #54c7b0!important;
}
.wl-title .wl-right {
	opacity: 0.1;
}
.wl-title .wl-right:hover {
	opacity: .7;
}
.wl-list li {
	border: none!important;
	padding: 10px 5px!important;
	border-radius: 10px;
}
.wl-list li:first-of-type {
	padding-top: 0!important;
}
.wl-list li h2 {
	color: white!important;
}
.wl-list li .symbol-title {
	color: #80849e!important;
}
.wl-list li:hover {
	background: #292938!important;
	cursor: pointer;
}
.history-list {
	margin: 0!important;
	text-align: center;
	padding: 0;
}
.history-list li a {
	color: #80849e!important;
}
.history-list li a:hover {
	color: white!important;
}
/* --------------- UPDATE ------------------- */

.profile-top .user-top-info * {
	color: #80849e;
}
#top-content .profile-top .user-top-info h1 {
	color: #54c7b0!important;
}
.avatar-container,
.avatar-container img {
	border-radius: 50%;
	border: none;
	background: none;
	box-shadow: none;
}
.side-box,
.side-box .section,
.side-box .section > *,
.side-box .section ul * {
	background: none!important;
	border: none!important;
	box-shadow: none;
	text-shadow: none;
	color: #80849e!important;
}
.side-box #traderStats a span {
	color: #54c7b0!important;
}
.side-box #traderStats li a:hover {
	background: #292938!important;
	border-radius: 10px;
}
.side-box.stats-box {
	top: 0!important;
}
.side-box.stats-box > div:first-of-type {
	position: absolute;
	bottom: -60px;
	right: 0;
}
.side-box .btn-edit-profile {
	padding: 10px 15px;
	margin: 0;
	text-transform: uppercase;
	border-radius: 10px;
	background: #292938!important;
	border: 1px solid transparent;
	box-shadow: none;
	text-shadow: none;
	color: #80849e;
}
.side-box .btn-edit-profile:hover {
	border: 1px solid #54c7b0;
	color: #54c7b0!important;
}
.side-box.sidebar-tabs-container h3 {
	font-weight: bold;
	color: #54c7b0!important;
}
.side-box.sidebar-tabs-container .pricing a {
	color: #54c7b0!important;
}
.side-box.sidebar-tabs-container .pricing a:hover {
	color: white!important;
	background: #292938!important;
	border-radius: 10px;
}
.side-item.sponsor {
	display: none;
}
.stream-list.small-style .message {
	padding: 10px;
	border-radius: 10px;
}
.stream-list.small-style .message:hover {
	background: #292938;
}
.stream-list .message-content a {
	color: #54c7b0;
}
.stream-list .message-content .username,
.stream-list .message-date a {
	color: #80849e;
}
.stream-list .message-content {
	color: white;
}
.single-msg#content-box {
	margin: 0 auto!important;
}
#msg-contain .single-msg {
	float: none!important;
}
/* -------------- UPDATE ------------------- */

@media only screen and (max-width: 1023px) {
	#two-col-left {
		z-index: 10;
		background: #292938!important;
		border: none;
		padding-bottom: 10px;
	}
	.wl-title,
	.recent-title {
		background: none!important;
		border: none!important;
	}
	.wl-item a h2 {
		color: #80849e!important;
	}
	.wl-item a:hover h2 {
		color: white!important;
	}
	.wl-title h2,
	.recent-title h2 {
		font-weight: bold!important;
	}
	.wl-all {
		border-color: #80849e!important;
		cursor: pointer;
	}
	.wl-all a {
		color: #80849e!important
	}
	.wl-all:hover {
		border-color: white!important;
	}
	.wl-all:hover a {
		color: white!important;
	}
}
.side-box #user-actions li a:hover {
	color: white!important;
}
.btn-unfollow,
.btn-follow {
	background: #292938!important;
	text-shadow: none;
	box-shadow: none;
	border-radius: 10px;
	border: 1px solid transparent;
	color: #80849e;
	padding: 5px 15px!important;
	text-transform: uppercase;
}
.btn-follow:hover {
	border-color: #68b04d;
	color: #68b04d!important;
}
.btn-unfollow:hover {
	border-color: #d5464a;
	color: #d5464a!important;
}
.media-type img {
	border: none!important;
}
#two-col-main .gray-bar {
	background: none!important;
	border: none!important;
}
.bar-options a.active {
	text-shadow: none!important;
}
.semi-fixed-content {
	position: static!important;
}
.chart:hover {
	cursor: crosshair;
}
.embedly-videoplayer {
	border-radius: 15px;
	overflow: hidden;
}
#default-modal.mb3-modal {
	background: #1e1e2a;
	border: none;
	border-radius: 10px;
	padding: 10px;
	box-shadow: none;
}
.mb3-modal .top-bar {
	background: none!important;
	text-shadow: none!important;
}
.mb3-modal .top-bar span {
