css:
@-moz-document domain("web.ciscospark.com"), domain("teams.webex.com") {
body {
    background-color: #000;
}

.listItem {
	color: #fff;
}

.roomListItem-description {
    color: #fff;
}

.roomListItem-title-text {
    color: #fff;
}

.teamDetails-list .listItem:hover:not(.isActive) {
    background-color: #666;
}

.roomListItem:hover {
  background-color: #666;
}

.navigation .navigation-bar {
	background-color: #000;
}

#convos {
	background-color: #333;
    border: 3px solid #333;
}

.convo-filter-menu-list-header {
    background-color: #333;
}

.convo-filter-menu-list {
    background-color: #333;
}

.convo-filter-menu-list-item {
    background-color: #333;
}

#conversation {
    background-color: #000;
}

.convo-filter-menu-list-header {
    border: 3px solid #333;
}

.teamDetails {
    background-color: #333;
}

.teamsSection {
    background-color: #333;
}

.teamProfile-bottom {
    background-color: #000;
}

.activity-title {
	background-color: #333;
}

.activities-inner-wrapper {
    background-color: #000;
}

body.roskilde #activities .activity-item:hover .content {
    background-color: #000;
}

.activity-text {
    color: #fff !important;
}

#activities .activity-item.me .activity-text {
    color: #fff !important;
}

.activity-item--message {
    color: #ddd !important;
}

.meta {
    color: #fff !important;
}

.room-actions-menu-icon {
    background-color: #333;
}
}
