@-moz-document domain("www.twitch.tv"), domain("clips.twitch.tv") {
/*			/* Twitch Flat Dark & Clean v.0.2 */
                    /* by MarUlberg */
/* Inspired by kirnbauer's "Youtube Dark Style Material [BETA]" */

.tw-theme--ffz .top-nav__menu, .tw-theme--ffz .tw-theme--dark .top-nav__menu, .tw-theme--ffz.tw-theme--dark .top-nav__menu{
    background-color: #424242 !important;
}
.tw-theme--ffz .twilight-root, .tw-theme--ffz .tw-theme--dark .twilight-root, .tw-theme--ffz.tw-theme--dark .twilight-root{
    background-color: #212121 !important;
}
.tw-aspect>*{
    border-radius: 8px !important;
}
.live-channel-card__boxart{
    border: none !important;
}
}
