css:
@-moz-document domain(trello.com) {
body, button, html, input, select, textarea {
    font: 12px Tahoma, "Helvetica Neue", Arial, Helvetica, sans-serif;
}
.board-header-btn-name, .window-module-title h3, .board-tile-details-name {
    font-family: "x yekan", "b yekan", "yekan";
}
.list-header-extras {
    right: inherit;
    left: 4px;
}
.list-header.is-menu-shown.is-subscribe-shown {
    padding-right: 0;
    padding-left: 60px;
}
.list-header.is-menu-shown, .list-header.is-subscribe-shown {
    padding-right: 0;
    padding-left: 34px;
}
.list-card-operation {
    padding: 1px;
    right: inherit;
    left: 8px;
}
.list-card-labels {
    margin: -6px 0px 0px 0px;
    overflow: auto;
}
.card-label.mod-card-front {
    margin: -3px 3px 3px 0;
    padding: 0;
}
.header-btn.header-woof {
    display: none;
}
.phenom{
    text-align: right;
    margin-left: 0;
    margin-right: 40px;
    direction: rtl;
}
.phenom-creator, .phenom-icon{
    left: inherit;
    right: -40px;
}
.phenom-desc{
    margin: 0 0 0 24px;
}
.phenom-reactions {
    margin-right: 0;
    margin-left: 24px;
}
.phenom-desc .u-font-weight-bold{
    float: right;
    margin-left: 4px;
}
.jd_title{
    color: #777;
    direction: rtl;
    position: absolute;
    right: 11px;
    top: 8px;
}
.jd_title.friday{color: #e11111;}

.list-card-members .member {
    width: 18px;
    height: 18px;
    margin: 4px 4px 4px 0;
}
.list-card-members img {
    width: 18px !important;
    height: 18px !important;
}
.list-card-members {
    float: left;
    margin-top: -4px;
}
.list-card-members .member-initials{
    font-size: 8px;
    top: -5px;
    left: -5px;
}
}
