css:
@-moz-document domain("die-staemme.de") {
body {
    background-color: #241e14;
    background-image: none;
    color: #9e9e9e;
}
a {
    color: #eab371;
}
#main_layout .bg_left {
    display: none;
}
#main_layout .bg_right {
    display: none;
}
.newStyleOnly {
    display: none;
}
.maincell {
    background: #241e14;
}
.maincell > .info_box {
    background: url(https://dsde.innogamescdn.com/8.78/32721/graphic/questionmark.png) no-repeat 4px center;
    filter: invert(100%);
}
.quest {
    background-color: #241e14;
}
.info_box {
    background: #241e14;
}
.content-border {
    background: #241e14;
    border: none;
}
#inner-border {
    border: 1px solid #433005;
}
.vis .row_a > td,
.vis.alternating tr:nth-child(even) td,
.vis .row_a > tr > td {
    background: #241e14;
}
.vis .row_b > td,
.vis.alternating-rows tr:nth-child(odd) td,
.vis .row_a > tr > td {
    background: #352c1d;
}
.selected,
.selected td {
    background-color: #7a6643 !important;
}
.forum-content {
    background: #181102;
}
.thread_new_open {
    filter: invert(100%);
}
.thread_new {
    filter: invert(100%);
}
.thread_new_close {
    filter: invert(100%);
}
.thread_poll_open {
    filter: invert(100%);
}
.thread_poll {
    filter: invert(100%);
}
.thread_poll_close {
    filter: invert(100%);
}
.thread_answer_open {
    filter: invert(100%);
}
.thread_answer {
    filter: invert(100%);
}
.thread_answer_close {
    filter: invert(100%);
}
.forum {
    background-color: #181102;
}
.shared_forum {
    background-color: #2a1e03;
}
.post {
    background-color: #0e0a01;
}
.igmline {
    background: #181102;
}
th,
.vis > h4 {
    background-color: #241e14 !important;
    background-image: none;
}
.header-border {
    background: none;
}
.vis td,
.vis_item {
    background: #31291b;
}
.header-border .firstcell {
    background: none;
}
.box-item:first-child {
    background: none;
}
.header-border .box {
    background: none;
}
.box {
    background-color: #241e14;
}
.icon-box {
    background: none;
}
.lit .lit-item {
    background-color: #453a26 !important;
}
#footer {
    background: #0e0a01;
}
.borderimage .popup_box {
    border: solid #804000;
    -moz-border-image: none;
    -webkit-border-image: none;
    -o-border-image: none;
    border-image: none;
}
.popup_box_content {
    background-image: none;
    background: #181102;
}
.quest-summary {
    background-color: #0e0a01;
}
.quest-goal {
    background-color: #0e0a01;
    background-image: none;
}
.quest-goal > table {
    border: none;
}
.map-legend-container table {
    background-color: #241e14;
}
.map_container, #warplanner {
    background-color: #241e14 !important;
}
.report_transparent_overlay {
    background: rgba(53, 44, 29, 0.84) !important;
}
.premium_account_hint {
    display: none;
}
.chat-window {
    border: none;
}
.chat-header {
    background: #352c1d;
}
.chat-body {
    height: 520px;
    background: #181102;
}
.chat-message {
    background: #0e0a01;
}
.chat-message.chat-message-other {
    background: #0e0a01;
}
.chat-contacts .chat-body {
    background: #181102;
}
.chat-footer {
    background: #181102;
}
.chat-input {
    background-color: #181102;
    color: #9e9e9e;
}
.chat-popover {
    background: #181102;
}
.chat-contact {
    border-bottom: 1px solid #5b420a;
}
.world_button_active {
    background: none;
}
.server_info {
    background-image: none;
}
}
