css:
@-moz-document domain(pogovorisomnoi.ru){
body {
    color: #fff !important;
    background-color: #000 !important;
}
.message.system {
    color: #4c7ad2 !important;
}
.btn-small {
    padding: 2px 10px;
    font-size: 11.9px;
    -webkit-border-radius: 0;
    -moz-border-radius: 0;
    border-radius: 0;
}
.btn {
    display: inline-block;
	/*padding: 4px 12px; */
    margin-bottom: 0;
    font-size: 14px;
    line-height: 20px;
    color: #fff;
    text-align: center;
    text-shadow: none;
    vertical-align: middle;
    cursor: pointer;
    background-image: none;
    background-color: #000000 ;
    border: 1px solid #4c7ad2 !important;
    -webkit-border-radius: 0;
    -moz-border-radius: 0;
    border-radius: 0;
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    box-shadow: none;
    -webkit-transition: .1s cubic-bezier(0.4, 0, 1, 1);
    -moz-transition: .1s cubic-bezier(0.4, 0, 1, 1);
    -o-transition: .1s cubic-bezier(0.4, 0, 1, 1);
    transition:  .1s cubic-bezier(0.4, 0, 1, 1);
}
.btn-large {padding: 14px 20px;}

.btn:hover, .btn:focus {
    color: #4c7ad2;
    text-decoration: none;
    background-position: 0 -15px;
    -webkit-transition: .1s cubic-bezier(0.4, 0, 1, 1);
    -moz-transition: .1s cubic-bezier(0.4, 0, 1, 1);
    -o-transition: .1s cubic-bezier(0.4, 0, 1, 1);
    transition:  .1s cubic-bezier(0.4, 0, 1, 1);
}
.btn:hover, .btn:focus, .btn:active, .btn.active, .btn.disabled, .btn[disabled] {
    background-color: #000 !important;
    color: #4c7ad2;
}

textarea{
	color: #fff;
    background-color: #000;
    border: 1px solid #4c7ad2 !important;
    border-radius: 0 !important;
}

.message.mine {
    text-align: left;
    color: #ffffff;
    background-color: rgba(76, 122, 210, 0.06);
    padding-left: 10px;
}

html::-webkit-scrollbar-thumb {
    background-color: #4c7ad2;
    border-radius: 6px;
    border: 2px solid #000;
    min-height: 50px;
}
html::-webkit-scrollbar {
    width: 12px;
    background-color: #000;
}
}
