css:
@-moz-document domain("www.tumblr.com")
	{

		@media (orientation: portrait) { .identity { background-image: url('https://i.imgur.com/FEt5gTc.jpg') !important; } }

		@media (orientation: landscape) { .identity { background-image: url('https://i.imgur.com/RuOfU6t.jpg') !important; } }

		/*Make Right TL column dissapear*/
		
		.controls_section.follow_list.user_list.is_rendering.recommended_tumblelogs,
		.sidebar_link.avatar_align,
		.controls_section.controls_section_radar,
		#tumblr_radar,
		.horse-friend.minimized,
		#posts.posts > li.notification.single_notification.alt.takeover-container.big-margin
                { display: none; }
		
		#right_column,
		.identity .right_column::after
			{ background: none !important; }
		
		/*Dissapear ends here*/
		
		/*Whatever column you didn't make it gone*/
		
		.l-container.l-container--two-column .right_column
			{
				margin-right: 22.5% !important;
				width: 64% !important;
			}
		
		#right_column.right_column.inbox { margin-right: 22.5% !important; }
		
		div#left_column
			{
					background-color: transparent !important;
					margin-left: 13.5% !important;
			}
		
		div#following > #left_column.left_column.clearfix { margin-left: 20.5% !important; }
		
		/*Whatever ends here*/
		
		ul.sidebar_nav,
		.l-footer-container.clearfix,
		.post_avatar.tumblelog-avatar,
		button.close
			{ display: none;}

		.post_avatar,
		.l-container.l-container--two-column-dashboard .l-content,
		.l-container.l-container--two-column .l-content,
		.ui_search,
		.toast.blog-sub,
		.ui_peepr_glass
			{ background: transparent; }

		.tmblr-attribution,
		.reblog-content pre,
		#left_column.left_column.white_pane	
			{ background-color: #111 !important; }
		
		.link-button.link-button--default.no-thumbnail .info-container
			{ background-color: #242526 !important; }

		.follow-text,
		i.icon_post_text,
		i.icon_post_photo,
		i.icon_post_quote,
		i.icon_post_link,
		i.icon_post_chat,
		i.icon_post_audio,
		i.icon_post_video,
		.post_content,
		.quote,
		.reblog-content pre,
		.note_item > .text p,
		#left_column.left_column.white_pane,
		.info-container .excerpt
			{ color: #ccc !important; }

		.reblog-tumblelog-name.post_info_link,
		.settings_heading,
		.post_info_link:hover,
		.info-container .title,
		.info-container:hover .publisher
			{ color: #eee !important;}

		span.name,
		div.text p
			{ color: #111 !important; }

		.reblog-list-item.original-reblog-content,
		.reblog-list,
		.note_item
			{ border-color: #111 !important; }

		.note_item { background-color: #242526 !important; }
		.nipple { border-left-color: #242526 !important; }
		.asker a { color: #eee !important; }


		.post_avatar.show_user_menu.post-avatar--sticky,
		.post_avatar.post-avatar--sticky
			{
						opacity: 0 !important;
						transition: opacity 0.5s !important;
			}

		.post_container:hover .post_avatar.show_user_menu.post-avatar--sticky,
		.post_container:hover .post_avatar.post-avatar--sticky
			{ opacity: 1 !important; }

		.pagination { padding-left: 22.5% !important; }

		.post_avatar_link,
		.post.post_full
			{
						border-radius: 0px !important;
						box-shadow: 5px 5px 10px 1px rgba(0,0,0,.7);
						background: #111;
			}

		.post.post_full { border: 0px solid #fff; }

		.l-header-container--refresh
			{
					box-shadow: 0 0 0 0px hsla(0,0%,100%,.13);
					background: rgba(33,33,33,.4);
			}
		
		.identity
				{
						background: transparent;					
						background-size: cover;
						background-attachment: fixed;
						background-position: center;
				}

		.tab_notice { background: #fff !important; }

		.tab-notice--outlined { border: 2px solid #444 !important; }

		.compose-button { fill: #ccc; }

		#search_query { background: rgba(0,0,0,.3); }

		div.post_tags.nowrap.draggable:after,
		div.post_tags.nowrap::after
			{ background: linear-gradient(to right, rgba(255,255,255,0) 0%, #111 100%) !important; }

		div.post_tags.nowrap.draggable:before { background: linear-gradient(270deg,hsla(0,0%,100%,0) 0,#111) !important; }

		.post-source-footer { background: #111 !important; }


		.post_body pre { color: #ccc !important; }

		.reblog-content pre 
			{
				overflow: hidden !important;
				font-size: 12px !important;
			}

		.ui_dialog_pos,
		.post-tab-switching
			{ background-color: rgba(36,37,38,0.5) !important; }

		.post_body pre,
		.link-button.link-button--default.has-thumbnail > .info-container
			{ background-color: #242526 !important; }

		/*Pop over menu*/

		.popover_inner,
		.popover_inner li,
		.indash_header_wrapper .navigation_bg
			{ background-color: #111 !important; }

		.popover_inner li:hover,
		.popover_inner a:hover
			{ background-color: #242526 !important; }

		.popover_inner li,
		.popover_inner li a,
		.popover-inner.popover_inner button
			{ color: #ccc !important; }

		.popover_inner li:hover,
		.popover_inner a:hover,
		.popover-inner.popover_inner:hover button
			{ color: #eee !important; }
		
		.post_glass.post_micro_glass_w_controls.post_micro_glass a.hover:hover,
		.info_popover_button.nav_icon.user_dropdown_lockup:hover
			{ background-color: transparent !important; }

		/*Pop over menu ends here*/
		
		/*Lighting notifications*/
		
		.activity-notification { border-color: #111 !important; }
		
		.activity-notification:hover .activity-notification__glass { position: unset !important; }
		
		/* Lighting notifications ends here*/
		
		/*Notification*/
		
		.notification
		  {
			  background-color: #111 !important;
				box-shadow: 2.5px 2.5px 5px 1px rgba(0,0,0,.7) !important;
		  }
		
		.notification:hover { background-color: #242526 !important; }
		
		.notification a { color: #ccc !important; }
		
		.notification a:hover { color: #eee !important; text-decoration: none !important; }
		
		/*Notification ends here*/

		/*Posted from*/
		@media (orientation: portrait)
			{
				.toastr,
				.toast-kit,
				.show-toast
					{
							width: 45px !important;
							padding: 0px !important;
					}

				.toastr
					{
						right: -950px !important;
						bottom: 40px !important;
					}

				.toast-bread { opacity: 0 !important; }

				.toaster.avatar { height: 35px !important; width: 35px !important; }
			}

		.toastr { left: 185px !important; }
		
		/*Posted from ends here*/

		/*Relatd post fixes
		I noticed that tumblr usses fixed measures to the related post list
		so I followed their rules, and centered it.
		Feel free to use relative measures but it is easy this way*/
		@media (orientation: portrait)
			{
				.rapid-recs-container,
				.rapid-recs,
				.rapid-rec-inner,
				.rapid-rec-title,
				.micro-post-list
					{ width: 240px !important; }

				.rapid-recs
					{
						right: -530px !important;
						bottom: 0px !important;
						padding: 0px !important;
					}

				.micro-post-list { margin-left: 48px !important; }

				.micro-post-item { float: none !important; width: 60% !important; }

				.rapid-recs-container.peepr-open,
				.rapid-recs-container.peepr-open .rapid-recs,
				.rapid-recs-container.peepr-open .rapid-rec-inner,
				.rapid-recs-container.peepr-open .micro-post-list
				
					{
						left: 562px !important;
						width: 480px !important;
					}
			}

		div.rapid-recs { background-image: none !important; }
		
		.peepr .ui_peepr_glass { background-color: rgba(36,37,38,0.75) !important; }

		.rapid-rec-title
			{
				padding: 0px !important;
				color: #eee !important;
				text-align: center !important;
			}
		
		.rapid-recs-container.peepr-open .rapid-rec-title
			{
				left: 53px !important;
				width: 279px !important;
			}

		/*End related posts*/

		/*Notes*/

		.post-activity.can-reply,
		.post-activity-notes.has-notes,
		.note-list li,
		.text-input
			{ background-color: #111 !important; }

		.post-activity-popover.popover.popover_menu.popover_gradient.popover--base.right,
		.post-activity-header.conversation,
		.post-activity-rollups,
		.post-activity-reply,
		.post-activity-note.note-item,
		.reblog-list-item,
		.is-followed.with-commentary .post-activity-note-content
			{ border-color: #111 !important; }

		.post-activity-note.note-item > .post-activity-note-content,
		div.tx-scrollbar-thumb
			{ background-color: #242526 !important; }

		.header-container,
		.post-activity-note-content p
			{ color: #ccc !important; }

		.header-inner p,
		.post-activity-note-content p > a
			{ color: #eee !important; }
		
		.post-activity-note.note-item:hover { background-color: transparent !important; }

		/*End notes*/
		
		/*Sharing*/
		
		.messaging-share-post .messaging-share-post-external-networks { background-color: #111 !important; }
		
		.tx-scrollbar-track.tx-scrollbar-track--horizontal .tx-scrollbar-thumb,
		.messaging-share-post .messaging-share-post-external-networks .messaging-share-post-external-network::before
		  { background-color: #242526 !important; }
		
		.messaging-share-post-search { border-color: #111 !important; }
				
		.messaging-share-post-result .name,
		.messaging-share-post .messaging-share-post-search .search-input:focus
		  { color: #ccc !important; }
				
		.messaging-share-post-result:hover .name { color: #eee !important; }
		
		.messaging-share-post .messaging-share-post-external-networks .messaging-share-post-external-network:hover,
		.messaging-share-post .messaging-share-post-result:hover
		  { background-color: transparent !important; }
		
		/*Sharing ends*/

		/*Audio player*/

		.audio_player,
		.audio-player
			{ background-color: #111 !important; }
		
		.progress { background-color: #242526 !important; }

		.player_text { color: #ccc !important; }

		.playhead { background-color: #eee !important; }

		.audio_player.playing.duration,
		.player_text_wrapper
			{ border-color: #111 !important; }

		i.play_button.audio_player_button::after { background-image: url('https://i.imgur.com/K36ci4h.png') !important;}

		i.pause_button.audio_player_button::after	{ background: url('https://i.imgur.com/VEgmF90.png') !important;}

		/*Ends audio player */

		/*Message People*/

		.messaging-inbox { background-color: #111 !important; }

		.messaging-inbox a,
		span.name
			{ color: #eee !important; }

		.selected-blog { color: #ccc !important; }

		.inbox-header,
		.inbox-conversation
			{ border-color: #111 !important; }

		.inbox-conversation:hover { background-color: #242526 !important; }

		/*Ends message people*/

		/*Message Chat*/
		
		.conversation-header,
		.conversation-main,
		.compose-text-input-container,
		.external-link
			{ background-color: #111 !important; }

		.conversation-header,
		.conversation-header a,
		.message-bubble-header a,
		.compose-text-input-container button
			{ color: #eee !important; }

		.conversation-main,
		.message-bubble.full-width-bubble,
		.message-bubble,
		.text-input
			{ color: #ccc !important; }

		a[data-js-tumblelog-name="imnotgoodatthisbutsomeoneneither"] ~ .message-bubble.full-width-bubble,
		a[data-js-tumblelog-name="imnotgoodatthisbutsomeoneneither"] + div,
		.external-link:hover
			{ background-color: #eee !important; color: #242526 !important; }

		a[data-js-tumblelog-name="imnotgoodatthisbutsomeoneneither"] ~ .message-bubble.full-width-bubble,
		a[data-js-tumblelog-name="imnotgoodatthisbutsomeoneneither"] ~ .message-bubble
			{
				margin-left: 40px !important;
				width: 100% !important;
			}
		
		.message-bubble { width: 100% !important; }
		
		a[data-js-tumblelog-name="imnotgoodatthisbutsomeoneneither"] { display: none !important; }

		.message-bubble.full-width-bubble,
		.message-bubble,
		.text-input:focus
			{ background-color: #242526 !important; }

		.conversation-compose { border-color: #111 !important; }

		.text-input	{ border-radius: 0px !important; }

		/*Ends Message chat*/

		/*Docked Video*/

		.post_header.post_info,
		.post_footer.clearfix
			{ background-color: #111 !important; }

		.post_footer.clearfix { border-radius: 0px !important; }

		/*Ends Docked Video*/

		/*Explore Tab */

		.tags-and-tumblelogs,
		article,
		.expand-post-content,
		.post_content .post_content_inner,
		.conversation_lines li,
		p.tmblr-attribution,
		#discover_actions_discover .elevator
			{ background-color: #111 !important; }

		article,
		.indash_blog.indash_tumblelog_compact.fade_in,
		.tags-and-tumblelogs
			{ box-shadow: 5px 5px 10px 1px rgba(0,0,0,.7) !important; }
		
		.post_brick .post_tags::after
			{ background-image: linear-gradient(90deg,hsla(0,0%,100%,0) 0,#111 80%) !important; }

		.post_content_inner,
		.post-brick-source-footer,
		.expand-post-content,
		.read_more
			{ color: #ccc !important; }

		.post-info-tumblelog a:hover,
		.post_controls_inner a
			{ color: #eee !important; }

		.post_header.post_info,
		.post-brick-source-footer,
		.discover-section-title
		{ border-color: #111 !important; }

		.controls_section.follow_list.user_list a::before { display:none !important; }

		.expand-post-content { opacity: 0.75 !important; }

		.tags-and-tumblelogs { margin-bottom: 15px !important; }

		.discover-section-title { padding-top: 10px !important; }

		.discover-search-terms { padding: 0px 10px !important; }
		
		.l-container.l-container--flex > .l-content.clearfix,
		.search_results_container .indash_blogs .blogs_wrapper_carousel::after,
		.search_results_container .tumblelog_mask_item
			{ background-color: transparent !important; }
		
		/*Ends Explore Tab*/

		/*Posting*/

		.post-forms-glass.post-forms-glass_active.active { background-color: rgba(36,37,38,0.95) !important; }

		.post-container,
		.post-form--form.post-content-text,
		.editor.editor-plaintext,
		.editor-placeholder p,
		.editor-plaintext,
		.editor-richtext,
		.post-container,
		.media-dropzone.split-dropzone.toggle-dropzone-upload,
		.editor-slot,
		.popover--tumblelog-select-dropdown.popover--base.bottom div,
		.popover--post-settings-dropdown.popover--base.bottom div,
		.popover--save-post-dropdown.popover--base.bottom div,
		select#editorType
			{ background-color: #111 !important; }

		.button-area.create_post_button.disabled,
		.split-button,
		.dropdown-area.icon_arrow_carrot_down.pinned-target,
		.button-area.create_post_button,
		.bubbles div::before,
		.popover--tumblelog-select-dropdown.popover--base.bottom:hover li,
		.popover--tumblelog-select-dropdown.popover--base.bottom:hover div,
		.popover--save-post-dropdown.popover--base.bottom li:hover,
		.dropdown-area.icon_arrow_carrot_down,
		.post-form--tag-editor .tag-label.hover-style
			{ background-color: #242526 !important; }

		.editor-slot,
		.editor-richtext p,
		.popover--tumblelog-select-dropdown.popover--base.bottom:hover .ts-title,
		.popover--save-post-dropdown.popover--base.bottom div,
		.group-content input,
		.group-content div,
		select#editorType,
		.tag-label.hover-style
			{ color: #ccc !important; }

		.popover--tumblelog-select-dropdown.popover--base.bottom:hover li,
		.popover--save-post-dropdown.popover--base.bottom:hover li,
		.group-label,
		.tag-label,
		.post-form--tag-editor .tag-label.hover-style
			{ color: #eee !important; }

		.media-container.link-media-container .link-editor,
		.popover--post-settings-dropdown.popover--base.bottom div
			{ border-color: #111 !important; }
		
		.dropdown-area.icon_arrow_carrot_down::after { display: none !important; }

		.bubbles div::before { border-radius: 15px !important; }

		.post-form.gif-search-variation .editor-slot .inline-controls .tray:not(.inline-embed-open) { width: 35% !important; }

		.editor.editor-plaintext { min-width: 75px !important; }

		select#editorType option
			{
				outline: none !important;
				box-shadow: none !important;
			}

		/*Posting Ends Here*/

		/*Activity*/

		span.activity,
		.note_follow,
		.graph_type_label,
		.graph_type_label_count
		  { color: #ccc !important; }

		span.activity a,
		.heading.arrow.open_popover,
		span.active,
		.activity-notification:hover .note_follow,
		.marker_tooltip_header
			{ color: #eee !important; }

		.notification_pane,
		.activity-popover-header,
		.activity-popover-tumblelog-select-container,
                .activity-notification,
		.ui_note.is_follower.no_icon.is_friend,
		.ui_note.is_friend,
		.axis-label,
		.ui_stats .unit .stat_txt,
		.activity-notification:hover .activity-notification__activity_message.conversational,
		.marker_tooltip
			{ background-color: #111 !important; }

		.blog-activity-content,
		.ui_notes .ui_note,
		.notifications_page .notifications_block,
		.ui_stats .unit .stat_txt,
		.marker_tooltip,
		.marker_tooltip_header
			{ border-color: #111 !important; }

		.date_header.clearfix,
		.blog-activity-more,
		.ui_notes.clearfix > .activity-notification:hover,
		.activity-notification__activity_message.conversational
			{
				background-color: #242526 !important;
				border-color: #242526 !important;
			}

		.ui_note,
		.ui_notes .ui_note
			{ border: none !important; }

		.notifications_page .section.divider { background: none !important; }

		.plop::before,
		.plop::after
			{ border-color: #242526 transparent transparent transparent !important; }

		.ui_stats .unit .button { top: 0px !important; }

		.plus-follow-button .follow_icon { margin-top: 0px !important; }

		.ui_notes .part_icon .note_follow { border: none !important; }

		.popover--activity-popover .blog-activity-notification .ui_note .part_icon .note_follow { background-color: #242526 !important; }
		
		.marker_tooltip_header { background: #111 !important; }
		
		.notification_pane { box-shadow: 5px 5px 10px 1px rgba(0,0,0,.7); }

		/*Activity Ends Here*/

		/*Profile Button*/

		.popover_menu_item_anchor:hover,
		.blog-sub-nav-details .blog-sub-nav-item:hover a,
		.blog-list-item.blog-list-item--on-light:hover,
		.blog-list-item-anchor:hover
			{ background-color: #242526 !important; }

		.blog-sub-nav-details .blog-sub-nav-item:hover .blog-sub-nav-item-label,
		.blog-list-item-anchor:hover .blog-list-item-info-name
			{ color: #eee !important; }

		.blog-list-item-anchor:hover .blog-list-item-info-title,
		.blog-sub-nav-item-label
			{ color: #ccc !important; }

		.popover_header,
		div.popover_item_suffix a
			{
				background-color: #242526 !important;
				border-color: #242526 !important;
			}

		.popover_menu_item.popover_menu_item_blog.popover_menu_item_blog--uncloseable.popover_menu_item_blog--open { background-color: #111 !important; }

		.blog-sub-nav-details .blog-sub-nav-item:hover
			{
				box-shadow: -4px 0 0 #242526,10px 0 0 #242526 !important;
				background-color: #242526 !important;
			}

		.popover_header
			{
				margin: 0px !important;
				border-radius: 0px !important;
			}

		.blog-sub-nav-item.selected
			{
				box-shadow: -4px 0 0 #111,10px 0 0 #111 !important;
				background-color: #111 !important;
			}

		/*Profile Button Ends Here*/
		
		/*Followers*/
		
		#dashboard_controls_open_blog,
		.left_column.followers
			{ box-shadow: 5px 5px 10px 1px rgba(0,0,0,.7) !important; }
		
		#dashboard_controls_open_blog,
		#user_to_check_for,
		.white_pane,
		.follower,
		.pagination,
		.follower:hover button
			{ background-color: #111 !important; }
		
		#user_to_check_for:focus,
		.follower:hover,
		.controls button
			{ background-color: #242526 !important; }
		
		#dashboard_useraction_followers,
		#user_to_check_for:focus,
		.open_blog_link,
		.controls button
			{ color: #ccc !important }
		
		.currently_selected_blog,
		.controls_section_item a,
		.controls_section_item:hover span.count,
		.title_and_controls h1,
		.name-link,
		.follower:hover button
			{ color: #eee !important }
		
		#user_to_check_for,
		.controls_section_item,
		.follower:hover button
			{ border-color: #111 !important; }
		
		.controls button,
		.controls_section_item.selected,
		.controls_section_item:hover
			{ border-color: #242526 !important; }
		
		 #followers-list .pagination { padding-left: 0px !important; }
		
		/*Followers ends here*/
		
		/*Following*/
		
		#following.clearfix { box-shadow: 5px 5px 10px 1px rgba(0,0,0,.7) !important; }
		
		#tabs .tab.selected,
		#following.clearfix
