css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("pokemon-revolution-online.net") {
    div.wrap {
        background: #292929 !important;
        z-index: -99 !important;
    }
    
    #inner-wrap, input.btnlite, a.button {
        background: #525252 !important;
    }

    div.parallax-mirror {
        z-index: 0 !important;
    }
    
    li.row, ul.dropdown-contents {
        background: #919191 !important;
        border-bottom-color: #8C8C8C !important;
    }
    
    div.profile_widget, ul.topiclist, div.post, div.viewtopic_wrapper, div.panel {
        background: #808080 !important;
    }
    
    a.specialbutton, span.button {
        background: #808080 !important;
    }
    
    textarea.inputbox, input.inputbox, li.tab, select.inputbox {
        background: #C4C4C4 !important;
    }
}
