@-moz-document url-prefix("http://vk.com"), url-prefix("https://vk.com") {
/*By Arslan Valeev*/
/*В разработке...*/

/*Долой рекламу! */
#ads_left {
    display: none !important;
	}
/* Долой рекламу! */

/* Шапка (н) */
#page_header_cont .back, #top_audio_player, #top_profile_link, #top_notify_btn, #top_audio, #top_switch_lang {
	background: #37474F !important;
	}
input.text.ts_input, #top_audio_player:hover, #top_profile_link:hover, #top_notify_btn:hover, #top_audio:hover, #top_switch_lang:hover {
	background-color: #263238 !important;
	}
