	background-image: url(https://pp.userapi.com/c837438/v837438821/23780/t910HBkAPxQ.jpg) !important; /*Картинка на фоне*/
	background-attachment:  fixed; /* */
	background-size: cover; /* */
	}
/* Фон (к) */
    
/* Левое Меню (н) */
#side_bar ol li {
    background-color: #fff;	/*цвет фона*/
    border-radius: 3px; /*Округленные углы*/
	}
/* Левое Меню (к) */

/* Слайдер аудио (н) */
.slider_back.slider_back_transition {
	background-color: #90A4AE !important;
	}

.slider_handler, .slider_amount {
	background-color: #37474F !important;
	}
/* Слайдер Аудио (к) */
}
