css:
@-moz-document domain(wykop.pl) {
*::-webkit-scrollbar {
    width: 10px;
    height: 10px;
    background: #1e1e1e !important;
    border: 1px solid #252525 !important;
}
*::-webkit-scrollbar-button {
    display: none;
}
*::-webkit-scrollbar-thumb,
*::-webkit-scrollbar-track {
    border: 0 !important;
    box-shadow: none;
}
*::-webkit-scrollbar-thumb {
    min-height: 28px;
    background: #3a3a3a !important;
}
*::-webkit-scrollbar-corner,
*::-webkit-scrollbar-track {
    background: #292929 !important;
}
::selection {
    background: #B6B6B6;
    color: grey;
}
body {
    background-color: #cccccc !important;
    color: #52F3FF !important;
}
a {
    color: #d9d9d9 !important;
}
a:hover {
    color: #d9d9d9 !important;
}
em.mark-number {
    background: #702321 !important;
}
textarea {
    background-color: #121212 !important;
    border: 1px solid #2E2E2E;
    color: #b6b6b6;
}
input {
    color: #c5c5c5!important;
    background-color: #2e2e2e !important;
    border-color: #2e2e2e !important;
}
#site {
    background: #19443e !important;
}
#site .article h2 a,
#itemsStream .article h2 a {
    color: #d9d9d9 !important;
}
#site .article h2 a:hover,
#itemsStream .article h2 a:hover {
    color: #d9d9d9 !important;
    text-decoration: underline !important;
}
#itemsStream .article .description a {
    color: #e7e7e7 !important;
}
#itemsStream .article .description a:hover {
    color: #2a2a2a !important;
    border: 0 !important;
}
#itemsStream .author {
    border-color: #1a1a1a !important;
}
.bdivider,
.tdivider {
    border-color: #19443e !important;
}
#nav {
    background: #4863A0 !important;
}
#nav .nav li.active a,
#site .nav li.active a {
    background: #2e2e2e !important;
    color: #d9d9d9 !important;
    border-color: #702321 !important;
}
#nav .nav li a:hover,
#site .nav li a:hover {
    border-color: #702321 !important;
    color: #d9d9d9 !important;
}
.nav {
    border-color: #1a1a1a !important;
}
.r-block h4 {
    color: #702321 !important;
    border-color: #1a1a1a !important;
}
#footer h4 {
    color: #702321 !important;
    border-color: #1a1a1a !important;
}
.diggbox span:first-child {
    display: block;
    width: 49px;
    height: 42px;
    line-height: 42px;
    font-weight: bold;
    color: #702321;
    background-color: #3d3d3d !important;
   
}
.rbl-block {
    background-color: #2e2e2e !important;
    border: 0 !important;
}
.menu-list li {
    border: 0 !important;
}
.description {
    border-top: 1px solid #2A2A2A !important;
    background: #323232 !important;
}
.light {
    color: #95a5a6;
}
.showSpoiler,
.show-more {
    border-color: #2A2A2A !important;
    background: #1C1C1C !important;
    box-shadow: 0 1px 0 0 rgba(128, 128, 128, .2);
}
#itemsStream {
    background: #a3a3a3 !important;
}
.comments-stream {
    background: none !important;
}
.comments-stream li {
    background: #323232 !important;
    border: 0 !important;
}
.r-block {
    background: #323232 !important;
    border: 0 !important;
}
.button {
    border-color: #2A2A2A !important;
    background: #1C1C1C !important;
    color: #C3C3C3 !important;
}
.button:hover {
    background: #1a1a1a !important;
    color: #C3C3C3 !important;
}
.button.selected {
    background: #702321 !important;
    border: 0 !important;
}
#footer {
    background-color: #323232 !important;
    border-top: 1px solid #161616 !important;
}
}
