css:
@-moz-document domain("app.asana.com") {
/*========================================
Name:			Streamlined Asana
Description:	A streamlined, customizable theme for Asana with more whitespace, team separators (sidebar), and a cleaner task list. Oh, and I've brought ♥s back (choose in Advanced Style Settings below)! Wider custom field columns (if you got 'em) for better usability. Oh, and you get thicker project highlight colors next to project names. Lots o' little things to make your Asana experience better. Updated regularly for the good of all mankind...and my own sanity. ;)
Version:		1.0.5
Author:			Joe Thomas
========================================*/

}

@-moz-document domain("app.asana.com") {
/* Sidebar
========================================*/

.Sidebar {
	padding: 0;
	background-color: /*[[bg-sidebar]]*/;
}

.CustomScrollbarContainer-content {
	width: 244px !important;
}

.SidebarRecentsAndFavoritesCollapsible,
.SidebarReports,
.SidebarTeamMembership,
.Sidebar-browseMoreTeams {
	padding-right: 4px;
	padding-left: 4px;
}

/* Sidebar scrollbar */
.CustomScrollbarContainer-thumb {
	background-color: rgba(255, 255, 255, 0.15);
}

/* Sidebar items
----------------------------------------*/

/* Widen item highlight color. */
.SidebarItemRow,
.SidebarDefaultSearchRow {
	border-left-width: 10px;
	margin: 3px 0;
}

/* Background color of selected item. */
.SidebarItemRow.is-selected {
	background-color: rgba(0, 0, 0, 0.3);
}

/* Change style of hovered and selected project highlight colors. */
.SidebarTeamDetailsProjectsList .SidebarItemRow,
.SidebarRecentsAndFavoritesCollapsible .SidebarItemRow,
.SidebarDefaultSearchRow,
.SidebarTeamDetailsProjectsList .SidebarItemRow.is-selected,
.SidebarRecentsAndFavoritesCollapsible .SidebarItemRow.is-selected,
.SidebarDefaultSearchRow.is-selected {
	-webkit-transition: border-left-width 500ms; /* Safari */
	transition: border-left-width 500ms;
}

.SidebarTeamDetailsProjectsList .SidebarItemRow:hover,
.SidebarRecentsAndFavoritesCollapsible .SidebarItemRow:hover,
.SidebarDefaultSearchRow:hover {
	border-left-width: 6px;
}

.SidebarTeamDetailsProjectsList .SidebarItemRow.is-selected,
.SidebarRecentsAndFavoritesCollapsible .SidebarItemRow.is-selected {
	border-left-width: 16px;
}

.SidebarTeamDetailsProjectsList .SidebarItemRow.is-selected,
.SidebarRecentsAndFavoritesCollapsible .SidebarItemRow.is-selected {
	border-left-width: 16px;
}

.SidebarTeamDetailsProjectsList .SidebarItemRow.is-selected:not(:hover) .SidebarItemRowMenu-button:not(.is-dropdownVisible):after,
.SidebarRecentsAndFavoritesCollapsible .SidebarItemRow.is-selected:not(:hover) .SidebarItemRowMenu-button:not(.is-dropdownVisible):after {
	width: 10px;
	height: 10px;
	content: '';
	margin-right: 2px;
	margin-left: -8px;
	background-image: url(data:image/svg+xml;base64,);
	background-size: 9px 9px;
	background-repeat: no-repeat;
	background-position: right .05em;
}

.SidebarItemRow.is-selected:not(:hover) .SidebarItemRow-icon {
	visibility: hidden;
}

.SidebarTeamDetailsProjectsList .SidebarItemRow:hover .SidebarItemRow-name,
.SidebarRecentsAndFavoritesCollapsible .SidebarItemRow:hover .SidebarItemRow-name,
.SidebarTeamDetailsProjectsList .SidebarItemRow.is-selected .SidebarItemRow-name,
.SidebarRecentsAndFavoritesCollapsible .SidebarItemRow.is-selected .SidebarItemRow-name {
	margin-left: -6px;
}

/* Logo
----------------------------------------*/
.SidebarHeader-asanaLogo {
	background-image: url(/*[[logo]]*/);
	background-size: 175px;
	background-position: 50% center;
	margin-right: 25px;
	padding: 20px 0
}

.SidebarHeader-closeIcon {
	margin-right: -25px;
}

/* Favorites and Recents
----------------------------------------*/

/* Background color. */
.SidebarRecentsAndFavoritesCollapsible {
	padding: 10px 4px 14px;
	background-color: rgba(255, 255, 255, 0.1);
}

/* Override background color of selected projects in Favorites and Recent sections. */
.SidebarRecentsAndFavoritesCollapsible .SidebarItemRow.is-selected {
	background-color: rgba(0, 0, 0, 0);
}

/* Recolor Favorites star. */
.SidebarRecentsAndFavoritesCollapsible .SidebarItemRow .SidebarItemRow-favoriteIcon,
.SidebarRecentsAndFavoritesCollapsible .SidebarItemRow .SidebarItemRowMenu-button:hover .SidebarItemRow-icon {
	fill: /*[[fstar-sidebar]]*/;
}

.SidebarRecentsAndFavoritesCollapsible .SidebarItemRow .SidebarItemRowMenu-button:hover .SidebarItemRow-icon {
	opacity: 0.3;
}

/* Move Special items. */
.SidebarRecentsAndFavorites-special .SidebarDefaultSearchRow {
	padding-left: 19px;
}

/* Reports
----------------------------------------*/

/*[[display-reports]]*/

/* Restyle Reports. */
.SidebarReports {
	margin-top: 0;
	padding: 11px 4px 10px;
	background-color: rgba(255, 255, 255, 0.15);
}

/* Teams
----------------------------------------*/

/* Remove Teams heading. */
.SidebarTeamsList-header {
	display: none;
}

/* Remove separator between teams. */
.SidebarTeamMembership.is-expanded + .SidebarTeamMembership:not(.is-expanded)::after,
.SidebarTeamMembership:not(.is-expanded) + .SidebarTeamMembership.is-expanded::after {
    background-color: transparent;
    height: 0;
}


/* Individual Team
----------------------------------------*/

/* Add background color behind team names. */

.SidebarTeamMembership {
	margin-top: 10px;
}

.SidebarTeamMembership.is-expanded .SidebarTeamMembershipHeader,
.SidebarTeamMembershipHeader {
	padding-top: 7px !important;
	padding-bottom: 5px !important;
}

.SidebarTeamMembership.is-expanded .SidebarTeamMembershipHeader {
	margin-bottom: 10px;
	border-bottom: 2px solid rgba(255, 255, 255, 0.3);
}

.SidebarTeamMembershipHeader-teamName {
	margin-left: 7px;
}

/* Remove invite member ads. */
.SidebarTeamMembersList-inviteAd {
	display: none;
}

/* Projects
----------------------------------------*/

/* Add separator under "PROJECTS" heading. */
.SidebarTeamDetailsProjectsList-header {
	margin-bottom: 10px;
	padding-bottom: 10px;
	border-bottom: 1px solid rgba(255, 255, 255, 0.3);
}

/* Move "Show Archived Projects" link. */
.SidebarTeamArchivedProjectsList-showArchivedProjectsLink {
	margin-left: 22px;
}

/* Change color of indicator when dragging a project to a new position. */
.dropTargetRow.is-hoveringAbove::before,
.dropTargetRow.is-hoveringBelow::after {
	margin-bottom: -1px;
	border-bottom-color: rgba(255, 255, 255, 0.1);
}

.dropTargetRow.is-hoveringAbove::before {
	margin-top: -2px;
}


/* Browse More Teams
----------------------------------------*/

.Sidebar-browseMoreTeams {
	margin-top: 30px;
}
}

@-moz-document domain("app.asana.com") {
/* Top Bar
========================================*/

.topbarContingentUpgradeButton-button.Button--primary,
.topbarContingentUpgradeButton-button.Button--primary:focus:not(.Button--disabled),
.topbarContingentUpgradeButton-button.Button--primary:hover:not(.Button--disabled) {
	padding: 6px 8px 5px;
	background: #fff;
	border-color: #afb4bb;
	font-size: 11px;
	text-transform: uppercase;
	color: #afb4bb;
	fill: #afb4bb;
}

.topbarContingentUpgradeButton-button.Button--primary:focus:not(.Button--disabled),
.topbarContingentUpgradeButton-button.Button--primary:hover:not(.Button--disabled) {
	background: -webkit-linear-gradient(top, #27f5d0, #1ee5d0 10%, #03b4d3 60%);
    background: linear-gradient(to bottom, #27f5d0, #1ee5d0 10%, #03b4d3 60%);
	border-color: transparent;
	box-shadow: none;
	color: #fff;
	fill: #fff;
}
}

@-moz-document domain("app.asana.com") {
/* Asana Main Area (Not Sidebar)
========================================*/

/* Set color/style of indicator line when dragging a task to a new position. */
#asana_main .dropTargetRow.is-hoveringAbove::before,
#asana_main .dropTargetRow.is-hoveringBelow::after {
	margin-top: 0;
	border-bottom: 2px solid rgb(201, 204, 210);
}

#asana_main .dropTargetRow.is-hoveringBelow::after {
	margin-bottom: -1px;
}
}

@-moz-document domain("app.asana.com") {
/* My Tasks
========================================*/

/* Restyle task group headers. */
.group_header {
	padding-top: 8px;
	padding-bottom: 6px;
	background-color: /*[[bg-sectionheader]]*/;
	margin-bottom: 0;
}

.itemListGroupView-zipper {
    margin-right: 5px;
}

/* Fix various border issues in tasks. */
.grid-uncompact #grid .grid_cell_boolean,
.grid-uncompact #grid .grid_cell_string,
.grid-uncompact #grid .grid_cell_schedule,
.grid-uncompact #grid .grid_cell_assignee,
.grid-uncompact #grid .task-row:hover > td,
.grid-uncompact #grid .grid_pseudorow_no_prioritized:hover > td,
.grid-uncompact #grid .bar-row:hover > td {
	border-top-color: transparent;
}

.grid-uncompact #grid .grid_pseudorow_no_prioritized#empty_item_0 .grid_cell_boolean,
.grid-uncompact #grid .grid_pseudorow_no_prioritized#empty_item_0 .grid_cell_string,
.grid-uncompact #grid .grid_pseudorow_no_prioritized#empty_item_0 .grid_cell_schedule,
.grid-uncompact #grid .grid_pseudorow_no_prioritized#empty_item_0 .grid_cell_assignee {
	border-bottom: 1px solid rgb(225, 226, 228);
}

.grid-uncompact #grid .task-row:not(.grid-row-selected) .grid_cell_boolean,
.grid-uncompact #grid .task-row:not(.grid-row-selected) .grid_cell_string,
.grid-uncompact #grid .task-row:not(.grid-row-selected) .grid_cell_schedule,
.grid-uncompact #grid .task-row:not(.grid-row-selected) .grid_cell_assignee {
	border-bottom: 1px solid rgb(225, 226, 228) !important;
}

.grid-uncompact #grid .task-row:hover > td,
.grid-uncompact #grid .grid_pseudorow_no_prioritized:hover > td,
.grid-uncompact #grid .bar-row:hover > td {
	border-bottom-color: transparent;
}

.grid-uncompact #grid .task-row:hover .grid_cell_show_details,
.grid-uncompact #grid .grid_pseudorow_no_prioritized:hover .grid_cell_show_details,
.grid-uncompact #grid .bar-row:hover .grid_cell_show_details {
	border-right-color: transparent;
}

.grid-uncompact #grid .task-row:hover .grid_cell_drag_handle,
.grid-uncompact #grid .grid_pseudorow_no_prioritized:hover .grid_cell_drag_handle,
.grid-uncompact #grid .bar-row:hover .grid_cell_drag_handle {
	border-left-color: transparent;
}

/* Add background color on task when hovered. */
.grid-uncompact #grid .task-row:hover {
	background-color: rgba(232, 247, 251, 0.5);
}

/* Remove white background behind task tags and date when task is hovered. */
.grid-uncompact #grid .task-row:hover .grid-tags-and-date,
.grid-uncompact #grid .task-row:hover .grid-custom-fields {
	background-color: transparent;
}

/* Add list item help text when list is empty. */
.grid-uncompact #grid .grid_pseudorow_no_prioritized#empty_item_0 .grid_cell_string:before {
	display: block;
	padding-left: 20px;
	content: 'Add a task';
	background-image: url(data:image/svg+xml;base64,);
	background-size: 9px;
	background-repeat: no-repeat;
	background-position: left .04em;
	font-size: 14px;
	color: #898e95;
}

.grid-uncompact #grid .grid_pseudorow_no_prioritized#empty_item_0 .grid_cell_string:hover:before {
	color: #595e65;
}
}

@-moz-document domain("app.asana.com") {
/* Task List
========================================*/

/* Restyle task sections. */
.SectionRow {
	background-color: /*[[bg-sectionheader]]*/;
}

.SectionRow-sectionName .TaskName-input,
.SectionRow-sectionName.TaskName-autogrowTextarea {
	box-shadow: none;
}

/* Add background color to task hovers. */
.itemRow--enabled:hover {
	background-color: rgba(232, 247, 251, 0.5);
}

/* Widen task list tags. */
.PotPillsContainer-potPillLink {
	max-width: 85px;
}

/* Widen task list custom field columns (type: Drop-down). */
.CustomPropertyHeader--editable .CustomPropertyHeader-fieldHeading--enum {
	width: 82px;
}
.CustomPropertyEnumEditablePreviewCell {
	width: 100px;
}

/* Change color of stars in custom field columns (type: Drop-down). */
.CustomPropertyEnumEditablePreviewCell span[title^='&starf;'] .Pill,
.CustomPropertyEnumEditablePreviewCell span[title^='&star;'] .Pill {
	color: #ffb900;
}

/* Widen task list custom field columns (type: Text). */
.CustomPropertyHeader--editable .CustomPropertyHeader-fieldHeading--text {
	width: 80px;
}
.CustomPropertyTextEditablePreviewCell-input {
	width: 100px;
}

/* Add list item help text when list is empty. */
.GridTaskList--isEmpty .ItemListHelpText:before {
	display: block;
	padding-left: 20px;
	content: 'Add a task';
	background-image: url(data:image/svg+xml;base64,);
	background-size: 9px;
	background-repeat: no-repeat;
	background-position: left .04em;
}

/* Remove empty line borders (but leave bottom border on first line of empty list). */
.ItemListEmptyLines-row {
	border-top: none;
	border-bottom: none;
}

/* Prevent bottom of task list being at bottom of screen. */
[class*="ProjectPageHeaderView"] + div .GridTaskList,
[class*="MyTasksPageHeaderView"] + div .grid-uncompact {
	margin-bottom: 200px;
}
}

@-moz-document domain("app.asana.com") {
/* Task Details Pane
========================================*/

/* Add monospace styling. */
.field-description code,
.BlockStory-body code,
span[style*="monospace"] {
	padding: 2px 5px 3px;
	background-color: #f0f1f2;
}

/* Fix full links inserted into monospace text as well as possible. */
.field-description code a,
.BlockStory-body code a,
span[style*="monospace"] .textEditor-link,
span[style*="monospace"] + .textEditor-link {
	color: inherit;
	text-decoration: none;
	pointer-events: none;
}

/* Make subtask arrow visible if there is metadata (e.g., comments, description) in subtask. */
.SubtaskTaskRow-detailsButton.SubtaskTaskRow-detailsButton--hasMetadata .SubtaskTaskRow-detailsArrow {
	visibility: visible;
}
}

@-moz-document domain("app.asana.com") {
/* Team Pages
========================================*/

.team-page-projects [class*='potColor-leftBorder-'] {
	border-left-width: 10px;
}

.team-page-projects .list-item {
	margin: 0 5px 5px;
}
}

@-moz-document domain("app.asana.com") {
/* Asana Promotions
========================================*/

.ProjectOverview-promotion {
	display: none;
}
}

@-moz-document domain("app.asana.com") {
