style_setting_options.value:
/* Hearts
========================================*/

/* I'm a cold-hearted snake. Hisssssssssss */

/* Hearts
========================================*/

/* Bring back hearts! Yay! */

a.MiniHeartButton--hearted,
a.MiniHeartButton--withThumbsUp,
.MiniHeartButton.MiniHeartButton--hearted,
.HeartButton--withThumbsUp a, /* In Task Pane Toolbar */
.miniHeartButtonView--withThumbsUp,
.blockStoryView-heartButton {
	position: relative;
}

a.MiniHeartButton--hearted[aria-pressed="true"],
a.MiniHeartButton--withThumbsUp[aria-pressed="true"],
.MiniHeartButton.MiniHeartButton--hearted[aria-pressed="true"],
.HeartButton--withThumbsUp div.CircularToggleButton--toggledOn a, /* In Task Pane Toolbar */
.HeartButton--withThumbsUp div.HeartButton-button--isHearted a, /* In Task Pane Toolbar */
.miniHeartButtonView--withThumbsUp.is-hearted,
.miniHeartButtonView--withThumbsUp.has-hearts,
.blockStoryView-heartButton.is-hearted,
.blockStoryView-heartButton.has-hearts {
	color: rgb(239,46,104);
	fill: rgb(239,46,104);
}

a.MiniHeartButton--hearted:before,
a.MiniHeartButton--withThumbsUp:before,
.HeartButton--withThumbsUp a:before, /* In Task Pane Toolbar */
.HeartButton--withThumbsUp a.CircularButton:before, /* In Task Pane Toolbar */
.miniHeartButtonView--withThumbsUp:before,
.blockStoryView-heartButton:before {
	content: "";
	display: block;
	position: absolute;
	right: 0;
	width: 16px;
	height: 16px;
}

.HeartButton--withThumbsUp a:before, /* In Task Pane Toolbar */
.HeartButton--withThumbsUp a.CircularButton:before /* In Task Pane Toolbar */ {
	width: 30px;
	height: 30px;
}

/* Empty gray heart (when there are no hearts) */
a.MiniHeartButton--hearted[aria-pressed="false"]:before, /* In Task List */
a.MiniHeartButton--withThumbsUp[aria-pressed="false"]:before, /* In Task List */
.SingleTaskPaneToolbar .HeartButton--withThumbsUp a:before, /* In Task Pane Toolbar */
.SingleTaskPaneToolbar .HeartButton--withThumbsUp a.CircularButton:before, /* In Task Pane Toolbar */
.miniHeartButtonView--withThumbsUp:before, /* In task comments and conversations */
.blockStoryView-heartButton:before /* In task comments and conversations */ {
	background-image: url(data:image/svg+xml;base64,);
	background-repeat: no-repeat;
	background-position: 50% 50%;
	background-size: 16px 16px;
}

.SingleTaskPaneToolbar .HeartButton--withThumbsUp a:before, /* In Task Pane Toolbar */
.SingleTaskPaneToolbar .HeartButton--withThumbsUp a.CircularButton:before /* In Task Pane Toolbar */ {
	background-position: 57% 50%;
}

/* White heart (on colored background when there are hearts) */
.SingleTaskPaneToolbar .HeartButton--withThumbsUp div.CircularToggleButton--toggledOn a:before, /* In Task Pane Toolbar */
.SingleTaskPaneToolbar .HeartButton--withThumbsUp div.CircularToggleButton--toggledOn a.CircularButton:before /* In Task Pane Toolbar */ {
	background-image: url(data:image/svg+xml;base64,);
}

.HeartButton-button--withThumbsUp.CircularToggleButton--toggledOff .CircularButton--enabled:focus,
.HeartButton-button--withThumbsUp.CircularToggleButton--toggledOff .CircularButton--enabled:hover {
	box-shadow: 0 0 0 3px rgb(239,46,104);
    color: rgb(239,46,104);
    fill: rgb(239,46,104);
}

.HeartButton-button--withThumbsUp.CircularToggleButton--toggledOn .CircularButton {
	background: rgb(239,46,104);
	border-color: rgb(239,46,104);
}

.HeartButton-button--isHearted ~ .HeartButton-heartCount--withThumbsUp {
	color: rgb(239,46,104);
}

/* Colored heart (when there are hearts) */
a.MiniHeartButton--hearted[aria-pressed="true"]:before, /* In Task List */
a.MiniHeartButton--withThumbsUp[aria-pressed="true"]:before, /* In Task List */
.miniHeartButtonView--withThumbsUp.is-hearted:before, /* In task comments and conversations */
.miniHeartButtonView--withThumbsUp.has-hearts:before, /* In task comments and conversations */
.blockStoryView-heartButton.is-hearted:before, /* In task comments and conversations */
.blockStoryView-heartButton.has-hearts:before /* In task comments and conversations */ {
	background-image: url(data:image/svg+xml;base64,);
	background-repeat: no-repeat;
}

/* Move heart down 1 px. */
.GridTaskList a.MiniHeartButton--hearted:before,
.GridTaskList a.MiniHeartButton--withThumbsUp:before,
.GridTaskList .MiniHeartButton.MiniHeartButton--hearted:before,
.SingleTaskPaneToolbar .HeartButton--withThumbsUp a, /* In Task Pane Toolbar */
.SingleTaskPaneToolbar .HeartButton--withThumbsUp a.CircularButton, /* In Task Pane Toolbar */
.SingleTaskPane .StoryFeed a.MiniHeartButton--hearted:before,
.SingleTaskPane .StoryFeed a.MiniHeartButton--withThumbsUp:before,
.SingleTaskPane .StoryFeed .MiniHeartButton.MiniHeartButton--hearted:before,
.miniHeartButtonView--withThumbsUp:before,
.blockStoryView-heartButton:before {
	transform: translateY(1px);
}

.MiniHeartButton--hearted svg, /* In Task List */
.MiniHeartButton--hearted svg.ThumbsUpIcon, /* In Task List */
.MiniHeartButton--hearted svg.MiniHeartButton-icon, /* In Task List */
.SingleTaskPaneToolbar .HeartButton--withThumbsUp svg, /* In Task Pane Toolbar */
.SingleTaskPaneToolbar .HeartButton--withThumbsUp svg.ThumbsUpLineIcon, /* In Task Pane Toolbar */
.MiniHeartButton--withThumbsUp svg, /* In task comments and conversations */
.MiniHeartButton--withThumbsUp svg.MiniHeartButton-icon, /* In task comments and conversations */
.MiniHeartButton--withThumbsUp svg.ThumbsUpLineIcon, /* In task comments and conversations */
.miniHeartButtonView--withThumbsUp svg, /* In task comments and conversations */
.blockStoryView-heartButton svg, /* In task comments and conversations */
.miniHeartButtonView--withThumbsUp svg.miniHeartButtonView-icon, /* In task comments and conversations */
.blockStoryView-heartButton svg.miniHeartButtonView-icon, /* In task comments and conversations */
.miniHeartButtonView--withThumbsUp svg, /* In task comments and conversations */
.blockStoryView-heartButton svg, /* In task comments and conversations */
.miniHeartButtonView--withThumbsUp svg.miniHeartButtonView-icon, /* In task comments and conversations */
.blockStoryView-heartButton svg.miniHeartButtonView-icon /* In task comments and conversations */ {
	visibility: hidden; /* Hide thumbs up */
}
