css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document domain("oantagonista.com") {

body {

    background: rgb(73, 73, 73);

    color: whitesmoke;

   

    background-repeat: repeat-x;

}

a {

    color: whitesmoke;

}


.new_comment {
    margin-bottom: 40px;
    background: #444444;
    padding: 10px;
}
    
 .form-text-wrapper {
    margin-bottom: 20px;
    background: #676768;
    padding: 5px;   
}
.form-submit {
    font-family: "freight-text-pro", serif;
    font-style: italic;
    font-weight: bold;
    font-size: 30px;
    border: none;
    background: transparent;
    color: #fcfcfc;
    padding: 0;    
}
    
}
