.noprint,
.navbar,
.footer-wrap,
.div-share,
.lyricsh,
.ringtone,
.smt,
br,
form {
    display: none !important;
}

body {
    background: url(https://k36.kn3.net/taringa/5/2/4/7/6/0/8/chloash/9AB.jpg?6454) !important;
    background-size: cover !important;
    background-attachment: fixed !important;
}

body,
body * {
    color: #fff !important;
    font-family: Ubuntu, Verdana, Arial;
    font-size: 1.25em;
    line-height: 1em;
    text-shadow: 2px 2px 5px #000;
}

b {
    font-size: 3em;
    line-height: 200px;
}

.text-center div br {
    display: block !important;
}

.main-page {
    background: rgba(0, 0, 0, 0.8);
    display: table;
    width: 100%;
    height: 100%;
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    padding: 100px;
    box-sizing: border-box;
}

.row {
    width: 100% !important;
    display: table-cell !important;
    height: auto !important;
    vertical-align: middle;
}

.text-center {
    width: 100%;
}
}
