css:
@-moz-document url-prefix("https://www.reddit.com/r/iOSthemes/") {
#header-bottom-left .tabmenu {
    background-color:rgba(255, 255, 255, 1);
	}

.side .titlebox .usertext .md>h1 a {
	color:#4c4c4c;
	}

.side a[href*="#contest"] {
	background: url(http://apt.xtrendence.com/Data/Images/iOSThemesFeaturedWallpaper.jpg) center 100px no-repeat,url(http://apt.xtrendence.com/Data/Images/iOSThemesFeatured.png) center 25px no-repeat;
    color:#6873df;
    font-size:0;
	}

.side a[href*="#contest"]:after {
	background: #ffffff;
    color: #4c4c4c;
    border-radius:20px;
    margin-top:8px;
    content:"Serena";
	}

.side a[href*="#contest"]:hover {
	    background: url(http://apt.xtrendence.com/Data/Images/iOSThemesFeaturedScreenshot.png) center 100px no-repeat,url(http://apt.xtrendence.com/Data/Images/iOSThemesFeatured.png) center 25px no-repeat;
    background-size: 294px,339px;
	}

.side {
    margin: 0;
    padding: 0 10px 0 10px;
    }

.pagename {
	background: url(http://apt.xtrendence.com/Data/Images/iOSThemesName.png);
    background-size:cover;
    height:60px;
	}

#header {
	background: url(http://apt.xtrendence.com/Data/Images/iOSThemesBanner.jpg);
    background-size:cover;
	}

.content {
	margin-top:-2px;
    margin-left:0px;
    margin-right:-15px;
	}

div.content {
	padding-top:0px;
    }

.comments-page .link {
	box-shadow:none;
	}

.loggedin .logout a {
    background: url(http://apt.xtrendence.com/Data/Images/Icons/Logout.png);
    background-size: 20px 20px;
    background-repeat: no-repeat;
    background-position-y: -1px;
    padding-top:1px;
	}

#sr-header-area .selected a {
	color:#551A8B;
	}

.side .titlebox .usertext .md>h1 {
	background-color:#f2f2f2;
    color:#4c4c4c;
    }

.footer {
	background-color:#ffffff;
    }
    
.bottommenu-advertise:after {
	content: '----------This theme was created by Xtrendence under the ABSE (Anyone But Stefan Esser) license----------';
    font-size:15px;
    color:#ffffff;
	}

.flat-vert.title {
	color:#4c4c4c;
    }

.side ol:nth-of-type(2) {
	display:none;
    }

#sr-header-area #sr-more-link {
	background-color:#ffffff;
    color:#4c4c4c;
    }

#sr-header-area .width-clip .dropdown.srdrop .selected.title {
	color:#4c4c4c;
    }

#sr-header-area {
	background-color:#ffffff;
	border-bottom: 1px solid #ffffff;
    }

#header::after {
	background: rgba(255,255,255,0);
    }
    
#sr-header-area a {
	color:#4c4c4c;
    }

.separator {
    visibility:hidden;
	}

#header-bottom-right {
    margin: 0 0 3px 0;
	}

#header-bottom-right a.pref-lang {
    background: url(http://apt.xtrendence.com/Data/Images/Icons/Settings.png);
    background-size: 20px 20px;
    background-repeat: no-repeat;
    background-position-y: -1px;
	}

#mail+.message-count {
	background-color:#551A8B;
    margin-top:5px;
	}

#mail.havemail {
	background: url(http://apt.xtrendence.com/Data/Images/Icons/Inbox.png);
    background-size: 20px 20px;
    background-repeat: no-repeat;
    padding-left:2px;
    padding-right:2px;
    padding-bottom:6px;
    margin-top:5px;
	}

#mail.nohavemail {
    background: url(http://apt.xtrendence.com/Data/Images/Icons/Inbox.png);
    background-size: 20px 20px;
    background-repeat: no-repeat;
    padding-left:2px;
    padding-right:2px;
    padding-bottom:6px;
    margin-top:5px;
	}

.link.promotedlink.promoted, .link.promotedlink.external {
    visibility: hidden;
    display: none;
	}

.organic-listing {
    visibility: hidden;
    display: none;
	}

.titlebox .usertext-body .md h6 {
    opacity: 0;
    visibility: hidden;
    display: none;
	}

#header-bottom-left .tabmenu li a {
    color:#4c4c4c;
    text-shadow: none;
	}

#header-bottom-left .tabmenu li.selected a {
    color: #4c4c4c;
	}

#header-bottom-right a {
    color: #4c4c4c;
	}

body .linklisting .odd, body .linklisting .even {
    background-color: rgba(255, 255, 255, 0.8);
	}
    
body {
	overflow-x:hidden;
    background: linear-gradient(270deg, #a5b984, #9fcabf, #af9fca, #cec6a6, #f5bc8d, #dd7c7c, #e8cd8f, #8a7db0);
background-size: 1600% 1600%;

-webkit-animation: GradientTransition 30s ease infinite;
-moz-animation: GradientTransition 30s ease infinite;
-o-animation: GradientTransition 30s ease infinite;
animation: GradientTransition 30s ease infinite;
}

@-webkit-keyframes GradientTransition {
    0%{background-position:0% 50%}
    50%{background-position:100% 50%}
    100%{background-position:0% 50%}
}
@-moz-keyframes GradientTransition {
    0%{background-position:0% 50%}
    50%{background-position:100% 50%}
    100%{background-position:0% 50%}
}
@-o-keyframes GradientTransition {
    0%{background-position:0% 50%}
    50%{background-position:100% 50%}
    100%{background-position:0% 50%}
}
@keyframes GradientTransition { 
    0%{background-position:0% 50%}
    50%{background-position:100% 50%}
    100%{background-position:0% 50%}
}

.nextprev, .next-suggestions {
	color:#ffffff;
    }

.nextprev a, .next-suggestions a {
	border-radius: 8px;
    color:#4c4c4c;
    background-color:#ffffff;
    border:none;
    padding:8px;
    }
    
.infobar, .content.submit .info-notice {
	background-color:#f2f2f2;
    }
    
.infobar a {
	color:#4c4c4c;
	}

.nav-buttons {
	margin-top:15px;
    margin-left:10px;
    }

.thumbnail.default {
    background: url(http://apt.xtrendence.com/Data/Images/Icons/News.png);
    width: 20px;
    background-size: cover;
    padding-right:30px;
    margin-right:25px;
	}

.thumbnail.self {
    background: url(http://apt.xtrendence.com/Data/Images/Icons/Discussion.png);
    width: 20px;
    background-size: cover;
    padding-right:30px;
    margin-right:25px;
	}

a img {
    border-radius:30px;
    width: 50px;
    height: 50px;
    background-color:#ffffff;
	}

#header-img {
    display:none;
	}
}
