	position: relative;
	cursor: pointer;
	transition:all 0.3s ease;
	width: 360px;
	height: 65px;
	color: #fff;
	background: #222;
	box-shadow: none;
	-webkit-box-shadow: none;
	font-size: 19px;
	font-weight: bold;
	border: 0;
	margin: 20px 0;
   
}
.downloadbtn .bicon{
	float: left;
	width: 65px;
	height: 65px;
	position: absolute;
	display: block;
	margin: 0;
	padding: 0;
	left: 0;
	top: 0;
	background: #222;
	box-shadow: none;
	-webkit-box-shadow: none;
}
.downloadbtn .btext{
	letter-spacing: 0.3px;
	line-height: 65px;
	float: left;
	width: 200px;
	height: 65px;
	position: absolute;
	display: block;
	top: 0;
	left: 110px;
}
.downloadbtn .barrow {
    position: absolute;
    transition: all .2s linear;
    -webkit-transition: all .2s linear; /** Chrome & Safari **/
    -moz-transition: all .2s linear; /** Firefox **/
    -o-transition: all .2s linear; /** Opera **/
}
.downloadbtn .van {
    top: 15px;
    left: 26px;
}
.downloadbtn .vanb {
	position: absolute;
    top: 30px;
	left: 20px;
}
.downloadbtn:hover{
	transition:all 0.5s linear;
	background: #222;
	box-shadow: none;
	-webkit-box-shadow: none;
}
.downloadbtn:hover .move-bot{
    transform: translate(0,8px);
    -webkit-transform: translate(0,8px); /** Chrome & Safari **/
    -o-transform: translate(0,8px); /** Opera **/
