css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("vimawesome.com") {

    body {
        background: #1E1F1F !important;
    }
    
    .install.row-fluid, .tabs-column, .tags-select, .span3.links,
    .span9.dates,.search, .sidebar, .plugin, .plugin-users,
    .github-stars, .category-tags, .pager-button, .site-footer {
        color: #B7BDB5 !important;
        background: #111111 !important;
    }
    
    .row-fluid.long-desc-container, .category-select > a.category-link {
        color: #828681 !important;
        background: #111111 !important;
    }
    
    .from, .tag-link, .tag-count {
        color: #1E1F1F !important;
        background: #B7BDB5 !important;
    }
    
    pre, code {
        color: #1E1F1F !important;
        background: #858A84 !important;
    }
    
    .long-desc pre {
        border-color: -moz-use-text-color -moz-use-text-color -moz-use-text-color #1E1F1F;
    }
    
    .site-footer, .long-desc-container {
       margin-top: 25px;
    }
    
    .old-vundle-notice, .category-link, i, .title a {
        color: #B7BDB5 !important;
    }
    
    .category-link:hover, a.pager-button:hover {
        color: #2AA198 !important;
    }
    
    .pagination ul > li > a.next-page-button:hover:after {
        border-left: 20px solid #2AA198;
    }
    
    .action-btn, .dropdown-toggle, ul.install-tabs > li.active, ul.install-tabs > li:hover {
        color: #2AA198 !important;
        background-color: rgba(147, 161, 161, 0.1) !important;
    }

}
