css:
@-moz-document domain("softuni.bg") {

/*  created by AiKiHack    2017*/
body {
    background-color: #30302F;
    color: #0e81d3;
}
.nav-bar ul li.active .nav-bar-item {
    color: #0e81d3;
}
.nav-bar ul li.hover .nav-bar-item {
    color: #0e81d3;
}
.nav-bar ul li.active .nav-bar-item:after {
    height: 4px;
    background-color: #26a4ff;
}


.content {
    background: #353535;
}
.user-data-wrapper {
    background-color: #202020;
}
.box-info.lead-box .box-body .scroll-holder {
    background-color: #353535;
    color: #8bbfbf;
}

.box-info.lead-box .box-head.calendar-box {
    background: #acbf96;
    color: #234465;
    
}
.box-info.lead-box .box-head {
    background: #77816b;
    color: #234465;
}
.box-info .bottom-bar {
     background-color: #77816b;
}
.box-info .resource-link .icon-link {
    color: #66a1dd;
}
.exam-additional-info .fa {
    color: #66a1dd !important;
}
.box-info .resource-link  {
    color: #66a1dd;
}
.courses-list .info-item .info-link {
   color: #66a1dd;
}
.box-info .info-link {
	color: #66a1dd;
}
.box-info .forum-link-info .user-wrap .user-name {
   color: #66a1dd;
}
.box-info .time-note .category-link {
color: #66a1dd;
}
.box-info .calendar-title {
    background: #202020;
    color: #CFC7C7;
}


p {
    color: #0e81d3;
}
.forum-post {
    background-color: #202020;
}
.forum-post.highlighted-post {
    background: #808080;
    color: #082D53;
}
.highlighted-post > div:nth-child(2) > div:nth-child(2) > p:nth-child(2), .highlighted-post > div:nth-child(2) > div:nth-child(2) > p:nth-child(1) {
    color: #0E3D6E;
}

.notifications-list .notification-item.viewed-item .notification-link::before, .notifications-list-page .notification-item.viewed-item .notification-link::before {
    color: #447cb4;
}
.notifications-wrapper .notification-item .notification-link {
    font-size: 16px;
    color: #5490cc;
}

.dropdown-menu {
    background-color: #202020;
}
.skills-wrap {
    background: #858585;
}
.fastTracksLectureInfo tbody {
    color: #189CFF;
}
.switch-wrap .km-switch {
    background: transparent;
}
.switch-label {
    color: #189CFF;
}
.switch-wrap .km-switch-label-on,
.switch-wrap .km-switch-label-off {
    color: #189CFF;
}
.k-block,
.k-widget {
    background-color: #202020;
}
.k-alt,
.k-pivot-layout>tbody>tr:first-child>td:first-child,
.k-resource.k-alt,
.k-separator {
    background-color: #303030;
}
.k-alt,
.k-pivot-layout>tbody>tr:first-child>td:first-child,
.k-resource.k-alt,
.k-separator {
    background-color: #404040;
}
.k-grid tr.k-alt td,
.k-selectable tr.k-alt td,
.striated-table-wrapper tr.k-alt td {
    background: transparent;
}
.k-tabstrip-wrapper .k-grid {
    color: #7d6666;
}
.grey-container {
    background-color: #202020;
}
.lectures-section .lecture-wrap .lectures-list .lecture .lecture-link {
    background-color: #808080;
    color: #234465;
}
.k-tabstrip .k-tabstrip-items {
    background: #202020;
}
.k-tabstrip .k-tabstrip-items .k-state-default.k-state-active .k-link {
    color: #0e81d3;
}
.k-tabstrip .k-item:after {
    height: 3px;
    background-color: #0e81d3;
}
.k-tabstrip .k-content.k-state-active {
    background-color: #202020;
    color: #676767;
}
.curriculum-profile-section {
    background-color: #202020;
}
/* kalendar */

element.style {
    background-color: #202020 !important;
}
.mv-event-container {
    background: #404040 !important;
    color: grey !important;
    border-color: #777777 !important;
}
.notifications-wrapper .viewed-item .notification-link {
    color: #c2c2c2;
}

 .forum-post .highlighted-post .post-item .post-content p {
    color: #082D53;
}

}

@-moz-document domain("softuni.org") {
/*  created by AiKiHack    2017*/

/*login  softuni.org*/

.navbar-inverse {
    background-color: #656e78;
}
.content {
    background-color: #202427;
    display: inline-block;
    width: 100%;
}
.content .login-container {
    background-color: #181c1e;
}
.white-container {
    background-color: #181c1e;
}
.content .sm-header h2 {
    color: #44A9F8;
}
.white-container input[type="text"], .white-container input[type="email"], .white-container input[type="password"], .white-container input[type="tel"], .white-container textarea {
    color: #44A9F8;
    background-color: #2d2f2d;
}
label.k-checkbox-label, label.checkbox-label {
    color: #9c9a9a;
}

}
