css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.survive-ark.com/taming-calculator/") {
  body{
    background-color: #333;
    color: #eee;
  }
  #header {
    background-color: transparent
  }
  #header-aside {
    background-color: #333;
  }
  #branding {
    background: #333;
  }
  #loop-meta {
    background: transparent;
  }
  
  h1, h2, h3, h4, h5, h6, .title, .titlefont{
    color: #fff;
  }
  
  .box_blue{
    background-color: #444;
    border-color: #000;
    color: #eee;
  }
  .selectedRow {
    background-color: #555;
    color: #f3595b;
  }
  .highlight-typo{
    background-color: #222;
    color: #fff;
  }
  .input-text, textarea, select, input[type="text"], input[type="input"], input[type="password"], input[type="email"], input[type="datetime"], input[type="datetime-local"], input[type="date"], input[type="month"], input[type="time"], input[type="week"], input[type="number"], input[type="url"], input[type="search"], input[type="tel"], input[type="color"], select{
    color: #f3595b !important;
    background-color: #555;
  }
  #ko-input-wrapper{
    border: 2px solid #111;
  }
  #ko-input-wrapper div{
    magin: 0 5px;
    border-color: #111 !important;
    background-color: #555 !important;
  }
  button{
    color: #333;
  }
}
