css:
@-moz-document url-prefix("https://mastodon.xyz/web"), url-prefix("https://mastodon.social/web"), url-prefix("https://social.tchncs.de/web"), url-prefix("https://awoo.space/web"), url-prefix("https://icosahedron.website/web"), url-prefix("https://aleph.land/web"), url-prefix("https://maly.io/web"), url-prefix("https://witches.town/web") {

/*Based on "Mastodon: Flat, Dark and Colourful!" from @KindlyFire@maly.io and "Mastodon style tweaks" from @er1n*/

.button {
    background-color: #A32538;
}
.button:hover,
.button:focus,
.button:active,
.button:disabled {
    background-color: #8b2222;
}
.emoji-row {
    background-color: #222;
}
.emoji-category-header {
    background-color: #111;
}
.emoji-dialog-header {
    background-color: #333 !important;
}
.active {
    background-color: #A32538 !important;
}
li:hover {
    background-color: #A32538 !important;
}
.emoji-category-title {
    color: #ddd !important;
}
.ui {
    background-color: #222;
}
.drawer .drawer__header,
.column-header,
.column-icon {
    background-color: #A32538;
    color: #ddd;
}
.column-icon {
    color: #ddd;
    transition: all 200ms;
}
.column-icon:hover {
    color: #eee;
}
.column-icon.collapsable {
    background-color: #fa5c65;
    color: #eee;
    font-size: 20px !important;
    padding-bottom: 13px !important;
}
.column-settings--outer,
.column-settings--section,
.setting-toggle {
    background-color: #A32538;
    color: #ddd;
}
.setting-text,
.setting-text:hover,
.setting-text:focus {
    color: #ddd;
    border-bottom: 2px solid #ddd;
}
.react-toggle--checked .react-toggle-track,
.react-toggle--checked:hover:not(.react-toggle--disabled) .react-toggle-track {
    background-color: #223;
}
.react-toggle--checked .react-toggle-thumb {
    border-color: #223;
}
.drawer .drawer__header .drawer__tab {
    color: #ddd;
}
.drawer .drawer__header a:hover {
    background-color: #223;
    color: #fb747b;
}
.search__input {
    background-color: #2A2839;
    transition: all 200ms;
    color: #fff;
}
.search__input:hover,
.search__input:focus {
    background-color: #2A2839;
    color: #000;
}
.drawer__inner {
    background-color: #2A2839;
}
.navigation-bar {
    color: #eee;
}
.navigation-bar strong {
    font-size: 14px;
    margin-top: 2px;
}
.search-results__header {
    background-color: #2A2839;
    color: #eee;
    text-transform: capitalize;
}
.drawer__inner.darker {
    background-color: #2A2839;
}
.account__display-name {
    outline: none;
}
.display-name span {
    color: #ddd;
}
.icon-button:hover,
.icon-button:focus,
.icon-button:active,
.icon-button.active {
    color: #fff
}
.status__prepend,
.status {
    background-color: #2A2839;
    color: #fff;
    border-bottom: 0;
}
.notification__message {
    color: #ccc;
}
.status .status__relative-time {
    color: #ccc;
}
.status__content a .fa,
.status__content a .fa:hover {
    color: #A32538;
}
.status__content a,
.reply-indicator__content a,
.getting-started a,
.muted .status__content a,
.account__header .account__header__username {
    color: #A32538;
}
.muted .status__display-name strong {
    color: #ccc;
}
.status__prepend .status__display-name strong {
    color: #fff;
    outline: none;
}
.muted .status__content p {
    color: #ccc;
}
.status__content {
    font-size: 18px !important;
}
.column-link,
.column {
    background-color: #2A2839;
}
.account__action-bar,
.account__action-bar__tab,
.detailed-status__action-bar {
    border: 0;
    background: #2A2839;
}
.autosuggest-textarea__textarea {
    background: #46425e;
    color: #ddd;
}
.emoji:hover {
    background: #A32538 !important;
}
.emojione {
    width: 24px;
    height: 24px;
    margin: -1px !important;
}
.spoiler-input__input {
    background: #46425e;
    color: #ddd;
}
.compose-form__modifiers {
    background: #46425e;
}
.account {
    border-bottom: 0;
}
.column-back-button {
    background-color: #2A2839;
    color: #A32538;
}
.status__content .status__content__spoiler-link,
.reply-indicator__content .status__content__spoiler-link {
    background: #29293d;
}
.autosuggest-textarea__suggestions {
    background: #fff;
}
.autosuggest-textarea__suggestions__item:hover {
    background: #8b2222;
    color: #fff;
}
.autosuggest-textarea__suggestions__item.selected {
    background: #8b2222;
    color: #fff;
}
.reply-indicator {
    background: #29293d;
}
.reply-indicator__content {
    color: #fff
}
.reply-indicator .reply-indicator__display-name {
    color: #fff
}
.search__icon .fa {
    color: #ddd;
    background-color: #2A2839 !important;
}
.detailed-status {
    background: #2A2839;
}
.account__header {
    -webkit-box-flex: 0;
    -ms-flex: 0 0 auto;
    flex: 0 0 auto;
    background: #2A2839;
    text-align: center;
    background-size: cover;
    background-position: center;
    position: relative;
}
.account__header>div {
    background: linear-gradient( to bottom, rgba(34, 34, 51, 0.10), #223);
}
.icon-button.inverted {
    color: #A32538
    /* Bouton photo + confiendalit√© */
    
    ;
    background-color: #2A2839 !important;
}
.icon-button.inverted.active {
    color: #8b2222
    /* Couleur des icones quand tu appuies dessus */
}
.text-icon-button {
    background: #2A2839 !important;
}
.icon-button.inverted:hover,
.icon-button.inverted:active,
.icon-button.inverted:focus {
    color: #8b2222
    /* Couleur quand tu passes la souris sur les icones */
}
.compose-form__buttons {
    background: #2A2839;
}
.text-icon-button.active {
    color: #8b2222
}
.icon-button {
    background: #2A2839 !important;
}
.privacy-dropdown__option {
    background: #2A2839;
    color: #ddd !important;
}
.privacy-dropdown__option.active {
    background: #8b2222;
}
.privacy-dropdown__option:hover {
    background: #8b2222;
}
.privacy-dropdown__option.active:hover {
    background: #8b2222;
}
.button {
    box-shadow: 0px 3px 5px rgba(0, 0, 0, 0.5);
    transition: box-shadow 0.1s ease-in-out;
    transition: transform 0.1s ease-in-out
}
.button:hover {
    box-shadow: 0px 7px 15px rgba(0, 0, 0, 0.5);
    transform: scale(1.05);
}
.button:focus,
.button:active,
.button:disabled {
    box-shadow: 0px 2px 3px rgba(0, 0, 0, 0.7);
    transform: scale(0.93);
}
strong {
    color: #ddd !important;
}
strong:hover {
    color: #A32538 !important;
}
/*Taille des colonnes*/

div.column {
    opacity: 0.9;
    flex-grow: 1;
    flex-shrink: 1;
}
div.drawer {
    opacity: 0.95;
}
}
