css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.yousuu.com") {
  .index-category, .panel, .booklist-item .mod, .sort
  { background-color: #121212 !important; }
  .dropdown-menu, .index-category .row div, .pagination > li > a, .sokk-index-left ul li a, .rate-bar-chart .progress, .panel-primary > .panel-heading, .btn-block, .nav-pills > li, .panel-footer, .panel-default > .panel-heading.ys-index-bd-panel-header, .indexboard .panel-heading, .panel-default > .panel-heading, .alert-info, .sokk-book-button-groups .btn-group .btn-default, .tag, .hidden-xs .btn-block, .modal-footer .btn-default, .ys-editdiv, .categorymain .list td button, .smallcenter > .btn-group > .btn-default, .bg-info, tbody.bookname a.btn-primary, .bg-danger, .ys-booklist-starBtn .btn-default, .ys-booklist-starBtn a.pull-right, .panel-body p .btn-info
  { background-color: #2D2D2D !important; }
  .dropdown-menu > li > a:focus, .dropdown-menu > li > a:hover, .navbar-brand > img, .index-category a:hover, .pagination > .active > a, .pagination > .active > a:focus, .pagination > .active > a:hover, .pagination > .active > span, .pagination > .active > span:focus, .pagination > .active > span:hover, .sokk-index-left ul li.active a, .nav > li > a:focus, .nav > li > a:hover, .nav-pills > li.active > a, .nav-pills > li.active > a:focus, .nav-pills > li.active > a:hover, .close:focus, .close:hover, .ys-comment-reply .pull-right, .close, button.close, .modal-header .close, .categorymain .list .btn-primary, .btn-group > .btn-primary, .navbar-default .navbar-nav > .active > a, .navbar-default .navbar-nav > .active > a:focus, .navbar-default .navbar-nav > .active > a:hover, .ys-index-bdlist > li.on .vobes .btn-default, .login-form .btn-success, .login-form .btn-primary, .indexboard > .collapse .btn-primary, .bg-danger .btn-primary
  { background-color: #CCCCCC !important; }
  .navbar-default, .table > thead > tr > th, #next_comment_btn .btn, .bookname tr:hover, .booklist-item .booklist-item-opt .btn-default
  { background-color: #3E3E3E !important; }
  .table-striped > tbody > tr:nth-of-type(2n+1), .common-footer .footer, .thumbnail, .booklist-item .bd, .booklist-item .ft, .booklist-item blockquote, .modal-content
  { background-color: #1F1F1F !important; }
  .ys-userpage-main
  { background-color: transparent !important; }
  .ys-userpage-main, .nav-pills > li.active > a, .nav-pills > li.active > a:focus, .nav-pills > li.active > a:hover, .indexboard .panel-heading, .panel-default > .panel-heading
  { background-image: none !important; }

  .rate-bar-chart .progress:nth-of-type(1) .progress-bar-primate
  { background-color: #0047ab !important; }  
  .rate-bar-chart .progress:nth-of-type(2) .progress-bar-primate
  { background-color: #802456 !important; }  
  .rate-bar-chart .progress:nth-of-type(3) .progress-bar-primate
  { background-color: #FF0000 !important; }  
  .rate-bar-chart .progress:nth-of-type(4) .progress-bar-primate
  { background-color: #805b00 !important; }  
  .rate-bar-chart .progress:nth-of-type(5) .progress-bar-primate
  { background-color: #00b500 !important; }
  
  [color="#000"], [color="#000000"], [color="Black"], .booklist-item .booklist-subject .abstract
  { color: #AAAAAA !important; }
  .rate-bar-chart .progress .progress-bar-hidden, .booklist-item blockquote span, .common-footer .copy, .ys-userpage-leftpane a span, .bookname tr:hover td, .indexboard .panel-heading .hidden-xs, .panel-default > .panel-heading, .alert-info, .input-group-addon, .thumbnail small.pull-right, #next_comment_btn a, .bg-info
  { color: #FFFFFF !important; }
  .dropdown-menu > li > a:focus, .dropdown-menu > li > a:hover, .index-category a:hover, .pagination > .active > a, .pagination > .active > a:focus, .pagination > .active > a:hover, .pagination > .active > span, .pagination > .active > span:focus, .pagination > .active > span:hover, .sokk-index-left ul li.active a, .nav > li > a:focus, .nav > li > a:hover, .btn-warning, .nav-pills > li.active > a, .nav-pills > li.active > a:focus, .nav-pills > li.active > a:hover, .ys-index-bdlist > li.on .btn, .booklist-item .bd .pull-right a, .booklist-item .bd .pull-right a:hover, .close:focus, .close:hover, .close, button.close, .modal-header .close, button.pull-right, .categorymain .list .btn-primary, .btn-group > .btn-primary, .navbar-default .navbar-nav > .active > a, .navbar-default .navbar-nav > .active > a:focus, .navbar-default .navbar-nav > .active > a:hover, .indexboard > .collapse .btn-primary, .btn-danger, .bg-danger .btn-primary
  { color: #000000 !important; }
  .ys-index-bd-panel-header a, .sokk-book-button-groups .btn-group .btn-default, .hidden-xs .btn-block, .fa.pull-right, .categorymain .list td button, .smallcenter > .btn-group > .btn-default, .ys-editdiv, .ys-booklist-starBtn .btn-default, .panel-body p .btn-info, .booklist-item .booklist-item-opt .btn-default
  { color: #E0BC2D !important; }

  [color="#00F"], [color="#0000FF"], [color="Blue"], [color="Navy"], [color="#000080"], [color="#0000F0"], [color="#0000ee"], .media-body span.small, .booklist-item .ft .time span, .ys-comments-main .media-heading small, .media-heading small, .ys-notice .small, table.table-condensed > tbody > tr > td:nth-of-type(3)
  { color: #3399FF !important; }
  .media-heading, .ys-index-bdlist > li > p.pnum .small
  { color: #2FC95C !important; }
  .ys-blackbtn, .modal-footer .btn-default, .bg-danger
  { color: #E3232A !important; }

  .indexboard .panel-heading
  { padding: 1px !important; }
  .num2star .fa
  { font-size: 14px !important; }

  body * { font-family: "文泉驛微米黑", "WenQuanYi Micro Hei", "Droid Sans Fallback", "Noto Sans CJK TC Regular", "Microsoft YaHei UI", "Microsoft YaHei", "微软雅黑", Sans-serif; }
  body * { text-decoration: none !important; text-shadow: none !important; box-shadow: none !important; }
  html, body { color: #AAAAAA; background: #121212 !important; background-color: #121212 !important; background-image: none !important; }
  body h1, body h2, body h3, body h4, body h5, body h6, body ul, body li, body ol, body span, body p, body dt, body dd, body dl { background-color: transparent !important; }
  body h1, body h2, body h3, body h4, body h5, body h6, body th, body em, body b, body strong, label { color: #FFFFFF; }
  body div, body p  { color: #AAAAAA; }
  body blockquote { color: #AAAAAA !important; background-color: #2D2D2D !important; background-image: none !important; }
  body textarea { color: #E0BC2D !important; background-color: #2D2D2D !important; background-image: none !important; }
  body table, body tr, body td { background-color: transparent; color: #AAAAAA; background-image: none !important; }
  body select, body option { background-color: #2D2D2D !important; color: #E0BC2D !important; }
  body input { color: #E0BC2D !important; background-color: #2D2D2D !important; }
  body button, body input[type="button"], body input[type="submit"] { color: #000000 !important; background-color: #CCCCCC !important; }
  body a { color: #E0BC2D !important; }
  body a:link { color: #E0BC2D; }
  body a:visited { color: #AAAAAA; }
  body a:hover { color: #3399FF !important; }
  body a:active { color: #423F3F !important; }
  lightbox * { background: transparent !important; background-color: transparent !important; }
}
