css:
@-moz-document domain("www.gotoshop.net.ua"), domain("gotoshop.ua"), domain("skidkaonline.ru"), domain("gotoshop.pl") {

header.navbar,
header.navbar .container,
header.navbar .container .collapse.navbar-collapse,
header.navbar .container .collapse.navbar-collapse ul.nav.navbar-nav,
header.navbar .container .collapse.navbar-collapse ul.nav.navbar-nav > li,
header.navbar .container .collapse.navbar-collapse ul.nav.navbar-nav > li > a {
    min-height: 20px !important;
    max-height: 20px !important;
    line-height: 20px !important;
}
header.navbar .container .collapse.navbar-collapse ul.nav.navbar-nav > li > a {
    margin: 0 10px;
    padding: 0;
}
header.navbar .container .collapse.navbar-collapse ul.nav.navbar-nav > li > a > span {
    margin: 0 5px;
}
/* .mynotes-count.badge {
    position: initial !important;
} */

#header {
    margin: 0;
    padding: 0;
    width: 100%;
}
#header,
#header .container,
#header .container .selectors-wrapper,
#header .container .selectors-wrapper .selectors,
#header .container .selectors-wrapper .selectors .selector,
#header .container .selectors-wrapper .selectors .selector .dropdown a.dropdown-toggle {
    min-height: 28px;
    max-height: 28px;
}
#header .container .selectors-wrapper .selectors .selector,
#header .container .selectors-wrapper .selectors .selector .dropdown,
#header .container .selectors-wrapper .selectors .selector .dropdown .btn-group-search,
#header .container .selectors-wrapper .selectors .selector .dropdown .btn.dropdown-toggle {
    margin: 0;
    padding: 0;
    min-height: 20px;
    max-height: 20px;
}
#header .container .selectors-wrapper .selectors .selector {
    margin-right: 20px;
}
#header .container .selectors-wrapper .selectors .selector label {
    display: none;
}
#header .container .selectors-wrapper .selectors .selector .dropdown a.dropdown-toggle span {
    vertical-align: inherit;
    min-height: 20px;
    max-height: 20px;
}
#header .logo {
    display: none;
}
#header .selectors-wrapper {
    margin: 0;
    padding: 0;
}
.fixed-tabs,
.overlay {
    display: none !important;
}
.shops-slider-wrapper .container {
    width: 100%;
}
.shops-slider-wrapper .selected-shop {
    display: none;
}
#content .main-title {
    display: none;
}
#content .container {
    width: 100%;
}
#content .form-filters {
    padding: 5px 0;
    min-height: 30px;
    max-height: 30px;
}
#content .form-filters .btn.btn-default,
#content .form-filters .btn.btn-link {
    min-height: 20px;
    max-height: 20px;
    padding: 0 5px;
}
.form-filters .pull-left,
.form-filters .pull-right {
    padding: 0;
}
#content .discounts .item {
    transition: none;
}
#content .discounts .item .info {
    position: absolute;
    top: 20px;
    left: 0;
    width: 100%;
}
#content .discounts .item .info,
#content .discounts .item .info .shops,
#content .discounts .item .info .shops a {
    min-height: 0px;
    max-height: 0px;
    padding: 0;
    margin: 0;
}
#content .discounts .item .info .discount-link {
    display: none;
}
#content .discounts .item .image-link {
    padding-top: 20px;
}
#content .discounts .item .info .shops a img {
    position: absolute;
    top: 0;
    left: 0;
    height: 50px;
    width: 50px;
    margin: 5px;
    background: white;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.5);
    border-radius: 50px;
    z-index: 5;
    opacity: 0.2;
    transition: all 0.2s ease-in-out 0.2s;
}
#content .discounts .item .info .shops a img:hover {
    opacity: 1;
    border-radius: 5px;
    width: 70px;
    height: 70px;
}
#content .discounts .item .info .btn-add-note {
    position: absolute;
    top: 0;
    right: 0;
    z-index: 5;
    margin: 0;
    padding: 5px;
}
#content .discounts .item .image-link .over {
    display: none;
}
#content .discounts .item .product-actions {
    padding: 0;
    border-top: 1px solid #777;
    background: rgba(0, 0, 0, 0.05);
}
#content .discounts .item .discount-days {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 20px;
    line-height: 20px;
    background-size: 15px 15px;
    background-position: 5px 2px;
    font-size: 12px;
    padding-left: 0;
    text-align: center;
}
.hidden-print.hidden-xs,
.share-item {
    display: none;
}
html,
body,
#wrapper,
#content {
    height: 100%;
}
}
