css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forecast.weather.gov")
{
  body
  {
    width: 900px !important;
    margin: 0 auto !important;
    font-size: 0.8em !important;
  }

  body,
  body > table[background="/images/wtf/navbkgrnd.gif"] *,
  body > table[background="/images/wtf/navbkgrnd.gif"] + table *
  {
    font-family: "DejaVu Sans", sans-serif !important;
  }

  body > table[background="/images/wtf/topbanner.jpg"] + table *
  {
    font-family: Delicious, sans-serif !important;
  }

  /* background color */
  body { background: #a1a1a1 !important; }
  body > table[background="/images/wtf/navbkgrnd.gif"] + table > tbody > tr:first-child + tr > td:first-child > table:first-child { border-color: #a1a1a1 !important; }

  .blue1, .big { color: #144FA0 !important; }
  .blue1 { font-size: 120% !important; }

  body *
  {
    font-family: inherit;
  }

  body > table
  {
    width: 100% !important;
  }

  body > table[background="/images/wtf/topbanner.jpg"]
  {
    display: none !important;
  }

  body > table[background="/images/wtf/topbanner.jpg"] + table
  {
    display: block !important;
    margin: 12px 0 0 0 !important;
    -moz-border-radius: 12px 12px 0 0;
    background: url(http://i43.tinypic.com/9tl7ad.jpg) #144FA0 !important;
    color: #fff !important;
    font-size: 280% !important;
  }

  body > table[background="/images/wtf/topbanner.jpg"] + table tr[valign="top"]
  {
    display: none !important;
  }

  td[background="/images/wtf/forecast_blank.jpg"]:before
  {
    content: "Your National Weather Service Forecast";
    display: block !important;
    font-size: 35% !important;
  }

  td[background="/images/wtf/forecast_blank.jpg"]
  {
    display: block !important;
    width: 852px !important; /* not sure why we have to set this manually */
    height: auto !important;
    padding: 24px !important;
    background: none !important;
    font-size: 160% !important;
    text-align: center !important;
    text-shadow: 0 0 16px #a1a1a1 !important;
  }

  /* hide images */
  body > table[background="/images/wtf/topbanner.jpg"] + table img,
  body > table[background="/images/wtf/topbanner.jpg"] + table + table img,
  img[src="/images/wtf/fcst.jpg"],
  img[src="/images/wtf/currentconds_down.jpg"],
  img[src="/images/wtf/radandsat.jpg"],
  img[src="/images/wtf/dpoint_forecast_up.jpg"],
  img[src="/images/wtf/ndfd-banner.jpg"],
  img[src="/images/wtf/addinfosml.jpg"]
  {
    display: none !important;
  }

  /*img[src="/images/wtf/currentconds_down.jpg"] + map + table 
  {
    display: block !important;
    position: relative !important;
    padding-top: 120px !important;
    margin-left: 40px !important;
  }

  .big
  {
    display: block !important;
    position: absolute !important;
    top: 0 !important;
    left: 80px !important;
    right: 120px !important;
    width: auto !important;
    color: #5E5E5E !important;
    font-size: 120% !important;
    text-align: center !important;
  }*/

  a[href$="&FcstType=kml"]/*,
  .noprint*/
  {
    display: none !important;
  }

  /* "Enter your ZIP" table... */
  body > table[background="/images/wtf/navbkgrnd.gif"]
  {
    display: block !important;
    margin: 1px 0 !important;
    background: #5E5E5E !important;
    color: #fff !important;
    text-align: center !important;
  }

  td.searchinput
  {
    display: block !important;
    width: 100% !important;
    padding: 4px !important;
    background: none !important;
    text-align: center !important;
  }

  body > table[background="/images/wtf/navbkgrnd.gif"] .yellow
  {
    color: #fff !important;
  }

  /* main table */
  body > table[background="/images/wtf/navbkgrnd.gif"] + table
  {
    background: #fff !important;
  }

  body > table[background="/images/wtf/navbkgrnd.gif"] + table > tbody > tr:first-child
  {
    display: none !important;
  }

  /* 7-day forecast */
  /*body > table[background="/images/wtf/navbkgrnd.gif"] + table > tbody > tr:first-child + tr > td:first-child > table:first-child
  {
    display: block !important;
    width: 900px !important;
    margin: 0 -50px !important;
    padding: 12px !important;
    background: #fff !important;
  }

  body > table[background="/images/wtf/navbkgrnd.gif"] + table > tbody > tr:first-child + tr > td:first-child > table:first-child + table
  {
    margin-top: 172px !important;
  }*/

  body > table[background="/images/wtf/navbkgrnd.gif"] + table > tbody > tr:first-child + tr > td:first-child > table:first-child
  {
    width: 902px !important;
    margin: 0 -4px 0 -4px !important;
    padding: 4px !important;
    border-bottom-width: 1px !important;
    border-bottom-style: solid !important;
  }

  td,
  th
  {
    font-size: 90% !important;
  }
}
