css:
@-moz-document domain("darklate.ru") {
/*AGENT_SHEET*/
*
{
    margin: 0;
    padding: 0;
    border: 0;
    outline: 0;
    font-size: 100%;
    vertical-align: baseline;
    background: transparent;
    -webkit-box-sizing: border-box;
    box-sizing: border-box
}

html
{
    font: normal 16px/1.4 sans-serif;
    line-height: 1
}

ol,
ul
{
    list-style: none
}

blockquote,
q
{
    quotes: none
}

blockquote:before,
blockquote:after,
q:before,
q:after
{
    content: '';
    content: none
}

:focus
{
    outline: 0
}

ins
{
    text-decoration: none
}

del
{
    text-decoration: line-through
}

table
{
    border-collapse: collapse;
    border-spacing: 0
}

p[style="text-align: center;"] > span[style="color: #ffffff;"]
{
    font-size: 20px!important;
    border: solid #000 1px!important;
    padding: 10px!important
}

table,
td
{
    border: 1px solid grey!important;
    border-collapse: collapse!important;
    padding: 10px!important;
    margin: auto!important
}

[id="vk-comments"],
[style="max-width:600px"]
{
    width: auto!important;
    max-width: 100%!important
}

#page
{
    width: auto!important
}

#content
{
    background-color: #EBE5D7!important
}

[style="color: #ffffff;"]
{
    color: #000!important
}

strong > a
{
    color: green!important;
    font-family: caption!important;
    font-style: italic!important
}

[id="secondary"] > *
{
    width: 275px!important;
    filter: opacity(0.5)!important;
    -webkit-filter: opacity(0.5)!important;
    -ms-filter: "progid:DXImageTransform.Microsoft.Alpha(opacity=50)"!important;
    -moz-opacity: .5!important;
    -khtml-opacity: .5!important;
    opacity: .5!important
}

[id="secondary"]:hover > *
{
    width: 275px!important;
    filter: opacity(1)!important;
    -webkit-filter: opacity(1)!important;
    -ms-filter: "progid:DXImageTransform.Microsoft.Alpha(opacity=100)"!important;
    -moz-opacity: 1!important;
    -khtml-opacity: 1!important;
    opacity: 1!important
}

iframe[style*="position:absolute"],
[id="google_ads_frame3"],
[class="googlepublisherpluginad"],
[class="wcommunity_wrap "],
[id="community_header"],
[id="adswidget1-quick-adsense"],
[style="width: 239px;"],
[id="adswidget1-quick-adsense"],
[id="adswidget2-quick-adsense"]
{
    display: none!important
}

[id="primary"]
{
    float: left!important
}

[id="primary"] > *
{
    width: auto!important;
    max-width: 100%!important
}

p > a > img
{
    margin: 0!important;
    padding: 0!important;
    width: 30%;
    height: 30%
}

div.textBook,
[id="primary"],
p
{
    margin: 0 auto!important;
    /*font-family: 'PT Serif', 'Liberation Serif', 'Bookman Old Style', 'Times New Roman', 'Lora', Georgia, 'Palatino Linotype', 'Book Antiqua', Palatino, Times, serif!important;*/
    font-family: 'PT Sans', 'Liberation Sans', 'Open Sans', 'Helvetica Neue', Helvetica, Arial, Tahoma, Verdana, sans-serif !important;
    font-size: 18px!important;
    text-indent: 20px!important;
    word-wrap: break-word!important;
    line-height: 1.25!important;
    text-align: justify!important;
}

div.numberPage
{
    margin: 20px auto!important;
    font-family: 'PT Sans', 'Liberation Sans', 'Open Sans', 'Helvetica Neue', Helvetica, Arial, Tahoma, Verdana, sans-serif !important;
    color: #9a9a9a!important
}

.textBook h2 a
{
    font-family: 'PT Sans', 'Liberation Sans', 'Open Sans', 'Helvetica Neue', Helvetica, Arial, Tahoma, Verdana, sans-serif !important;
    color: #0707f7!important
}

p
{
    padding-bottom: 15px!important
}

h1,
h2
{
    font-family: 'PT Sans', 'Liberation Sans', 'Open Sans', 'Helvetica Neue', Helvetica, Arial, Tahoma, Verdana, sans-serif !important;
    font-size: 24px!important;
    text-shadow: 1px 2px 2px #555, 0 0 5px #aaa!important;
    line-height: 2!important;
    text-align: center!important
}

h3,
h4,
h5,
h6
{
    font-family: 'PT Sans', 'Liberation Sans', 'Open Sans', 'Helvetica Neue', Helvetica, Arial, Tahoma, Verdana, sans-serif !important;
    font-size: 22px!important;
    line-height: 2!important;
    text-shadow: 1px 2px 2px #555, 0 0 5px #aaa!important
}

hr
{
    margin: 20px 0!important;
    height: 1px!important;
    border: none!important;
    background: -webkit-gradient(linear, left top, right top, from(transparent), color-stop(rgba(0, 0, 0, 0.87)), to(transparent))!important;
    background: -webkit-linear-gradient(left, transparent, rgba(0, 0, 0, 0.87), transparent)!important;
    background: -o-linear-gradient(left, transparent, rgba(0, 0, 0, 0.87), transparent)!important;
    background: -webkit-gradient(linear, left top, right top, from(transparent), color-stop(rgba(0, 0, 0, 0.87)), to(transparent))!important;
    background: linear-gradient(to right, transparent, rgba(0, 0, 0, 0.87), transparent)!important
}
}
