@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("vsetop.com"), domain("vsetop.org") {

	@font-face {
  		font-family: 'Roboto';
  		src: local('Roboto'), /* in case the font is installed */
		url(https://fonts.googleapis.com/css?family=Roboto:400,500,700,900&amp;subset=cyrillic) format('ttf');
	}

	html,
	body {
    	background: url("http://search.png") !important;
		z-index: 1;
	}
	
    html,
    body,
    #wrapper1,
    #sidebar {
        font-family: 'Roboto', sans-serif !important;
		background: #e8e8e8 url("http://search.png") !important;
    }
    * h1,
    h1 * {
        font-family: 'Roboto', sans-serif !important;
        font-weight: bold !important;
        font-style: normal !important;
        text-transform: uppercase;
    }
    .logo {
        display: none;
    }
    #header {
        background: #546E7A;
        width: 182%;
        height: 200px;
        margin-left: -440px;
        box-shadow: 0 2px 4px 0 rgba(0, 0, 0, 0.25);
    }
    .body div,
    .user_comment {
        font-size: 14px;
    }
	.user_comment {
        padding-top: 20px !important;
    }
    .naviga {
        font-family: 'Roboto', sans-serif !important;
        background: none;
        width: 130%;
        height: 30px;
        margin-top: -30px;
        border: 0px;
        box-shadow: none;
    }
    .naviga li h2 {
        color: white;
        text-align: center;
        font-family: 'Roboto', sans-serif !important;
        font-weight: bold;
        font-style: normal;
        font-size: 14px;
        text-transform: uppercase;
        margin-bottom: 0px;
        margin-left: 160px;
        line-height: 16px;
        width: 95px;
        height: 28px;
        padding-right: 15px;
    }
    .naviga li h2:hover {
        border-bottom: 3px solid white;
    }
    .naviga a,
    .naviga li {
        border: 0px;
    }
    .naviga a:hover,
    .naviga li:hover {
        background: none;
        border: 0px solid;
    }
    #main {
        width: 980px;
        margin-top: 30px;
    }
    #main h1 {
        margin-left: 0px;
    }
    #main p {
        font-size: 12px;
    }
    .news1 {
        background: white;
        border: 0px;
        border-radius: 3px;
        box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.25);
    }
    .news1:hover {
        box-shadow: 0 2px 5px rgba(0, 0, 0, 0.3);
    }
    .news1 .bottombody {
        font-weight: bold;
        font-size: 1.1em;
        text-transform: uppercase;
        margin-top: 20px !important;
        padding: 0 12px 5px 0px !important;
        margin-right: 30px;
    }
    .news1 .bottombody a {
        color: white;
        border-radius: 2px;
        padding: 13px;
        background: #546E7A;
    }
    .news1 .bottombody a:hover {
        background: #37474F;
        text-decoration: none;
    }
    .body > div > div > img {
        width: 900px !important;
    }
    .body > div > div > a > img {
        width: 900px;
    }
    .lightbox img {
    	width: 430px !important;
        margin-bottom: -10px !important;
        margin-top: 0 !important;
    }
    .shadow {
        border: 0px;
    }
    #search {
        background: transparent url("http://search.png");
        position: absolute;
        width: 640px;
        margin-left: 6%;
        margin-top: -138px;
    }
    #search:before {
        content: url(http://i.imgur.com/ndV37SG.png);
        position: absolute;
        margin-left: 230px;
        margin-top: -70px;
    }
    #search h3 {
        display: none;
    }
    .textbox {
        color: #546E7A;
        font-family: 'Roboto', sans-serif !important;
        font-weight: 700;
        font-size: 14px;
        padding-left: 15px !important;
        padding-top: 15px;
        padding-bottom: 15px;
        background: #B0BEC5;
        border: 0px;
        border-top-left-radius: 3px;
        border-bottom-left-radius: 3px;
    }
    .textbox:focus {
        background: rgba(236, 239, 241, 1.0);
    }
    input[type="submit"] {
        background: white;
        color: #546E7A;
        font-weight: 700;
        text-transform: uppercase;
        cursor: pointer;
        border: 0px;
        border-top-right-radius: 3px;
        border-bottom-right-radius: 3px;
        margin-left: -22px;
        width: 75px;
        height: 47px;
    }
    input[type="submit"]:hover {
        color: white;
        background: #445963;
    }
    /*Right menu*/
    .inside2 {
        color: black;
        background: white !important;
        width: 250px;
        position: relative;
        margin-left: -6px;
        margin-top: -50px;
        border: 0px !important;
    }
    #gray li a,
    #red li a,
    #orange li a,
    #blue li a {
        color: black;
        border: 0px solid !important;
    }
    #gray,
    #red,
    #orange,
    #blue,
    .inside1,
    .inside2 {
        background: url("http://gray1.png") !important;
    }
    .inside2 h2 {
        color: black;
        background: url('http://bg.png') !important;
    }
    .inside2 a {
        color: black !important;
        font-size: 14px;
    }
    #orange .inside2 a {
        font-family: 'Roboto', sans-serif !important;
        font-size: 15px;
        margin-left: 0px;
    }
    #orange .inside2 {
        font-family: 'Roboto', sans-serif !important;
        font-size: 15px;
        margin-left: 26px;
    }
    #orange h2 {
        margin-left: -13px !important;
        margin-bottom: 10px;
    }
    #gray h2,
    #red h2,
    #orange h2,
    #blue h2 {
        color: #546E7A;
        text-transform: uppercase;
        text-align: center;
        font-weight: bold;
        font-size: 17px !important;
        border-bottom: 1px solid #ced7db;
        border-radius: 0px;
        background: transparent !important;
        width: 200px;
        margin-left: 19px;
        padding-right: 0px;
    }
    #gray,
    #red,
    #orange,
    #blue {
        color: black;
        height: 270px;
    }
    #red,
    #blue {
        display: none;
    }
    #sidebar {
        color: black;
        background: white !important;
        position: absolute;
        border-radius: 3px;
        margin-top: 30px;
        margin-left: 1030px;
        box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.25);
        height: 400px;
    }
    /*Right menu end*/
    .button_download {
		background: rgb(38,200,98);
        margin-left: 42px !important;
		border-radius: 3px !important;
		padding: 0px 15px !important;
    }
	.button_download::before {
		position: absolute;
		background: #2e7d32;
        width: 61px;
        height: 31px;
        border-top-left-radius: 3px;
        border-bottom-left-radius: 3px;
        padding-top: 9px;
        margin-left: -56px;
        content: url('http://i.imgur.com/EQsDG3W.png');
		text-align: center;
		cursor: pointer;
    }
    .button_download a {
        font-weight: bold;
        text-decoration: none !important;
        margin-top: 20px;
        padding-top: 10px;
        padding-bottom: 10px;
        background: transparent;
        width: 200px !important;
        box-shadow: none;
    }
    .button_download:hover {
        background: #07d571;
    }
    .button_download:hover .leftbt {
        margin: 0px;
    }
    .stombody {
        padding: 12px;
        text-align: left !important;
        margin-top: 20px;
        margin-bottom: -50px !important;
    }
    .stombody a::before {
        position: absolute;
        background: #8d3836;
        content: url('https://i.imgur.com/vBJCJxJ.png');
        margin-left: -84px;
        margin-top: -11px;
        padding: 6px 17px;
        border-bottom-left-radius: 3px;
        border-top-left-radius: 3px;
    }
    .stombody a {
        padding-top: 11px;
        padding-left: 50px;
        margin-left: 256px !important;
        margin-top: -72px;
        text-align: center;
        background: #ef5350;
        border-radius: 3px;
        width: 300px;
        height: 30px;
        text-decoration: none;
        display: block;
        color: white !important;
    }
    .stombody a:hover {
        background: #db756c;
    }
    .lightbox[href*='/donat.html'] {
    	background: rgb(55,145,187) !important;
        padding: 11px 127px !important;
        color: #fff !important;
		border: 0 !important;
        margin-left: -298px !important;
        font-size: 16px !important;
        border-radius: 3px !important;
    }
    .lightbox[href*='/donat.html']:hover {
    	background: rgb(98,165,196) !important;
    }
    .lightbox[href*='/donat.html']::before {
        position: absolute;
    	background: rgb(67,97,111);
        content: url('https://i.imgur.com/XkXKUY7.png');
        margin-left: -127px;
        margin-top: -10px;
        padding: 4px 18px;
        padding-top: 8px;
        border-bottom-left-radius: 3px;
        border-top-left-radius: 3px;
    }
	a[href*='www.donationalerts.ru/r/vsetop'] {
		background: rgb(63,153,204) !important;
		border: 0 solid !important;
		border-radius: 3px !important;
		padding: 15px 45px !important;
		font-size: 25px !important;
	}
	a[href*='www.donationalerts.ru/r/vsetop']:hover {
		background: rgb(74,202,97) !important;
	}
    #footer .inside2 {
        position: absolute;
        margin-left: -7px;
        margin-top: 36px;
    }
    #footer ul {
        color: white !important;
        width: 300px !important;
        margin-top: -40px;
    }
    #footer {
        display: none;
    }
    table.addcomment input[type='submit'] {
        color: white;
        background: #546E7A;
        margin-left: 825px !important;
        margin-top: 10px !important;
        border-radius: 3px;
        width: 150px;
        box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.25);
    }
    table.addcomment input[type='submit']:hover {
        color: white;
        background: #445963;
    }
    table.addcomment input[id='allow_subscribe'] {
        margin-left: 180px;
    }
    #userinfo {
        background: white;
        border-radius: 3px;
        box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.25);
        height: 487px;
    }
    #userinfo h1 {
        padding-left: 189px;
        padding-top: 125px;
        padding-bottom: 35px;
    }
    .userinfo {
        margin-left: 125px !important;
        height: 300px !important;
        font-size: 13px !important;
    }
    .userinfo img {
        margin-left: -50px !important;
    }
    #options {
        background: white;
        border-radius: 3px;
        box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.25);
    }
    .userinfo .textbox {
        width: 300px !important;
        border-radius: 3px !important;
    }
