        color: white;
        background: #546E7A;
        width: 100px;
        border-radius: 3px;
    }
    .userinfo .red {
        width: 100px !important;
        padding-right: 300px;
    }
    .jquery-lightbox-mode-html .jquery-lightbox-background {
        background: #FFFFFF;
        box-shadow: 0px 0px 4px 0px black !important;
        border-radius: 2px !important;
        
	}
    .jquery-lightbox-html {
        border: 0px !important;
        margin-left: 70px !important;
        margin-top: 70px !important;
        padding-right: 100px !important;
        font-size: 16px !important;
    }
    .jquery-lightbox-html h1 {
        font-size: 25px !important;
        margin-left: 0;
    }
    .jquery-lightbox-html p b {
        margin-top: 10px !important;
    }
    .jquery-lightbox-border-top-left,
    .jquery-lightbox-border-top-middle,
    .jquery-lightbox-border-top-right,
    .jquery-lightbox-border-bottom-left,
    .jquery-lightbox-border-bottom-middle,
    .jquery-lightbox-border-bottom-right {
        display: none;
    }
    .jquery-lightbox-button-close {
		top: 15px !important;
    	right: 15px !important;
        z-index: 999999 !important;
	}
    a[href='https://www.donationalerts.ru/r/vsetop'] {
    	margin-left: -800px !important;
    }
    #sendmail {
        background: white;
        border-radius: 3px;
        box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.25);
    }
    .feedback {
        padding: 20px;
    }
    #sendmail h1 {
        padding: 20px;
        margin-left: 23px !important;
        border-bottom: 1px solid #B0BEC5;
        width: 895px;
    }
    .feedback .textbox {
        background: transparent;
        width: 200px;
        border: 1px solid #B0BEC5;
        border-radius: 3px;
    }
    .feedback [name='message'] {
        padding: 20px;
        border: 1px solid #B0BEC5;
        border-radius: 3px;
        width: 600px;
    }
    .feedback .button {
        margin-left: 600px;
        color: white;
        width: 150px;
        background: #546E7A !important;
        border-radius: 3px;
    }
    .feedback .button:hover {
        background: #364349 !important;
    }
    .postfooter {
        background: #F1F1F1 !important;
        margin-bottom: 25px;
        border-radius: 1px;
        padding-top: 8px !important;
        padding-left: 15px !important;
    }
    .category {
        position: absolute !important;
        background: transparent !important;
        border: 0px !important;
        text-align: center;
        margin-top: -2px !important;
        margin-right: -15px !important;
        font-weight: lighter !important;
        font-size: 1em !important;
        float: left !important;
    }
    .title h1 {
        margin-left: 5px !important;
        margin-top: -15px !important;
    }
    .title .news {
        display: none;
    }
    .answer {
        background: white;
        border-radius: 3px;
        padding: 10px;
        box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.25);
    }
    .answer th {
        background: transparent;
        color: rgb(68,62,62) !important;
        border-bottom: 1px solid #cbcfcf;
        padding: 10px;
		text-transform: uppercase;
		font-weight: bold;
    }
    .answer a {
        color: rgb(68,62,62) !important;
    }
	.title_spoiler {
		margin-top: 20px !important;
	}
	.title_spoiler a {
		background: #546E7A;
		color: white !important;
		padding: 10px 15px !important;
		padding-left: 45px !important;
		margin-left: -5px !important;
		border-radius: 3px !important;
	}
	.title_spoiler a::before {
		position: absolute;
		content: url('https://png.icons8.com/material/15/ffffff/plus-2-math.png');
		margin-left: -30px;
		
	}
	.title_spoiler a:hover {
		background: #708a95;
		text-decoration: none !important;
	}
	.text_spoiler {
		margin-top: -27px;
		margin-left: -1px;
		border: 1px solid #b9c1c4;
		border-radius: 3px;
		padding: 20px;
		padding-top: 50px;
	}
    .comment_bottom td {
        background: transparent !important;
    }
    .comment_bottom .right a {
        display: block;
        background: #546E7A !important;
        padding: 7px;
        color: white !important;
        text-decoration: none;
        text-transform: uppercase;
        width: 87px;
        text-align: center;
        border-radius: 3px;
    }
    .comment_bottom .right a:hover {
        background: #445963 !important;
    }
    table.addcomment tr th {
        background: #B0BEC5;
        color: #445963;
        border-radius: 3px;
        padding: 10px !important;
        box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.25);
    }
    #comments {
        border: 0px;
        border-bottom-right-radius: 3px;
        border-bottom-left-radius: 3px;
        box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.25);
        height: 200px;
        padding: 15px;
    }
    .bb-editor {
        margin-top: 10px;
    }
    .bb-pane {
        width: 975px;
        border-top-right-radius: 3px;
        border-top-left-radius: 3px;
        box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.25);
    }
    .quote {
        background: #F4f5f6;
        padding: 15px;
        border: 1px dashed !important;
        border-left: 4px solid #445963 !important;
    }
    .title_quote {
        border: 1px dashed !important;
        border-left: 4px solid #445963 !important;
        border-bottom: 0px !important;
    }
    .navigation {
        margin-top: 30px !important;
        margin-bottom: 30px !important;
    }
    .navigation a,
    .navigation span {
		background: #fff !important;
        border: 0px !important;
        border-radius: 3px;
        padding-right: 20px !important;
        padding-left: 20px !important;
        padding-top: 15px !important;
        padding-bottom: 15px !important;
		font: 10px;
        box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.25);
    }
    .navigation a:hover {
        border: 0px;
        background: #445963 !important;
    }
    #fullsearch {
        background: white;
        border-radius: 3px;
        box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.25);
        margin-bottom: 15px;
    }
    #searchinput {
        padding: 6px;
        width: 500px !important;
        margin-left: 10px;
        margin-top: 15px;
        margin-bottom: 16px;
        border-bottom: 2px solid #B0BEC5 !important;
    }
    #searchuser {
        padding: 6px;
        width: 400px !important;
        margin-left: 10px;
        margin-top: 15px;
        margin-bottom: 15px;
        border-bottom: 2px solid #B0BEC5 !important;
    }
    #dosearch {
        background: #546E7A;
        color: white;
        text-transform: uppercase;
        font-size: 11px;
        font-weight: bold;
        border: 0px;
        border-radius: 3px;
        width: 150px;
        height: 40px;
        margin-left: 10px !important;
        margin-bottom: 10px !important;
        cursor: pointer;
    }
    #doclear,
    #doreset {
        background: #546E7A;
        color: white;
        text-transform: uppercase;
        font-size: 11px;
        font-weight: bold;
        border: 0px;
        border-radius: 3px;
        width: 150px;
        height: 40px;
        margin-left: 0px !important;
        margin-bottom: 10px !important;
        cursor: pointer;
    }
    #dosearch:hover,
    #dofullsearch:hover,
    #doclear:hover,
    #doreset:hover {
        background: #445963;
    }
    #dofullsearch {
        background: #546E7A;
        color: white;
        text-transform: uppercase;
        font-size: 10px;
        font-weight: bold;
        border: 0px;
        border-radius: 3px;
        width: 150px;
        height: 40px;
        margin-bottom: 10px !important;
        cursor: pointer;
    }
    .sresult {
        text-transform: uppercase;
        font-family: 'Roboto' !important;
        font-weight: normal;
        line-height: 16px;
        font-size: 12px;
        color: #606D77;
    }
    .news1 {
        margin-top: 15px;
    }
    .search legend {
        font-size: 18px;
        margin-left: 3px;
        margin-top: 12px;
    }
    #dle-content h1 {
        margin-left: 0;
    }
    #dle-content {
        margin-top: 15px !important;
    }
    .login {
        margin-right: 50px !important;
    }
    .login .button1 {
        background: #546E7A !important;
        height: 30px;
        width: 80px;
        text-transform: uppercase;
        border-radius: 3px;
        cursor: pointer;
    }
    .login .button1:hover {
        background: #445963 !important;
    }
    span.right a {
        margin-top: 10px !important;
    }
    a[href*='#comment'] {
    	background: transparent !important;
        color: #546E7A !important;
    }
    a[href*='#comment']:hover {
    	background: transparent !important;
        color: #242829 !important;
    }
	
	.yashare-auto-init, .leftbt, .rightbt, .title_spoiler img, div[align="center"] > a[href*="yabrowser"] {
		display: none;
