css:
@-moz-document domain('chan.sankakucomplex.com'),
url(https://chan.sankakucomplexxx.com/) {
    #header {
        height: auto !important;
        margin-bottom: 0 !important;
    }
    #header,
    #header *,
    #news-ticker *,
    div#news-ticker {
        background: #4d4d4d !important;
        background-color: #4d4d4d !important;
    }
    #headerlogo {
        display: none !important;
        height: auto !important;
    }
    #headerthumbs {
        height: auto !important;
        margin-bottom: 1em !important;
        padding: 1em !important;
    }
    #popular-preview {
        margin: 0 0 1em !important;
        padding: 0 0 1em !important;
        width: 100% !important;
        border: 1px solid #e542f4 !important;
    }
    #site-title {
        padding: 0 1em 0 0.5em !important;
    }
    #sticky {
        overflow: visible !important;
    }
    img.preview {
        padding: 0 !important;
        margin: 0 !important;
        width: auto !important;
        height: auto !important;
        border: 0 solid #fff !important;
    }
    #subnavbar {
        height: 2.5em !important;
        margin: 3.5em 1em 0 !important;
        width: auto !important;
    }
    .content {
        margin: 1em 1em 1em 22em !important;
        width: calc(100% - 26em) !important;
        border: 1px solid #fff !important;
        padding: 1em !important;
    }
    .lang-select {
        padding-bottom: 0.4em !important;
        padding-top: 0.5em !important;
    }
    .sidebar {
        margin: 1em 1em 0 0 !important;
        min-height: 244px !important;
        width: 20em !important;
    }
    .status-notice {
        border: 1px solid #fff !important;
        margin-top: 1em !important;
    }
    .subnav {
        height: auto !important;
        margin-bottom: 0 !important;
    }
    a {
        color: #e542f4 !important;
    }
    div#header ul#navbar li {
        border: 1px solid #ddd !important;
        border-bottom: 2px solid #FAFAFA !important;
    }
    html,
    body,
    #content,
    #content div,
    #content label,
    #content span,
    #content th,
    #content td,
    #content tr,
    #content ul,
    #content li {
        background: #4d4d4d !important;
        background-color: #4d4d4d !important;
        color: #fff !important;
    }
    img.has-children {
        border: 2px solid #6542f4 !important;
    }
    img.has-parent {
        border: 2px solid #e542f4 !important;
    }
    input {
        background-color: #ccc !important;
        border: 1px solid #fff !important;
    }
    #right-col > div {
        width: auto !important;
        float: none !important;
    }
    #right-col > div:not(#post-content) {
        padding: 0.5em !important;
        border: 1px solid #fff !important;
        margin: 1em 0 !important;
    }
    #post-content {
        padding: 0 !important;
        text-align: center !important;
        border: none !important;
        margin: 0 !important;
    }
    #sp1 {
        display: none !important;
    }
    #right-col > br {
        display: none !important;
    }
    #post-content img {
        margin: auto !important;
        max-height: 90vh !important;
        max-width: 100vw !important;
        width: auto !important;
        height: auto !important;
        display: inline-block !important;
    }
    .comment .content {
        margin: 0 0 0 10em !important;
    }
    #comment-list h3 {
        margin: 0.5em 0 0.5em 0.4em !important;
    }
    #search {
        margin: 1em !important;
    }
    img.favorited {
        box-shadow: 0 0 12px 2px #ff9bce !important;
    }
    .content {
        max-width: none !important;
        width: calc(100% - 27em) !important;
    }
    #subnavbar {
        height: auto !important;
        margin: 3.5em 0 0 1em !important;
        width: calc(100% - 4em) !important;
    }
    span.thumb {
        height: auto !important;
        margin: 0 !important;
        padding: 0.5em !important;
        display: inline-block !important;
        width: auto !important;
        float: none !important;
        text-align: initial !important;
        vertical-align: initial !important;
    }
    .content-page {
        display: inline-block !important;
    }
    .lang-select {
        color: #fff !important;
    }
    #content {
        padding: 0 1em 1em !important;
    }
    #image {
        margin: 0 !important;
        max-width: 100% !important;
        max-height: 100vh !important;
        vertical-align: top !important;
    }
    #content h4 {
        padding: 1em 0 0 !important;
    }
	#content h1, h2, h3, h4 {
		padding: 1em 0 0 !important;
	}
	.subnavbar-option {
		color: #fff    !important;
	}

	.ranking-top-spot {
		border: 1px solid #e542f4 !important;
	}

	.ranking-item {
		border-bottom: 1px solid #fff !important;
	}

	.pagination a {
		background: #333 !important;
	}
	
	#navbar li, #navbar a {
			background: #333 !important;
	}
	
	table {
		border-collapse: collapse !important;
	}
	
		.tag-type-copyright>a, a.tag-type-copyright {
		color: #99c0ff !important;
	}

	.tag-type-character>a, a.tag-type-character {
		color: #F8A6E9 !important;
	}

	.tag-type-artist>a, a.tag-type-artist {
		color: #00ff00 !important;
	}

	.tag-type-medium>a, a.tag-type-medium {
		color: #ff0000 !important;
	}

	.tag-type-studio>a, a.tag-type-studio {
		color: #00ffff !important;
	}

	.tag-type-meta>a, a.tag-type-meta {
		color: #ffff00 !important;
	}
}
