css:
@-moz-document regexp("(http|https)://serienstream.to/.*") {

body
  {
    background: #141414 !important;
    color: #EEEEEE !important;
    font-size: 15px;
    line-height: 140%;
    min-height: 100vh;
  }
  .main-header
  {
    background: #333333 !important;
  }
  .primary-navigation
  {
    background: #333333 !important;
  }
  .logo-wrapper
  {
    background: #d18d1b !important;
  }
  .add-series-button
  {
    background: #d18d1b !important;
  }
  a
  {
    color: #c18319 !important;
  }
  .rating
  {
    color: #c18319 !important;
    margin-bottom: 0px;
    -webkit-filter: grayscale(100%); filter: grayscale(100%);
  }
  .hosterSiteDirectNav ul li:hover a 
  {
    background-color: #68460e !important;
    color: #fff !important;
  }
.hosterSiteDirectNav ul li .active {
    color: #fff !important;
    background-color: #4c330a !important;
}
.hosterSectionTitle {
    background: #d18d1b;
}
.hosterSiteVideo{
 background: #141414;
}
.hosterSiteVideo ul li>div {
    background: #141414;
    border: 1px solid #d18d1b;
}
.col-md-4 {
    width: 25%;
}
.hosterSiteDirectNav ul li a, .hosterSiteDirectNav ul li span {
    color: #fff !important;
    background-color: #d18d1b;
}
.changeLanguageBox {

    background: #141414;

    box-shadow: 0 1px 3px #141414;
}
.socialshareboxseries {
    background: rgba(220,160,140,0.0);
    padding: 0px 0px;
}
.socialMediaBox {
    padding: 0px 0px;
background: rgba(220,160,140,0.0);
}
.button.blue {
    background: #d18d1b;
}
.footer-container {
    background: #000000;
    height: auto;
}
.catalogNav li {
    background: rgba(220,160,140,0.0);
}
.catalogNav li a {
    color: #d18d1b !important;
}
.newEpisodeList {
    background: rgba(220,160,140,0.0);

}
.listTag.blue2 {
    background: #d18d1b;
}
.listTag.blue1 {
    background: #68460e;
}
.listTag.green {
    background: #4c330a ;
}
.homeContentGenresList li a, .homeContentGenresList li>div {
    background: #000000;
}
.coverListNavigation span {
    border: 2px solid #4c330a;
    background: 0;
    color: #fff;
    cursor: pointer;
    font-weight: 600;
    display: inline-block;
    height: 37px;
    z-index: 101;
    width: 37px;
    border-radius: 56px;
}
.reviewList>li {
    list-style-type: none;
    box-sizing: border-box;
    float: left;
    margin: 10px 0;
    -webkit-filter: grayscale(100%);
    filter: grayscale(100%);
}
.header-container nav .user .modal ul li .icon-container i {
    padding: 0;
    color: #d18d1b;
}
.profileSection .profileSectionHeader .profileSectionHeaderSocial>div span {
    color: #d18d1b;
}
.profileSectionTable tbody tr td:nth-child(even) {
    color: #d18d1b;
    float: right;
}
.footer-container .wrapper h4 {
    color: #d18d1b;
    font-weight: 700;
    padding-bottom: 15px;
    font-size: 1.125em;
}
.seriesListHorizontalContent i {

    background: #d18d1b;
}
.menuSearchButton:hover .fa.fa-search {
    color: #d18d1b;
}
.liveNewsFeedSection>ul>li .liveNewsFeedIcon i {
    color: #d18d1b;
}
.searchContainerArea {
    background: rgba(220,160,140,0.0);
}
#inputSearch {
    width: 50%;
    color: #fff;
    border: 0;
    padding-left: 36px;
    font-size: 15px;
    border-radius: 4px;
    background: #000;
}
.socialIcons a {
    background: #68460e;
}
.messagesContainer .newChat {
    background: #68460e;
}
.messagesContainer .newChat button {
    background: #d18d1b;
}
.header-container nav .user .dd p a:hover i, .header-container nav .user .dd p a i.active {
    color: rgb(0, 0, 0);
}
.header-container nav .user .modal {
    display: none;
    position: absolute;
    top: 45px;
    left: 62%;
    margin-left: -99px;
    z-index: 5;
    width: 285px;
    background: #000;
    -webkit-border-radius: 5px;
    -moz-border-radius: 5px;
    -ms-border-radius: 5px;
    -o-border-radius: 5px;
    border-radius: 5px;
}

}
