css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("corbettreport.com")
{
  body, p, #header, #outer-wrap, #content, #page, #related
  {
    background: black;
    color: lightgray;
    font-family: Verdana, Arial, sans-serif;
    font-size: 13px;
  }
  
  h1, h2, h3, h4, h5
  {
    color: #de8a30;
  }
  
  .entry h4, h5, h5 span
  {
    color: lightgray !important;
  }
  
  input
  {
    color: lightgray;
  }
  
  table td
  {
    background: black;
    border: none;
  }
  
  table tr
  {
    color: lightgray;
  }
  
  table td span
  {
    background: black !important;
    color: #de8a30 !important;
  }
  
  table td div
  {
    color: white;
  }
  
  table td div strong a
  {
    background: #888; 
  }
  
  #wrap
  {
    background: black;
  }

  #outer-wrap
  {
    border: none;
  }
  
  #topnav
  {
    background: #101010;
  }
  
  #documentation tr td
  {
    background: #101010;
    border-color: #101010;
    color: lightgray;
  }
  
  #topnav ul a
  {
    color: #0973BF;
    text-shadow: none;
    border-right: none;
  }
  
  #sidebar .widget.subscribebox
  {
  	background: black;
	  padding: 20px;
  }
  
  blockquote, blockquote p
  {
    background: black;
  }
  
  .maincontent a, .maincontent a:link, .maincontent a:visited
  {
    color: #0973BF;
  }
  
  .comment-body, .comment-body p, .widgettitle
  {
    background: #101010;
    font-family: Verdana, Arial, sans-serif;
    font-size: 13px;
  }
  
  .pings
  {
    background: #101010;
  }
  
  #searchform #searchfield
  {
    background: white;
    color: black !important;
  }
  
  p.email-form .sub {
    color: black;
  }
  
  select, input, textarea 
  {
    color: black;
  }
  
  div[style]
  {
    background: transparent !important;
  }
  
  span
  {
    background: black !important;
    color: darkgray !important;
  }
}
