css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.medhelp.org") {

	:root {
		--main-bg-color: #283A10;
		--main-color: #8EA86C;
		--main-accent-color: #66636C;
		--lightest-color: #EAF7D9;
		--light-color: #C3D6AA;
		--dark-color: #4D642D;
		--top-bar: #046302;
		--textarea-bg: #1e2b0b;
	}
	
	textarea {
		background: var(--textarea-bg) !important;
		color: var(--light-color) !important;
		font-size: 125% !important;
		font-family: "Courier New"; 
		resize: both !important;
	}
	
	#side_forum_contextual_search_field {	
		background-color: var(--textarea-bg) !important;
		color: var(--light-color) !important;
		font-family: "Courier New"; 
	}
	.medhelp_forum.partner_default #community_header .srch_box input {
	      border: 1px solid var(--main-color) !important;;	
	}
	
 	#footer, #posts_wrapper, #universe {
		background: inherit !important;
		color: var(--light-color) !important;
	}
	.medhelp_forum.partner_default a {
		color: var(--light-color) !important;
	}
	.medhelp_forum.partner_default .comments {
		border: solid 1px var(--main-color) !important;;
	}
	.photo_link {
		border-radius: 0% !important;
	}
	
	body, .post_message fonts_resizable, .os_14, .post_message_container, post_answer_form float_fix, new_comment_form post_entry, .comments, .post_entry .float_fix .comment, #post_answer_body, .post_answer_form, .background_changeable ._bg {
		background: var(--main-bg-color) !important;
		color: var(--main-color) !important;
	}
	.medhelp_forum .blue_bg, .medhelp_forum .gray_bg {
		background-color: inherit !important;
		background: inherit !important;
	}
	.hn_16b { /* post title on page */
		color: var(--lightest-color) !important;
	}
	#top_nav_container {
		background: var(--top-bar) !important;
	}
	#top_nav .item:hover, #top_nav .item_selected {
		background-color: var(--main-accent-color) !important;
	}
	
	.medhelp_forum.partner_default .post_comment_button .new_medhelp_btn.medium_btn, .medhelp_forum.partner_default #community_header .srch_box .srch_link_txt_inner, .new_medhelp_btn.red_btn {
		background-color: var(--main-accent-color) !important; /* answer & search & ask buttons */
	}
	
	.email_subscription {
		background-color: inherit;
	}
	
	/* forum list page */
	
	.hn_20b { /* forum name */
		color: var(--main-accent-color) !important;
	}
	
	.mh_tertiary_btn { /* buttons upvote, next question etc */
		background-color: var(--dark-color) !important;
		color: var(--lightest-color) !important;
	}

	.medhelp_forum.partner_default .font-sizer, .medhelp_forum.partner_default .manage_user_groups_link, .medhelp_forum.partner_default .join_user_group_link {
		background-color: var(--main-color) !important;
		color: var(--main-bg-color) !important;
	}
	
	#sub_nav_menu_hover_box .sub_nav_menu_elements {
		background-color: var(--main-accent-color) !important;
	}
	#start_group { 
		/* start a group image was too bright */
		opacity: 0.6;
	}
	.medhelp_forum #right_panel .side_widget {
		border: 3px solid var(--main-color) !important;;
	}
	.medhelp_forum #learn_more_link {
		background-color: var(--main-accent-color) !important;
	}
	
	/* footer */

	#medhelp_footer a {  /* delete? */
		color: var(--main-color) !important;
	}
	#medhelp_footer {
		color: inherit !important;
	}
	
	/* pages */
	.status {
		background: var(--main-bg-color) !important;
	}
	#header {
		background: var(--main-accent-color) !important;
		box-shadow: inset 0 3px 10px #000000;
	}
	#header #page_heading div.page_title {
		text-shadow: 1px 1px 2px black;
		color: var(--light-color)
	}
	
}

