css:
@-moz-document domain("adkami.com") {
body
  {
    background-color :#212F3C !important;       
  }

.video-item-list.up
{
animation-name: none;
}
.wallpaper-fiche
{
 visibility:hidden;
}

h4
{
color:white!important;
}
 #body
 {
    background:linear-gradient( to bottom,#212F3C,#0B5345) !important;
    background-attachment:fixed;
    background-repeat:no-repeat;

 }

 body::-webkit-scrollbar-track
{
	-webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3)!important;
	background-color: #F5F5F5!important;
}
  .bloc,.blocm
  {

  background:linear-gradient( to top,#34495E,#34495E) !important;

  }
  .col-s-4
 {
  position:absolute;
  margin-left:60.5%!important;
  }

  div h2,aside h2, div h1
  {

  background: linear-gradient( to top,#17202A,#1A5276) !important;
      border-radius:0px!important;
      border-left:3px solid #17202A;
      border-right:3px solid #17202A;

  }

  .top.bloc ,.set.bloc
  {
      border-radius:10px!important;
  }
  .top.bloc img, #image_manga
  {
      border-radius:0px!important;
  }
  h1
  {
  width:auto ! important;
  }

  #tab2 li
  {
   background:linear-gradient( to top,#283747,#283747) !important;
  }

  #tab2 li:hover
  {
   background:linear-gradient( to top,#283747,#283747) !important;
  }

  header,#login .activebox
  {
   box-shadow:none!important;
   background:linear-gradient( to top,#212F3C,#0B5345) !important;
  }

  .accueil, .anime, .team, .flux, .serie, .dessin
  {
  font-family: cursive!important;
      font-size:20px;
  }
  nav a ,#pages,footer,#footer th,#info_video
  {
  background:none !important;
    background-color:  transparent !important;
  }
footer
{
  box-shadow:none;
  background-color: transparent!important;
 background:linear-gradient( to top,#212F3C,#0B5345)!important;
}
  footer table
  {
  background:linear-gradient( to bottom,#212F3C,#06352C);
  }
  #menuvideo_tab
  {
   background-color:#2E4053!important;
  }
  #menuvideo li a:hover
  {
   background-color:#34495E;
  }
  #menuvideo li a.actuel,#menuvideo li a:visited,#menuvideo li a:hover:visited
  {
  background-color:transparent;
      color:white;
  }
  #tab3 li a,#tab3 li.top,#newanime tr
  {
  background:none;  
  }

  #tab3 li:hover a,#newanime tr:hover
  {
  background:linear-gradient( to bottom,#34495E,#212D3A);    
  }

  #tab3 li:first-of-type a,#newanime tr:first-of-type th
  {

  border-top:2px solid #17202A;

  }
  #tab3 li a,#newanime th
  {
   background-color:transparent;
  border-bottom:2px solid #17202A;
  }

#nav-episode ul li a:hover
{
background-color: #34495E!important
}
#nav-episode ul li a:visited
{
background-color: rgba(0,0,0,.72)!important
}

#nav-episode ul li a:hover, #nav-episode ul li.actived a,#nav-episode ul li a:hover, #nav-episode ul li.actived a
{
background-color:transparent!important;
}

  .video-item-list:hover span.Shojo, .video-item-list:hover span.Shonen,.video-item-list:hover span.Seinen
  {
  transform: rotate(20deg)
  }
  .comm:hover .comm-ava img
  {
  transform: rotate(3deg)
  }
}
