@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("expressen.se") {
.b-overlay__fg--welcome, .b-overlay__fg, .b-overlay__bg.visible,.b-ad-block-warning, .behaviour-survey-popup.behaviour-survey-popup-visible,.b-adblock-main, .b-export__wrap.b-export__fourth-column.b-adblock-sidebar-container, #feedbackify, .b-news-flash, .has-flash-message .b-flashes, .b-welcome, .b-article-share.fixed ul, .feedbackify-button, #adtoma_container_fus, #helsidaAd, #helsidaContainer, #AD-rich_media1-wrapper, adblock-container, .adblock__message, .b-ad__caption, #AD-panorama1-wrapper  {
    display:none!important;}
.l-body{   
    width: calc(100%);}    
.l-page, .b-header, .fixed.b-header__menu .menu-items, .fixed-section.b-header__banner-back-to-expressen .b-header__banner-back-to-expressen--content {
    width: 1292px !important;}
.l-column__main-column {
    width: 837px !important;}
.l-column__two-columns {
    width: 1137px !important;}
.l-main-article-column, .l-main-section-column, .l-secondary-article-column {
    width: 972px !important;}
.l-main-article-column.l-main-longread-column, .l-main-section-column.l-main-longread-column, .l-secondary-article-column.l-main-longread-column {
    width: 1282px !important;}
.l-main-longread-column .b-img-container  {
    width: 1268px !important;}
.b-article.b-article--long-read .b-article__header, .b-article.b-article--long-read .b-article__preamble, .b-article.b-article--long-read .b-article__body{
    max-width: 945px;}
.b-article.b-article--long-read .b-text-image--full figcaption{
    width: 1268px;
    text-align: center;}
.b-text-image--full .b-text-image__photographer {width: 1268px;
    float: left;}
.main-container {width: 1312px !important;padding-left: 10px !important; padding-right: 10px !important;}
.below-article .one-half img, .below-article .full-width img, .below-article .one-third img {
    min-height: 137px !important;
    max-height: 172px !important;}
}
