css:
@-moz-document domain("ogame.gameforge.com") {
/*
  - CREATED BY TheSxW - Do Not Delete This Line!!!
  */
#login {display:block!important;position:absolute;top:70px;right:200px;}
#contentWrap,#loginBtn,#mmonetbar{display:none!important}

div#start{background-image: none!important;}

#subscribe{position:relative!important;top:-110px!important;left:50px!important;}
[class*="openX_"]{display:none!important;}/*REMOVE AD ON LOGIN SCREEN*/
span.next_event{width:210px!important;}
[id*='js_eventDetails']{margin: -35px 1px 0 0!important;}
/*kolorowanie ruchu floty*//*
[data-return-flight^="true"]{background:rgba(33,33,33,0.6)!important;}
[data-return-flight^="false"]{background:rgba(0,255,0,0.3)!important;}*/
/*eventlist fixed right bottom corner*/
#eventListWrap{background:black;border:1px solid #747474;position:fixed!important;bottom:0px!important;right:0px!important;left:auto!important;top:auto!important;overflow-y:scroll;max-height:450px!important}
/*gra ingame content*/
body{background:black!important;
    font-family: 'Courier New', sans-serif!important;
    letter-spacing: 0.8px;
    text-transform: uppercase }
#logoLink{padding-top:20px;padding-left:10px;z-index:0;
content:url("http://vignette3.wikia.nocookie.net/pl.ogame/images/c/c9/Logo.png/revision/latest?cb=20091107092323");}
#boxBG{background:black!important;}
#banner_skyscraper{display:none!important;}
#star{display: none !important;}
#info div#star::before{color: red;content: 'OGAME - TheSxW - 2017 version 0.05';font-weight: bold;font-size:12px;}
#info{background-image: url(http://oi65.tinypic.com/246vv5c.jpg)!important;}
/*TOPNAV*/
.ago_clock{width:auto!important;right:40px;top:0px;font-size:10px!important;}
body.no-commander .contentBoxBody{top:0px!important;}
#helper{padding-top:10px;z-index:-5;}
#helper a.new_helper:hover{background: rgba(255, 0, 0,0.55) url(http://gf1.geo.gfsrv.net/cdn96/18e4684df27114667e11541e5b2ef8.png) -143px -123px no-repeat!important;box-shadow: 0 0 30px 5px red;}
.OGameClock span{font-family: "Courier New"!important;font-size: 15px!important;padding-top: 5px;color: #93989f;}
.OGameClock{
    background: #0f1419;background: -moz-linear-gradient(top,#0f1419 0,#101419 100%);
    background: -webkit-gradient(linear,left top,left bottom,color-stop(0%,#0f1419),color-stop(100%,#101419));
    background: -webkit-linear-gradient(top,#171d23 0,#101419 100%); 
    z-index:5;color: #80868f;padding: 0 10px 0 10px;border: 1px solid #80868f;border-radius:5px 5px 5px 5px;font-family: "Courier New"!important;
    position: absolute;left: 10px;top: 40px;text-align: left!important;font-size: 15px!important;width: auto }
#playerName span{font-family: "Courier New"!important;font-size: 15px!important;color: #93989f;}
#playerName{
    background: #0f1419;background: -moz-linear-gradient(top,#0f1419 0,#101419 100%);
    background: -webkit-gradient(linear,left top,left bottom,color-stop(0%,#0f1419),color-stop(100%,#101419));
    background: -webkit-linear-gradient(top,#171d23 0,#101419 100%); height:19px;
    z-index:5;padding: 0 10px 0 10px;border: 1px solid #80868f;border-radius:5px 5px 5px 0;font-family: "Courier New"!important;position: absolute;
    left: 12px;top: 21px;text-align: right!important;font-size: 15px!important;width: auto }
#ago_panel_button{z-index:999}

/*planety prawe menu*/
#rechts #myPlanets .ago_planets_construction{left:20px!important;top:-2px!important;border:1px solid darkgrey;border-radius:5px;padding:3px 3px 0 3px;background:black}
#rechts #myPlanets .planet-name{top:15px!important}
#rechts .planet-koords{top:25px!important}
.icon12px {margin-left: 4px;}
#rechts #myPlanets .ago_planets_construction_moon{left: auto;right: 37px!important;top: 47px!important;border:1px solid darkgrey;border-radius:5px;padding:3px 3px 0 3px;background:black}
/*koniec*/
/*Podglad planety*/
.desc{width:100px!important;text-align:right!important;}
.data{text-align:left!important;}
.planetMoveStart{position: absolute;top: 30px;left: 0px;}
a.openPlanetRenameGiveupBox.dark_highlight_tablet{position: absolute;top: 50px;left: 0px;}
#planetOptions{position: absolute;top: -60px;left: -270px;}
.planetMoveProgress,#moveCountdown{width:150px!important;display:block}
a.cancelMove{position:relative;left:4px;}

/*ressources*/
body#resourceSettings td.label{width:165px!important}
/*OKNO Budowań*/
body#research .clearfloat{clear:none!important}
#planet div#slot01.slot{top:0px!important;right:5px!important;z-index:12!important}
body#resources #buttonz{position:relative!important;z-index:10;top:-260px!important;}
body#station #buttonz{position:relative!important;z-index:10;top:-260px!important;}
body#research #buttonz{position:relative!important;z-index:10;top:-225px!important;}
body#shipyard #buttonz{position:relative!important;z-index:10;top:-225px!important;}
body#defense #buttonz{position:relative!important;z-index:10;top:-225px!important;}
#buttonz .content, #buttonz #wrapButtons{opacity:0.9;}
body#research div.detail_screen,body#shipyard div.detail_screen,body#defense div.detail_screen{top:280px!important;position:relative!important}
#planet h2{padding-top:3px!important;}
#buttonz h2, #fleet3 #sendfleet h2, #movement h3 a, #statistics h3, #highscoreContent h2{text-align:left!important;padding-left:35px}
body#resources div.detail_screen,body#station div.detail_screen{top:300px!important;position:relative!important}
#stationbuilding{height: 220px!important;margin: 0px auto 0px 17px!important;}
#buttonz .footer{bottom: -5px!important;}
body#research .content-box-s,body#shipyard .content-box-s,body#defense .content-box-s,#pqueue{top: -220px;position: relative;}
body#resources .content-box-s,body#station .content-box-s{top: -270px;position: relative;}
/*Handlarz zakladki i glowne menu*/
#div_traderResources .big_tab{min-width:49%!important;width:49%!important;}
#traderOverview .back_to_overview{background:black!important;color:red;border-radius:10px;border:1px solid #747474;height:25px;top:-20px!important;left:10px!important;}
/*body#traderOverview #header_text,body#traderOverview #planet{height:80px!important}*/
.div_trader{top:80px!important;position:absolute;z-index:50}
#callTrader{top:55px!important}
.c-left,.c-right{display:none!important}
a.back_to_overview::before{content:'BACK';}
a.back_to_overview{text-align:center;padding-top:5px;float:left!important}
/*STATYSTYKI*/
#highscoreContent #ranks .sendmsg_content{width:auto!important;}
.position,.score{text-align:center;}
.score{width:120px!important}
/*Raporty*/
.detail_list_txt{width:auto!important;}
.detail_msg .section_title{height:auto!important;background:#001111!important;border:1px solid #002222}
.combat_participant{margin:3px 0 0 0!important;width:49%!important}
.ship_list_28 li{width:100%!important;border-bottom:1px solid #fff!important;}
.military_ships{width:95%!important}
.detail_shipname{text-align:center!important;padding: 0px;}
/*FLOTA*/

.ago_movement{font-size:9.5px!important;}
#ago_calc td{padding: 1px!important;}
#speedLinks span{padding-right:1px;}
/*GALAKTYKA*/
#galaxy.ago_improve #galaxytable{background: #050101;}
#galaxytable .info_header span{color:yellow!important;}
/*ogame addons content*/
.ago_planets_shortcut{left:-10px;top:5px!important;border-radius:5px;border:1px solid darkgrey ;right:auto!important;width:auto;height:auto;padding:4px 2px 4px 2px;background:black;}
#rechts #myPlanets .smallplanet{height:60px!important;}
/*Wiadomości*/
.subtabs .list_item{text-align:center;font-size: 9.5px!important;border:1px solid #001111;padding:0 5px 0 5px!important;border-radius:10px;}
.subtabs .last .txt_link{right:0px!important}
.subtabs .first .txt_link{left:0px!important}
.subtabs a.txt_link{height:100%!important;width:100%!important;vertical-align:middle;}
.subtabs,.subtabs a.txt_link{background-image: none!important;}
#messages ul.subtabs{margin-bottom: 50px!important;}
.tabs_wrap .tab_ctn{padding:5px!important;}

.subtabs .ui-tabs-active.first .txt_link, .subtabs .ui-tabs-active.last .txt_link, .no-touch .subtabs .ui-state-hover, .subtabs .ui-tabs-active{border-radius:10px;background:#222!important}
/*FOOTER*/
#empire #siteFooter .content, #siteFooter .content{WIDTH:AUTO!IMPORTANT}
#siteFooter .fleft{display:none;}
#siteFooter {width:315px!important;border:2px solid #777;background:black!important;bottom:25px;}
#siteFooter .fright{float:left!important;}
}
