css:
@-moz-document domain("www.eimacs.com") {
#page .javacodediv, #page div.javacodediv div {
    font-family: "Fira Mono";
}

#page ol.num {
    margin: 0 !important;
    padding: 20px !important;
}

.num .javacodediv {
    width: calc(100% + 40px) !important;
    margin-left: -30px !important;
}

#page .multichoice .javacodediv, #page table .javacodediv {
    margin-left: 10px !important;
}


div.title > h2, #contentslink, #indexlink, #rightlogo,  #bottom, #top, #footer, .ace_gutter, .testdatadiv > h4 {
    display: none;
}

.gradetable, #topnav ul {
    border: none;
}

.resizediv.ui-resizable {
    max-width: 100%;
    min-width: 100%;
    min-height: 120px;
}

#dl-top, #program-finder, #dl-bottom {
    background: none;
}

#dl-top {
    height: 70px;
}

#page .taskbody .javacodediv {
    width: calc(100% + 12px) !important;
    margin-left: -16px !important;
}

#page .javacodediv {
    width: 100%;
    margin-left: -10px;
    background-color: #dadada;
    border: none;
}

.testdatadiv {
    background-color: white !important;
    border: none !important;
    width: calc(100% + 8px);
    margin-left: -16px !important;
}

#page .longdesc {
    margin-top: 10px;
    margin-bottom: 10px;
    padding-left: 10px;
    background-color: #dadada !important;
    border-left: 4px solid #4482eb;
}

#page .itemtestdata {
    margin: 0 !important;
}


.thegradetablediv > table {table-layout:fixed;width:100%;}
.thegradetablediv > table  > tbody>tr>*:nth-last-child(2)~*{ width:50%}
.thegradetablediv > table  > tbody>tr>*:nth-last-child(3)~*{ width:33.3%}
.thegradetablediv > table  > tbody>tr>*:nth-last-child(4)~*{ width:25%}

td.gradetitlediv:nth-last-child(2):after {
    visibility: visible;
    display: block;
    content: "Grades";
    background-color: midnightblue;
    width: 100%;
}

#c2 {
    padding: 10px;
}

table.gradetable td.r {
    text-align: left !important;
}

#header {
    padding-top: 20px;
}

#locationbox {
    top: 106px;
}

#topnav ul {
    background: #4482eb !important;
}

#topnav li, #topnav .right, #middle {
    background: none;
}

#middle {
    background-color: white !important;
}

body {
    background: #dadada !important;
}

#navbar {
    left: calc(403px + 50vw) !important;
    width: 100px !important;
    top: 167px !important;
}

#navbar ul li {
    border: none !important;
}

#header > a >img {
    transform: translate(-50%);
    margin-left: 50% !important;
    position: static;
}

#prevlink, #prevbutton > img {
    content: url(https://cdn.rawgit.com/google/material-design-icons/a6145e16/navigation/ios/ic_arrow_back.imageset/ic_arrow_back.png);
}

#nextlink, #nextbutton > img  {
    content: url(https://cdn.rawgit.com/google/material-design-icons/a6145e16/navigation/ios/ic_arrow_forward.imageset/ic_arrow_forward.png);
}

#page #bottomarrows {
    margin-top: 12px;
}

.quizpopper {
    display: none;
}

#page h3, #page div.taskbody, #page h3.subtitle {
    background: none;
    border: none;
}

#topnavul {
    border: none !important;
}

#container {
    box-shadow: -5px 0 20px 0px rgba(51, 51, 51, 0.13), 5px 0 20px 0px rgba(51, 51, 51, 0.13);
    margin: 0;
}

div#middle {
    width: 1024px;
}

#c2-home {
    margin-right: 10px !important;
    float: right;
}

#c1-home {
    overflow-x: hidden;
    width: 600px;
    padding: 0 0 0 30px;
    margin:0 !important;
}

#quickLinks {
    width: initial !important;
}

#form1 input[type="text"],#form1 input[type="password"] {
    width: 130px;
}

#form1 input[type="image"] {
    margin-left: 10px;
}

.imagesdiv {
    max-width: 600px;
    margin-left: 0 !important;
}

.moretextdiv {
    max-width: 600px;
}

#content2 > p, #content2 > h2 {
    max-width: 600px;
}

#content2 {
    margin-bottom: 40px;
}

#c2-home {
    position: absolute;
    margin-right: -10px;
    right: 0;
}

.ansbtn > input {
    padding-left: 98px !important;
    background: url(https://dabuttonfactory.com/button.png?t=Check+solution&f=Calibri-Bold-Italic&ts=14&tc=fff&w=98&h=21&c=0&bgt=unicolored&bgc=00a351);
}

.ansbtn {
    margin-left:89%;
    max-width: 98px;
    overflow: hidden;
    max-height: 22px;
    margin-bottom: 10px;
}

#page .javacodediv, #page .testdatadiv {
    padding: 10px 10px;
}

.exbox .javacodediv {
    width: calc(100% + 8px) !important;
    margin-left: -14px !important;
}

.ui-state-default, .ui-widget-content .ui-state-default, .ui-widget-header .ui-state-default {
    background: #d7ebf9 !important;
}

.ui-state-active, .ui-widget-content .ui-state-active, .ui-widget-header .ui-state-active {
    color: #0070a3 !important;
}

#page .exlabel {
    margin: 0;
    font-weight: bold;
    border: none;
    padding: 6px 6px 6px 20px;
    background-color: #4482eb;
    color: #ffffff;
}

#page .exbox {
    background-color: white;
    border: none;
}

#page .excontent {
    margin: 0;
}

#page h3 a.pagthis, #page h3 a.pagthis:link, #page h3 a.pag, #page h3 a.pag:link {
    width:22px;
    height:20px;
    background-color: #f1f1f1;
	border: none;
    padding: 4px 4px;
    display:inline-block;
    text-align: center;
}

#page h3 a.pagthis, #page h3 a.pagthis:link {
    background-color: #dc143c;
}

#page div.title {
    border: none;
}

#page h3.subtitle {
    padding-bottom: 4px;
    margin: 0;
}

#topprevnextbuttons div {
    left: 55px;
    top: 4px;
}
}
