@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.freedesktop.org") {
  
  /** OPTIONAL FONT - DISABLE IF YOU DONT WANT TO MAKE REQUESTS TO GOOGLE SERVERS **/ 
  
  /* latin-ext */
  @font-face {
    font-family: 'Open Sans';
    font-style: normal;
    font-weight: 400;
    src: local('Open Sans'), local('OpenSans'), url(https://fonts.gstatic.com/s/opensans/v13/u-WUoqrET9fUeobQW7jkRRJtnKITppOI_IvcXXDNrsc.woff2) format('woff2');
    unicode-range: U+0100-024F, U+1E00-1EFF, U+20A0-20AB, U+20AD-20CF, U+2C60-2C7F, U+A720-A7FF;
  }
  
  /* latin */
  @font-face {
    font-family: 'Open Sans';
    font-style: normal;
    font-weight: 400;
    src: local('Open Sans'), local('OpenSans'), url(https://fonts.gstatic.com/s/opensans/v13/cJZKeOuBrn4kERxqtaUH3VtXRa8TVwTICgirnJhmVJw.woff2) format('woff2');
    unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215;
  }

  * {
    font-family: 'Open Sans' !important;
  }
  
  /** END OF OPTIONAL FONT **/
  
  div.page {
    background-color: #f5f5f5;
  }
  
  /* Header */
  
  div.pageheader {
    padding: 0;
    position: initial;
    overflow: initial;
    background: none;
    background-color: #c3c3c3;
    border: none;
    padding-bottom: 5px;
  }
  
  div.pageheader:after {
    content: '';
    display: block;
    clear: both;
  }
  
  div.header {
    margin: 0 auto 0 auto !important;
    box-sizing: border-box;
  }
  
  .logo {
    float: left;
    clear: none;
  }
  
  #searchform {
    margin: 1rem 0 0 0;
  }
  
  input#searchbox {
    padding: .5em .6em;
    display: inline-block;
    border: 1px solid #ccc;
    box-shadow: inset 0 1px 3px #ddd;
    border-radius: 4px;
    vertical-align: middle;
    box-sizing: border-box;
    width: 160px;
  }
  
  input#searchbox:focus {
    outline:0;
	  border-color:#129FEA;
  }
  
  .pageheader .actions {
    margin: 1rem 0 0 0 !important;;
    width: 280px;
    display: inline-block;
    float: initial;
    clear: initial;
  }
  
  .pageheader .actions ul li a {
    background: none;
    color: #FFFFFF;
    border: none;
    
  }
  
  .pageheader .actions ul li a:visited {
    transition: all .2s ease-out;
    text-decoration: none;
  }
  
  .pageheader .actions ul li a:hover, .pageheader .actions ul li a:active{
    text-decoration:underline;
    color: #FFFFFF;
  }
  
  
  /* Body */
  div#content {
    background-color: #ffffff;
    border: 1px solid rgba(0,0,0,0.1);
    transition: box-shadow .25s;
    border-radius: 2px;
    box-shadow: 0 2px 5px 0 rgba(0,0,0,0.16),0 2px 10px 0 rgba(0,0,0,0.12);
    margin: 3rem auto 1rem auto !important;
    padding: 10px 20px 10px 20px;
    box-sizing: border-box;
  }
  
  div#pageinfo {
    border: none;
    margin: 0 auto 0 auto !important;
    padding: 10px 0 10px 0;
    box-sizing: border-box;
  }
  
  /* Footer */
  div#footer {
    background-color: #333;
    font-size: 14px;
    font-weight: 300;
    text-align: left;
    color: #fff;
    margin: 0;
  }
  
  div.pagedate, div#backlinks {
    margin: 0;
  }
  
   #backlinks a {
    color: #ffffff;
    text-decoration: none;
  }
  
  #backlinks a:visited {
    transition: all .2s ease-out;
    text-decoration: none;
  }
  
  #backlinks a:hover, #backlinks a:active{
    text-decoration:underline;
  }

  .popup:hover .balloon,  .popup:focus .balloon {
    background-color: #222;
  }
  
  /* Some special handling for smaller devices */
  @media (min-width: 800px) {
    div#content, div#pageinfo, div.header {
      width: 700px;
    }
  }
  
  @media (min-width: 900px) {
    div#content, div#pageinfo, div.header {
      width: 800px;
    }
  }
  
  @media (min-width: 1000px) {
    div#content, div#pageinfo, div.header {
      width: 900px;
    }
  }
  
  @media (min-width: 1200px) {
    div#content, div#pageinfo, div.header {
      width: 1000px;
    }
  }
  
  @media (min-width: 1200px) {
    div#content, div#pageinfo, div.header {
      width: 1100px;
    }
  }
}
