css:
@-moz-document domain("quizlet.com") {
html, body {
    font-family: sans-serif !important;
    font-size: 100%;
    line-height: 1.625;
}
.SetPageHeader, #SetPageTarget, .SetPage-menu {
    background: #f0f0f0 !important;
}
.UITooltip, .UITooltip--micro, .SetPage-modeGroupLabel, .SiteHeaderChiclet, .SiteNav-section .SiteNav-blogSection, .SiteNav-footer, .upsell, #animalGallery, .more-animals, .Divider, .SiteHeader-userDropdownOptionSection .UIMenuItem a, .DashboardFeedEmptyState-body, .CreateFolderModal-main .UIInput-label, .CreateFolderModal-main .UIInput-label, .UIHeading--three, .ClassSearchPage-facebookWrap, .UIInput-label, .CardsItem-flipHint, .CardsModeControls-progressLabelText, .facebook, .password-change, .username-change, .privacy, .account-delete, .locale-preference, .email-change, .SiteHeader-username, .SiteHeader-userDropdownIcon, .UIPopover::after, .UISocialButton, .UIModalBodySeparator, .UITextarea-label, .SiteFooter, .SetPageFooter-editButtonWrapper {
    display: none !important;
}
.DashboardLayout-sidebar, .DashboardPage-header, .DashboardHeader {
    background: #f0f0f0;
    box-shadow: none;
}
.profile-photo-upload .the-label {
    background: #ffffff;
    border-radius: 2px;
}
.SiteNav-menuItem html.cursor .UILink:hover, .SiteNav-menuItem .UIMenuItem .UILink:hover .UIMenuItem-inner {
    background: #e1e1e1 !important;
}
.SiteNav-menuItem--active .UIMenuItem-inner, .SiteNav-menuItem--active .UILink:hover, .SiteNav-menuItem--active .UILink:hover {
    background: #f9f9f9 !important;
}
.DashboardPage-main {
    padding-top: 0px
}
.ModeControls-modeName {
    color: #ffffff
}
.ModeControls {
    box-shadow: none;
}
.SiteHeader-avatar .UserAvatar {
    height: 48px !important;
    width: 48px !important;
}
.UIPopover {
    background: #fff;
    box-shadow: none;
}
.UIModalBody {
    padding: 2rem 2rem 1rem 2rem;
}
.SetPage.has-adz .SetPage-diagram, .SetPage.has-adz .SetPage-terms {
    padding-right: 0;
}
.SetPage.has-adz .SetPage-modes, .UIDiv .SetPage-options, .SetPage.has-adz .SetPage-options, .SetPageHeader-container, .SetPageHeader.has-adz .SetPageHeader-container {
    padding-right: 0;
    text-align: center;
}
.SetPageTerm-side, .SetPageTerm-largeSide {
    width: 50%;
}
.SetPageHeader-termCount {
    float: none;
    line-height: 0 !important;
}
.SetPageHeader-creator {
    display: inherit !important;
}
/*Dark Code*/
.SetPageFooter, .SiteNavSectionHeading, .FolderPageBanner, .SetPageAdz-wrapper, .SetPageTermChunk-subtitle {
    display: none !important;
}
.SetPage-termsWrapper, .SetPage-diagramWrapper, .SetPageHeader, #SetPageTarget, .SetPage-menu, .SetPage-modesBottomBackground, .SetPageHeader-infoDelimiter, .DashboardLayout-sidebar, .DashboardPage-header, .DashboardHeader, .SetPageHeader, #SetPageTarget, .SetPage-menu, .DashboardLayout-sidebar, .DashboardPage-header, .DashboardHeader, .DashboardLayout, .UIDropdown, .SetPageModeButton, .ModeLayout, body.no-tan .site, body.no-tan .page, .SiteHeader, html, .UIToggle-optionLabel {
    background: #000000 !important;
}
.ModeControls, .ModeControls-actions {
    background: #181818;
}
.SetPageHeader-infoDelimiter {
    border: none;
}
.SetPageModeButton {
    border: 0.03125rem solid #4257b2;
    border-bottom: 0.3125rem solid #4257b2;
}
.UIDropdown {
    border: 0.125rem solid #4257b2;
}
.SetPageTerm {
    background: #dbdbdb;
}
.SetPageTerm-sideContent {
    border-right: 0.125rem solid #4257b2;
}
.SiteNavSectionHeading {
    font-weight: 200;
    text-transform: uppercase;
    font-size: 1rem;
    letter-spacing: 0.009rem;
    line-height: 0.05;
}
.UISwitch-label {
    background-color: #181818 !important;
    border: 0.125rem solid #4257b2;
}
.ModeControls-back {
    border-bottom: 1px solid #4257b2;
}
::-webkit-scrollbar {
    width: 10px;
    height: 12px;
    background-color: #000000;
}
::-webkit-scrollbar-thumb {
    background-color: #1e1e1e;
    border-radius: 2px;
}
.SiteNavSectionHeading {
    border-bottom: 1px solid #f0f0f0;
    padding: 0;
}
.SiteNav-section {
    border-bottom: 0;
    padding: 0.25rem;
}
.SiteNav {
    padding-top: 2.75rem !important;
}
.DashboardFeedGroup-separator .UIHorizontalRule {
    border-color: #3b4c9b;
    width: 1000%;
}
.SiteNav-menuItem--active .UIMenuItem-inner, .SiteNav-menuItem--active .UILink:hover, .SiteNav-menuItem--active .UILink:hover {
    background: rgba(59, 76, 155, 0) !important;
    border: 0.0125rem solid #4257b2;
}
.SiteNav-menuItem html.cursor .UILink:hover, .SiteNav-menuItem .UIMenuItem .UILink:hover .UIMenuItem-inner {
    background: rgba(59, 76, 155, 0) !important;
    border: 0.0125rem solid #4257b2;
}
.FolderPageHeader-byline {
    border-left: 0.125rem solid #3b4c9b;
}
.UILinkBox {
    background-color: #f2f2f2;
}
.UIToggle, .ScatterTermSide .ScatterTermSide-inner {
    border: 0.125rem solid #4257b2;
}
.UIToggle-option {
    border-left: 0
}
.UIToggle-optionInput:checked:not(:disabled) ~ .UIToggle-optionLabel, .UIToggle-optionLabel:after, .UIToggle-optionLabel:before {
    background-color: #4257b2 !important;
    color: #ffffff;
    border-right: none;
    border-left: none;
}
.ScatterTermSide {
    background-color: #101010;
}
.ScatterTermSide.dragging .ScatterTermSide-inner, .ScatterTermSide.over .ScatterTermSide-inner, .ScatterTermSide.mouseover .ScatterTermSide-inner {
    border-color: #ffcd1f;
}
.ModeControls-main .UIHeading--three {
    display: inline !important;
}
}
