css:
@-moz-document regexp("https?:\/\/(yande|konachan).*"), regexp("https?:\/\/.*booru.*") {
IFRAME,
div.blocked, .paginator, #paginator { 
	display: none !important;
}
div.sidebar, #tag-sidebar { 
	font-size: 0.8em;
	position:absolute;
	margin-top: -5px;
	width: 95% !important;
}
div#main-menu > ul { padding-left: 26px !important;}
#stats, .sidebar > div:nth-child(5), .sidebar > div:nth-child(6) {
	margin-left: 10px !important;
	width: 95% !important;
}
div.content { 
	margin: 64px 0px 0px 20px !important;
	width: 95% !important;
	padding-left: 30px !important;
}
#post-list > .content > div:nth-child(5) { 
}
.tag-link, .tag-type-general, .tag-type-copyright { 
	float: left;
}
ul { 
	margin: -10px;
}
input { 
	width: 150px;
}
li.user {
	width: auto;
}
div.lsidebar, #paginator
	{ 
	display: none !important;
}
body {
	padding: inherit;
}
span.blacklisted, li.javascript-hide {
	display: inline-block !important;
}
hr.autopagerize_page_separator, #blacklisted-sidebar, #post-list > div.sidebar > div:nth-child(5) > h5, .autopagerize_page_info{
	display: none !important;
}
div.content > center > div {
	display: none !important;
}

/*
#post-list > div.content > div:nth-child(5) { margin-left: 40px}
div.sidebar { position:absolute ; bottom: 85%; left:68% }
div.sidebar > div:nth-child(1) { width: 100%; height:70%; }
div.sidebar > div:nth-child(5), #blacklisted-sidebar { display: none;}
*/
}
@-moz-document regexp("https?:\/\/konachan.*post/show.*") {
#image {
		width: 90% !important;
		height: 90% !important;
		position: fixed right !important;
		padding-left: 166px !important;
		margin-right: 0px !important;
	}
	.sidebar > div:nth-child(3) > h5:nth-child(1), #stats > h5:nth-child(1), .sidebar > div:nth-child(5) > h5:nth-child(1), .sidebar > div:nth-child(6) > h5:nth-child(1) { 
		display:none!important;
	}
	#tag-sidebar{
		margin-top: 5px !important;
	}
	#stats > ul:nth-child(2), .sidebar > div:nth-child(5) > ul:nth-child(2) { 
		margin-top: 5px !important;
		width: 15% !important;
	}
	.sidebar > div:nth-child(5) > ul:nth-child(2), .sidebar > div:nth-child(6) > ul:nth-child(2) {
		margin-top: 15px !important;
	}
	.comment-avatar-container	{
		width: 5% !important;
	}
}
