css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("blogger.com") {
  /*Page Background*/
	body {/*
		background-color: 000 !important;
		background-image: url('http://i65.tinypic.com/iqh7rm.jpg') !important;
		background-attachment: fixed !important;*/
		margin: 0px !important;;
	}
	
	/*Content*/
	#allHolder {
    /*background: none;*/
		background-color: rgba(31,37,38,1) !important;
		background-image: none !important;
		margin: 0px;
	}
	
	.postCommentButtonHolder, #commentBodyField {
    /*background: none;*/
    background-color: rgba(22,26,27,1) !important;
    /*background-color: rgba(164,194,204,0.30) !important;*/
		background-image: none !important;
	}
	
	.dropdown {
    /*background: none;*/
    background-color: rgba(164,194,204,.75) !important;
		background-image: none !important;
	}
	
	/*Borders*/
	#allHolder, #commentBodyField {
		border: 1px solid #000 !important;
	}
	
	/*Whitetext*/
	#commentBodyField, .checkboxLabel, .dropdown {
		color: #fff !important;
	}
	
	/*Buttons*/
	.signoutButton, [type='button'], #identityMenu, .dropdown {
		opacity: 0.75 !important;
	}
	
}
