css:
@-moz-document url-prefix("https://robertsspaceindustries.com/spectrum") {

/*
 * Spectrum Redux v0.1.1c (12/2/2017)
 * Copyright 2017 Royalkin
 * Licensed under MIT (https://github.com/cquanu/github-dark/blob/master/LICENSE)
*/

/*-------------------------------------------------------------------------------------------------
GENERAL MODIFICATIONS
--------------------------------------------------------------------------------------------------*/

/*.content-block.image {
	background: none !important;
	display: inline-block !important;
	padding-left: 40px !important;
	width: inherit !important;
}*/

blockquote {
    background-color: #060a11 !important;
	color: #c1d0dd !important;
	border-left: 1px solid #29516a !important;
    font-family: 'Roboto Slab', Roboto, sans-serif !important;
    font-size: 0.85em !important;
    line-height: 180% !important;
}

blockquote, pre {
    padding: 20px 20px 20px 30px !important;
}

/* Changes quoted text to Roboto slab for easier identification */
.content-block.quote .content-block.text {
    font-family: 'Roboto Slab', Roboto, sans-serif !important;
    font-size: 0.9em !important;
    line-height: 170% !important;
}

/* Increases padding for block quotes  */
.content-block.quote {
	background-color: #060a11 !important;
	border-left: 1px solid #29516a !important;
	padding: 20px 0 20px 20px !important;
}

/* Adds a gradient background to block quotes (dark theme only) */
.theme-dark blockquote {
	border-left: 1px solid #29516a !important;
}

/*-------------------------------------------------------------------------------------------------
USER MODIFICATIONS
--------------------------------------------------------------------------------------------------*/

/* Removes rounded edges and border from avatars */
.member-avatar {
	border: 0 !important;
	border-radius: 0 !important;
}

.avatar {
    border-radius: 0 !important;
}

/* Increases the spacing between bookmark items */
.sidebar-list>.sidebar-item {
	padding: 10px 20px !important;
}

/* Maintains no additional spacing for Main & Organization Section Links */
.sidebar-list .sidebar-item.community {
    padding: 0 !important;
}

/* Removes the rounded edges on bookmark icons in the sidebar */
.avatar .community-avatar {
	border-radius: 0 !important;
}

/* Removes the rounded edges on the empty private messages indicator */
.sidebar-private-lobbies-empty, .sidebar-private-lobbies-empty:hover {
	border-radius: 0 !important;
}

.sidebar-list .sidebar-item>.column.avatar>a {
    border: 0px !important;
    border-radius: 0px !important;
}

/*-------------------------------------------------------------------------------------------------
THREAD LIST MODIFICATIONS
---------------------------------------------------------------------------------------------------*/

/* Changes the styling of the thread stats (Replies, Views) */
.stat {
    font-size: .68rem !important;
    text-transform: uppercase !important;
}

span.count { color: #8bdffb !important; font-weight: 600 !important; }
.vote .count { color: #8bdffb !important; font-size: 0.85rem !important; }

/* Increases size, weight, and color of thread count */
.community-item-right>.info {
	color: #8bdfff !important;
	font-size: 12px !important;
	font-weight: 700 !important;
}

/* Modifies the maximum width of the thread title column */
#page.forum-channel .threads-list .row .column.subject .subject-content {
	max-width: 50% !important;
}

/* Modifies the new post indicator at the end of thread titles */
#page.forum-channel .threads-list .row .column.subject .subject-content .thread-subject-new-replies {
	color: #ffb131 !important;
	background: none !important;
	border: 0px !important;
	border-radius: 0px !important;
	font-size: 10px !important;
	font-weight: 600 !important;
	letter-spacing: 0.7 !important;
	padding: 0  !important;
	text-transform: uppercase;
}

/* Increase the height of thread list rows */
#page.forum-channel .threads-list .row {
	padding: 10px 15px !important;
}

/* Adds distance between thread title, thread author, and started by and last post time >>> */
#page.forum-channel .threads-list .row .column.subject .subject-content .subject-content-footer {
	margin-top: 5px !important;
}

#page.forum-channel .threads-list .row .column.last-activity > .time-created {
	margin-top: 5px !important;
}

/* Adds uppercase, bolds, and decreases the font size of the thread list column headers to uppercase */
#page.forum-channel .threads-list .row.columns-header {
	font-size: 0.8rem; font-weight: 600 !important; text-transform: uppercase !important;
}

/* Changes the dark theme color for thread list column headers */
.theme-dark #page.forum-channel .threads-list .row.columns-header .column {
	color: #76bae5 !important;
}

/* Increase the size and weight of thread titles */
#page.forum-channel .threads-list .row .column.subject .subject-content a.thread-subject {
	font-size: 0.9rem; font-weight: bold !important;
}

/* Changes the dark theme color for thread titles */
.theme-dark #page.forum-channel .threads-list .row .column.subject .subject-content a.thread-subject {
	color: #d6e3Eb !important;
}

/* Changes the font size of the channel header */
#page.forum-channel .channel-header .info .info-header h1.channel-name {
	font-size: 1.1rem !important;
}

/* Adds indiciation of a visited thread (dark theme) */
.theme-dark #page.forum-channel .row.thread:not(.unread) .column.subject .subject-content .thread-subject {
	color: #667e8a !important;
}

/* Adds an indicator for a visited thread (light theme) */
#page.forum-channel .threads-list .row .column.subject .subject-content a.thread-subject:visited {
	color: #a8b1b6 !important;
}

/* Removes the background for thread types (Left side, Thread List (Nested, Chronological)) */
	.column.type {
	background: none !important;
}

/*-------------------------------------------------------------------------------------------------
THREAD & REPLIES MODIFICATIONS
---------------------------------------------------------------------------------------------------*/

/* Removes rounded edges (radius) and borders from avatar badges */
.member-avatar .avatar-badges {
	/*border: 0px !important;*/
    border-radius: 0px !important;
}

/*.avatar-badge {
    border: 0px !important;
}*/

/* Removes the border for avatars with badges */
.member-avatar .avatar.has-badges {
    /*border: 0px !important;*/
    border-radius: 0px !important;
}

/* Specifies left margin/padding for posts in nested threads */
.forum-thread-item .forum-thread-item-children-replies {
    	margin-top: 20px !important;
   	width: calc(100% + 240px) !important;
    	transform: translateX(-140px) !important;
}

/* Removes the border from images */
.content-block.image .content-block-media {
	border: 0;
}	

/* Changes headline font to Roboto Sans from Orbitron */
.content-block.text h1 {
	color: #ffffff !important;
	font: 700 1.75em "Roboto", sans-serif !important;
	margin-top: 20px;
}

h1.forum-thread-subject.desktop-only {
    font: 700 1.25em "Orbitron", sans-serif !important;
    margin-left: 17px !important;
    text-transform: uppercase !important;
}

/* Descreses size and increases leading of body text */
.content-block.text p {
	font-size: 0.9em !important;
    line-height: 170% !important;
}

/* Increases padding before and after quote blocks */
blockquote, pre { margin: 20px 0 20px 0 !important; }


/* Increases padding between reply and post body */
.content-blocks .forum-thread-item-reference {
	margin-bottom: 30px !important;
}

/* Maintains proper margins on forum thread headers */
.forum-thread-header {
    margin-right: 48px !important;
}

/* Increase padding between category items */
.community-item {
	border: 0 !important;
	margin-top: 10px !important;
	margin-bottom: 10px !important;
}

/* Enbolden Thread Title */
.forum-thread-item .forum-thread-subject {
	font-size: 30px !important;
	font-weight: 600 !important;
}

/* Adds a margin to the reply count to line up with the other elements */
.theme-dark #page.forum-thread .page-content .forum-thread .forum-thread-replies>h2 {
	margin: 0 0 0 103px !important;
}

/* Restyles hover block for replies >>>  */
.forum-thread-item-reference .hover-preview {
	border: 1px solid #141d2a !important;
	border-bottom: 20px solid #1c2737!important;
	overflow: hidden !important;
}

.forum-thread-item-reference .hover-preview .content-blocks {
	max-height: 200px !important;
	overflow: hidden !important;
}

.forum-thread-item-reference .hover-preivew .content-blocks .image {
	display: none;
}

/* Increase height of collapsed reply so that username and avatar are visible */
.forum-thread-item.is-collapsed {
	height: 108px !important;
}

/* Increase the distance between the post body and the action buttons */
.forum-thread-item.type-forum_thread_reply > .content .content-footer {
	padding: 100px 0 0 0 !important;
}

/* Increases padding between the replies list and the bottom of the post window */
.forum-thread-item > .content .content-footer {
	margin-bottom: 20px !important;
    padding: 100px 0 0 0 !important;
}

/* Increses the distance from the top of the post window to the username */
.forum-thread-item > .content .content-header {
	margin-top: 20px !important;
}

/* Manages the padding from the top of post to the username for nested view */
.forum-thread-item.style-type-small>.content .content-header {
	margin-top: 4px !important;
}

/* Flushes time creation and username */
.forum-thread-item > .content .content-header .left {
	margin: 2px 0 0 0 !important;
}

/* Increase the distance from the username to the post body and adds an indent */
.content-blocks {
	margin: 35px 0 0 15px !important;
}

/* Increase the distance from the username to the post subject line */
.forum-thread-item .forum-thread-subject {
	margin: 55px 0 0 0 !important;
}

/* Increases padding on the right side of original post content block ( thread post) */
.forum-thread-item>.content .content-blocks, .forum-thread-item > .content .content-footer {
	padding-right: 30px !important;
}

/* Increases padding around reply windows */
.theme-dark #page.forum-thread .page-content .forum-thread .forum-thread-replies .forum-thread-item {
	margin: 0px 50px 0px 100px !important;
    padding: 25px 50px 25px 25px !important;
}

/* Increase the distance between image and quote blocks */
.content-block:not(.text) {
	margin: 40px 0 !important;
}

/* Removes border radius from post windows */
.forum-thread-item {
    border-radius: 0px !important;
}

/* Increase distance between original post and replies */
#page.forum-thread .page-content .forum-thread .forum-thread-replies {
	margin-top: 80px !important;
}

/* Adds padding between replies list and bottom of reply window */
div.replies {
	margin: 0 0 25px 0;
}

/* Adds bottom spacing to nested replies */
#page.forum-thread .page-content .forum-thread .forum-thread-replies .forum-thread-item {
	padding-bottom: 20px !important;
}

/* Adds top margins to nested post replies */
/* Adds a border to the closed edges (top, bottom, and left) of an odd nested thread reply */
.theme-dark #page.forum-thread .page-content .forum-thread .forum-thread-replies .forum-thread-item.depth-odd {
    border-top: 1px solid #405171 !important;
    border-bottom: 1px solid #405171 !important;
    border-left: 1px solid #405171 !important;
    margin-top: 60px !important;
}

/* Removes all borders from odd thread replies and ALL CHRONOLOGICAL THREAD REPLIES */
.theme-dark #page.forum-thread .page-content .forum-thread .forum-thread-replies .forum-thread-item.depth-even {
    border: 0px !important;
    margin-top: 60px !important;
}

/*-------------------------------------------------------------------------------------------------
BUTTON MODIFICATIONS
-------------------------------------------------------------------------------------------------*/

/* Increases padding between "Open Previous Quotes" and the text body */
.content-block.quote .show-previous-posts {
	margin-bottom: 15px !important;
}

/* Increase top and bottom padding for "Open quote images" button */
button.open-block.reset {
	margin: 20px 0 20px 0;
}

/* Removes the roundedges on thread flags (Pinned, Sticky, etc.) */
.thread-flag {
    border-radius: 0 !important;
}

/* Changes the styling for Thread Tags in the Thread List */
.forum-label {
    background: none !important;
    border: 1px solid #40526c !important;
    border-radius: 0 !important;
    font-size: 9px !important;
    font-weight: 600 !important;
    text-transform: uppercase !important;
}

/* Removes rounded edges on tooltips */
.theme-dark .react-tooltip {
    border-radius: 0px !important;
	font-size: 10px !important;
    font-weight: 600 !important;
    text-transform: uppercase !important;
}

/* Remove border radius from buttons */
button {
	border-radius: 0px !important;
}

button.simple.flag-post.reset {
    border: 0px !important;
    margin-right: 5px !important;
}

button.action.icon-only.flag-post.reset {
    border: 0px !important;
}

a.action.icon-only.permalink {
    background: none !important;
    border: 0px !important;
}

a.action.icon-only.permalink:hover {
    background: none !important;
}

button.reset.action.search {
    border: 0px !important;
}

div.left-triangle::before {
    border-bottom: 0px !important;
}


/* Increase the size, weight, and color of thread actions in category list */
#app-content>#page.community>#page-main .community-list .community-list-header .community-list-header-actions .btn {
	font-size: 12px !important;
	font-weight: 700 !important;
	text-transform: uppercase;
}

/* Removes borders and rounded corners from some specific buttons >>> */
.btn.btn-round {
	border: none !important;
	border-radius: 0 !important;
}

button.reset {
	border-bottom: 1px solid #405171 !important;
}

button.reaction {
	background: none !important;
	margin: 0 0 2px 5px !important;
	padding: 0 !important;
}

button.reaction.tooltop-top {
	background: none !important;
}

button:hover {
	background: none !important;
}

/* Restyles the NEW THREAD button in upper right >>> */
button.create.reset {
    background: #25c3ff !important;
    color: #ffffff !important;
    font-size: .7rem !important;
    font-weight: 600;
    padding: 5px 9px !important;
    text-transform: uppercase;
}

button.create.reset:hover {
    background: #405171 !important;
}

/* Restyles the thread sorting and tag buttons */
.theme-dark button.select  {
	background: none !important;
	color: #fcfcfc !important;
}

.theme-dark button.select.active {
	border: 1px solid #649dc1 !important;
}

div.sort-selector>.label, .dropdown .dropdown-label {
	float: left;
	font-size: 10px !important;
	text-transform: uppercase;
}

.forum-channel div.buttons {
	padding: 0 0 0 0 !important;
	width:375px;
}

div.dropdown {
	min-width: 150px !important;
}

div.sort-selector>.label, .dropdown .dropdown-label {
	margin: 7px 15px 0 0 !important;
}

.theme-dark .list-navigator .widgets button.action {
	border: 1px solid #416481 !important;
	background-color: #293952 !important;
}

button.reset {
        border: 0px !important;
}

.sidebar-list-footer button {
	border-bottom: 1px solid #405171 !important;
}

button.btn.primary {
    background: none !important;
    color: #939dab !important;
}

button.btn.primary:hover {
    background: none !important;
    color: #25bdd7 !important;
}

a.new-replies-link, a.new-replies-link:hover {
    border: 0 !important;
	border-radius: 0 !important;
}

 a.new-replies-link:hover {
	border-radius: 0 !important;
}

button.vote {
    background: none !important;
    border: none !important;
    font-size: 2rem !important;
}

button {
	border: none !important;
}

.tracked-posts-btn {
	border-color: #e5c100 !important;
	border-radius: 0 !important;
}

/*-------------------------------------------------------------------------------------------------
DRAFT WINDOW MODIFICATIONS
-------------------------------------------------------------------------------------------------*/

.forum-editor-block.text .draft-extend-editor .DraftEditor-root .public-DraftEditor-content {
    font-size: 0.9em !Important;
    margin: 15px !Important;
}
}
